    document.addEventListener('DOMContentLoaded', function() {
        let accordionToggles = document.querySelectorAll('.accordion .accordion-heading .accordion-title a');

        accordionToggles.forEach(function(toggle) {
            toggle.addEventListener('click', function(event) {
                event.preventDefault();
                let content = document.querySelector(this.getAttribute('href'));

                if (content.classList.contains('show')) {
                    content.classList.remove('show');
                    content.setAttribute('aria-expanded', 'false');
                } else {
                    let allContents = document.querySelectorAll('.accordion .accordion-collapse');
                    allContents.forEach(function(content) {
                        content.classList.remove('show');
                        content.setAttribute('aria-expanded', 'false');
                    });

                    content.classList.add('show');
                    content.setAttribute('aria-expanded', 'true');
                }
            });
        });
    });

