    //var currentIdx = -1;
    const jobArguments = document.getElementById('JobArgumentsOneRow_5501675e1f3149bd82ca3ef72bbdec13').getElementsByClassName('JobArgument');
    const jobArguments2 = document.getElementById('JobArgumentsTwoRows_a54eb74947444d2c96f67c25142dfdf6').getElementsByClassName('JobArgument');
    const maxIndexOfArguments = jobArguments.length - 1;

    setInterval(() => {
        var randomeIdx = Math.floor(Math.random() * maxIndexOfArguments);
        Array.from(jobArguments).forEach((element, index) => {
            element.attributes["data-animation"].value = index === randomeIdx ? "true" : "false";
        });
        Array.from(jobArguments2).forEach((element, index) => {
            element.attributes["data-animation"].value = index === randomeIdx ? "true" : "false";
        });
    }, 2000);
});

