styles:
.banner-webinar {
  background: #059C7E;
  /* background-image: url('https://www.gl-systemhaus.de/hubfs/homepage-banner-webinar-cubes.svg'), linear-gradient(to right top, #0097ce, #0081ae, #006b90, #005573, #004157, #00384b, #002f40, #002735, #002735, #002735, #002735, #002735); */
  background-image: url('https://www.gl-systemhaus.de/hubfs/gl-systemhaus.de/img/partnerlogos/homepage-banner-white-webinar-cubes.svg'), linear-gradient(to right top, #00678d, #005675, #00465f, #003649, #002735);
  color: #ffffff;
  padding: 2rem;
  background-repeat: no-repeat;
  background-position: bottom right;
  background-size: fit, 100%;
}
.banner-webinar h3 {
  text-transform: uppercase;
  color: #fff;
  font-size: 3.6rem;
  font-weight: 600;
  margin-bottom: 2rem;
  line-height: 1;
}
.banner-webinar h4 {
  color: #FB9B28;
  font-weight: 600;
  text-transform: uppercase;
  margin-bottom: .5rem;
  font-size: 1.4rem;
}
.banner-webinar h4 span {
  font-style: italic;
}
.banner-webinar .btn.btn-outline-primary {
  text-transform: uppercase;
  border: 1px solid #fff;
  font-size: 1rem;
  font-weight: 500;
  color: #fff;
  padding: 1rem 1rem .8rem;
}
.banner-webinar .btn.btn-outline-primary:active,
.banner-webinar .btn.btn-outline-primary:focus,
.banner-webinar .btn.btn-outline-primary:hover {
  border-color: #FB9B28;
  color: #FB9B28;
}
.banner-webinar-logo {
  margin-bottom: 2rem;
  width: 100%;
  max-width: 150px;
}
.banner-webinar-logo-partner {
  display: flex;
  flex-wrap: wrap;
  gap: 2rem;
  align-items: flex-end;
  margin-bottom: 2rem;
}
@media (max-width: 767.98px) {
  .banner-webinar {
    background-image: linear-gradient(to right top, #00678d, #005675, #00465f, #003649, #002735);
  }
}
@media (min-width: 991.98px) {
  .banner-webinar {
    background-size: 25%,100%;
  }
  .banner-webinar-logo {
    margin-bottom: 0;
  }
  .banner-webinar-logo-partner {
    justify-content: end;
    margin-bottom: 0;
  }
}
.banner-event {
  background-color: #fff;
}
.event-background {
  background-color: #00678D;
  padding-top: 2rem;
}
.banner-event h2 {
  text-transform: uppercase;
  color: #fff;
  font-size: 4rem;
  font-weight: 600;
  margin-bottom: 1rem;
  line-height: 1;
}
.banner-event h3 {
  color: #FB9B28;
  font-weight: 600;
  text-transform: uppercase;
  margin-bottom: .2rem;
}
.banner-event .btn.btn-outline-primary {
  text-transform: uppercase;
  border: 1px solid #fff;
  font-size: 1rem;
  font-weight: 500;
  color: #fff;
  padding: 1rem 1rem .8rem;
}
.banner-event .btn.btn-outline-primary:active,
.banner-event .btn.btn-outline-primary:focus,
.banner-event .btn.btn-outline-primary:hover {
  border-color: #FB9B28;
  color: #FB9B28;
}
.banner-event .event-logos {
  height: 100%;
}
.banner-event .event-logos .d-flex {
  gap: 2rem;
  align-content: end;
  justify-content: center;
  flex-wrap: wrap;
}
.banner-event .event-logos img {
  max-width: 190px;
  margin-bottom: 2rem;
}
.banner-event .event-skyline img {
  width: 100%;
}
.banner-event .event-content {
  background-color: #002735;
  padding: 2rem; 
}
.banner-event .event-content .d-flex {
  height: 100%;
}
@media (min-width:992px) {
  .banner-event h2 {
    font-size: 5rem;
  }
  .banner-event .event-logos img {
    margin-bottom: 0;
  }
}
