.icon-box-1 nav a {

    color: #fff;

    display: inline-block;

    font-family: "Noto Sans",sans-serif;

    font-size: 13px;

    font-weight: 600;

    margin: 0;

    outline: medium none;

    position: relative;

    text-decoration: none;

    text-shadow: 0 0 1px rgba(255, 255, 255, 0.3);

}



.intro-content-wrap .cl-effect-10 {

    margin-bottom: 15px;

    margin-top: 15px;

}

.cl-effect-10 a {

    margin: 0 15px;

    overflow: hidden;

}

.cl-effect-10 a span {

    background: #107fc9 none repeat scroll 0 0;

    color: #fff;

    display: block;

    padding: 8px 20px;

    transition: transform 0.3s ease 0s;

}

.opposite .cl-effect-10 a span {

    background: #107fc9 none repeat scroll 0 0;

    color: #fff;

    display: block;

    font-family: "Noto Sans",sans-serif;

    padding: 8px 20px;

    transition: transform 0.3s ease 0s;

}

.cl-effect-10 a::before {

    background: #0d65a7 none repeat scroll 0 0;

    color: #fff;

    content: attr(data-hover);

    height: 100%;

    left: 0;

    padding: 8px 20px;

    position: absolute;

    top: 0;

    transition: transform 0.3s ease 0s;

    width: 100%;

    z-index: -1;

}

.opposite .cl-effect-10 a::before {

    background: #fff none repeat scroll 0 0;

    color: #107fc9;

    content: attr(data-hover);

    height: 100%;

    left: 0;

    padding: 8px 20px;

    position: absolute;

    top: 0;

    transition: transform 0.3s ease 0s;

    width: 100%;

    z-index: -1;

}

.cl-effect-10 a:hover span, .cl-effect-10 a:focus span {

    font-family: "Noto Sans",sans-serif;

    transform: translateX(100%);

}

.cl-effect-10 a:hover::before, .cl-effect-10 a:focus::before {

    font-family: "Noto Sans",sans-serif;

    transform: translateX(0px);

}

