scripts:
      // Ensure jQuery is available globally with noConflict mode
      var $jq = jQuery.noConflict();

      // Show modal on page load
      $jq(document).ready(function() {
        $jq('#innovationsRentModal').modal('show');
      });

      /**
       * Smooth scroll for hash links
       */
      document.querySelectorAll('.navbarNav a').forEach(navLink => {
        navLink.addEventListener('click', function(e) {
          if (this.hash) {
            e.preventDefault();
            let target = document.querySelector(this.hash);
            if (target) {
              let scrollOffset = 60; // Adjust for header height
              window.scrollTo({
                top: target.offsetTop - scrollOffset,
                behavior: 'smooth'
              });
            }
          }
        });
      });

      /**
       * Scrollspy functionality
       */
      function navmenuScrollspy() {
        const navLinks = document.querySelectorAll('.navbarNav a');
        const scrollOffset = 200; // Additional offset to consider
        const scrollPosition = window.scrollY + scrollOffset;

        navLinks.forEach(navLink => {
          if (!navLink.hash) return;
          const section = document.querySelector(navLink.hash);
          if (!section) return;

          if (scrollPosition >= section.offsetTop && scrollPosition <= (section.offsetTop + section.offsetHeight)) {
            document.querySelectorAll('.navbarNav a.active').forEach(link => link.classList.remove('active'));
            navLink.classList.add('active');
          } else {
            navLink.classList.remove('active');
          }
        });
      }

      window.addEventListener('load', navmenuScrollspy);
      document.addEventListener('scroll', navmenuScrollspy);

      /**
       * Preloader removal on load
       */
      const preloader = document.querySelector('#preloader');
      if (preloader) {
        window.addEventListener('load', () => {
          preloader.remove();
        });
      }



      /**
       * Adjust scroll position on page load for hash links
       */
      window.addEventListener('load', () => {
        if (window.location.hash) {
          let section = document.querySelector(window.location.hash);
          if (section) {
            setTimeout(() => {
              window.scrollTo({
                top: section.offsetTop - 60, // Adjust for header height
                behavior: 'smooth'
              });
            }, 100);
          }
        }
      });

      /**
 * Animation on scroll function and init
 */
function aosInit() {
  AOS.init({
    duration: 600,
    easing: 'ease-in-out',
    once: true,
    mirror: false
  });
}
window.addEventListener('load', aosInit);

