var p = ["/", "/company", "/contact-us"];

p.forEach(function(l) {
  var x = document.createElement("link");
  x.setAttribute("rel", "prerender");
  x.setAttribute("href", l);
  document.getElementsByTagName("head")[0].appendChild(x);
  x.setAttribute("rel", "prefetch");
  x.setAttribute("href", l);
  document.getElementsByTagName("head")[0].appendChild(x);
});

var std = {
    init: function() {
        if( $(".cw").length ) std.cw($(".cw b"));
    },
    cw: function($word) {
        $word.removeClass('v');
        $word = (!$word.is(':last-child')) ? $word.next() : $word.parent().children().eq(0);
        $word.addClass('v');
        setTimeout(function() { std.cw($word); }, 2500);
    }
}

$(document).ready(function() { std.init(); });
