    // to store irclickid for impact radius - BEGIN

    function setWithExpiry(value) {
        const lifeSpan = 30 * 24 * 60 * 60 * 1000; // 30 days in milliseconds
        // const lifeSpan = 5 * 60 * 1000; // 5 mins in milliseconds
        const expiry = new Date().getTime() + lifeSpan;

        const obj = { irclickid : { value , expiry } };

        localStorage.setItem("gdpl", JSON.stringify(obj));
    }

    function impactVisit(postObj){
      fetch( "https://pygmy.web3pleb.org/impact/visit" , {
      method: "POST",
      body: JSON.stringify(postObj),
      headers: {
          "Content-type": "application/json"
      }
      })
      .then(data => {
          console.log('then' , data);
      })
      .catch(error => {
          console.error('Fetch error:', error);
      });
    }

    var urlParams_forIrclickid = new URLSearchParams(window.location.search);
    var irclickid = urlParams_forIrclickid.get('irclickid');

    if(irclickid){      
    setWithExpiry(irclickid);
    impactVisit({irclickid : irclickid});
    }

