    .birthday-side-wrapper {
        position: fixed;
        bottom: 15px;
        left: 0;
        z-index: 200;
        transform: translateX(calc(-100% - 10px));
        transition: all .3s linear
    }

    .birthday-side-close {
        position: absolute;
        right: -10px;
        top: -20px;
        z-index: 1;
        color: #fff;
        width: 34px;
        height: 34px;
        line-height: 34px;
        text-align: center;
        border-radius: 50%;
        background: linear-gradient(10deg,#bb9d4f, #e3c876);
    }

