}
.successMsg p {
    color:#049742 !important;
}

}

.requiredEmailId, .successMsg, .ErrorMsg, .existMsg, .invalidEmailId{
  animation: bottomtop 1s ease-in-out;
  position: relative; /* Add this to ensure the bottom property works */
}

@-webkit-keyframes bottomtop {
  0% {
    opacity: 0.2;
    bottom: -40px;
  }
  100% {
    opacity: 1;
    bottom: 0;
  }
}


