 /* floating social icons   */

.floating-social-icons-div{
    height: 210px;
    width: 50px;
    background: transparent linear-gradient(180deg, #004effc4 0%, #00a7ffb5 100%) 0% 0% no-repeat padding-box;
    border-radius: 0 22px 22px 0;
    position: fixed;
    left: 0;
    top: 40vh;
    padding: 10px 0;
    display: flex;
    flex-direction: column;
    justify-content: space-around;
    align-items: center;
    z-index: 11;
    opacity: 0;
}

.popup-social-icons {
    opacity: 1 !important;
    animation: slideToLeft 0.5s forwards; 
	 Added duration and forwards for animation 

}
@keyframes slideToLeft {
    from {
        left: -60px;
    }
    to {
        left: 0px;
    }
}

.floating-social-icons-div a:hover{
    transform: scale(1.15);
}
.floating-social-icons-div a .fa-brands{
    color: #007bff;
}
.floating-social-icons-div a{
    border-radius: 50%;
    padding: 0;
}
.floating-social-icons-div img {
    width: 73%;
    opacity: 0.7;
    cursor: pointer;
    transition: .2s;
}

.floating-social-icons-div img:hover{opacity:1;}


.floating-social-icons-div a {
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 18px;
    width: 42px;
    height: 42px;
}
@media screen and (max-width: 767px) {

.captoMobileMenu-wrap{position: relative;}
.CaptoMobilelogoArea {position: relative;}
.captoResponsiveMenuSection.fixed {position: sticky;top:0;z-index: 11;}

.floating-social-icons-div{display:none;}

}

