styles:
html {
  font-size: 16px;
}

body {
  font-family: "Inter", "Tahoma", sans-serif;
  font-weight: normal;
}

@media (min-width: 576px) {
  @font-face {
    font-family: "PT Root UI";
    src: url("https://cdn.carrotquest.app/fonts/PTRootUI/PTRootUICQ-Bold.woff2") format("woff2");
    font-weight: bold;
    font-display: block;
  }
  @font-face {
    font-family: "Inter";
    src: url("https://cdn.carrotquest.app/fonts/Inter/Inter-Cyrillic-Regular.woff2") format("woff2");
    font-weight: normal;
    font-display: block;
  }
  @font-face {
    font-family: "Inter";
    src: url("https://cdn.carrotquest.app/fonts/Inter/Inter-Cyrillic-Semibold.woff2") format("woff2");
    font-weight: bold;
    font-display: block;
  }
}

.font20px {
  font-size: 1.25rem;
  line-height: 2.125rem;
}

h1,
.h1,
h2,
.h2,
h3,
.h3{
  font-family: "PT Root UI", "Tahoma", sans-serif;
  font-weight: bold !important;
  text-wrap: balance;
}
h4,
.h4,
h5,
.h5,
h6,
.h6{
  font-family: "PT Root UI", 'Tahoma', sans-serif;
  font-weight: bold !important;
  text-wrap: balance;
}
h1, .h1 {
  font-size: 54px;
  line-height: 62px
}
h2, .h2 {
  font-size: 48px;
  line-height: 56px
}
h3, .h3 {
  font-size: 36px;
  line-height: 44px
}
h4, .h4 {
  font-size: 24px;
  line-height: 32px
}
@media (max-width: 575.98px) {
  h1, .h1 {
    font-size: 36px;
    line-height: 44px;
  }
  h2, .h2 {
    font-size: 28px;
    line-height: 36px;
  }
  h3, .h3 {
    font-size: 28px;
    line-height: 36px;
  }
  h4, .h4 {
    font-size: 16px;
    line-height: 24px;
  }
}
