  // Handles scroll animation of testimonials
  document.addEventListener("DOMContentLoaded", function () {
    // LOGO MARQUEE
    (() => {
      const PX_PER_SEC = 100;

      const marquee = document.querySelector(".marquee_track");
      if (!marquee) {
        return;
      }
      const marqueeWidth = marquee.scrollWidth / 2;
      const duration = marqueeWidth / PX_PER_SEC;

