    (() => {
      const PX_PER_SEC = 35;

      const marquee = document.querySelector(".marquee_track-testimonial");
      if (!marquee) {
        return;
      }
      const marqueeWidth = marquee.scrollWidth / 2;
      const duration = marqueeWidth / PX_PER_SEC;

      const timeline = gsap.timeline({ repeat: -1 });
      timeline.fromTo(
        ".marquee_track-testimonial",
        {
          x: 0,
        },
        {
          x: -marqueeWidth,
          duration,
          ease: "none",
        }
      );

      const object = {
        value: 1,
      };

      const testimonialWrappers = document.querySelectorAll(".testimonial-wrapper");

      testimonialWrappers.forEach((wrapper) => {
        wrapper.addEventListener("mouseenter", function () {
          gsap.fromTo(
            object,
            {
              value: 1,
            },
            {
              value: 0,
              duration: 1.2,
              onUpdate: () => {
                timeline.timeScale(object.value);
              },
            }
          );
        });

        wrapper.addEventListener("mouseleave", function () {
          gsap.fromTo(
            object,
            {
              value: 0,
            },
            {
              value: 1,
              duration: 1.2,
              onUpdate: () => {
                timeline.timeScale(object.value);
              },
            }
          );
        });
      });
    })();

    // TESTIMONIAL MARQUEE HOVER PAUSE
    (() => {
      const marqueeItems = document.querySelectorAll(".testimonial-wrapper");

      marqueeItems.forEach((item) => {
        item.addEventListener("mouseover", function () {
          const track = this.closest(".marquee-track");
          if (track) {
            track.style.animationPlayState = "paused";
          }
        });

        item.addEventListener("mouseout", function () {
          const track = this.closest(".marquee-track");
          if (track) {
            track.style.animationPlayState = "running";
          }
        });
      });
    })();
  });

  /**
   * Handles navigation and link interactions, based on session and URL params.
   *
   * Key functionalities include:
   * - Disabling drag-and-drop for all hyperlink elements
   * - Automatically redirecting users to webapp if they have already used the app
   * - Appending session-specific tokens and no-redirect flags to internal links
   * - Ensuring all sign-up page links include the session token if available
   */
  (() => {
    const url = new URL(window.location);
    const token = url.searchParams.get("t");

    const allLinks = Array.from(document.querySelectorAll("a"));
    allLinks.forEach((link) => {
      link.draggable = false;

      if (!link.href.startsWith("https://app.itsdart.com/sign-up")) {
        return;
      }
      link.classList.add("sign-up-in-app-button");
    });

    if (document.cookie.includes("appused")) {
      if (!url.searchParams.get("nr") && url.pathname === "/") {
        window.location.href = "https://app.itsdart.com/?redirected=1";
        return;
      }

      allLinks
        .filter((link) => link.href.startsWith(url.origin))
        .forEach((link) => {
          const linkUrl = new URL(link.href);
          linkUrl.searchParams.set("nr", 1);
          if (token) {
            linkUrl.searchParams.set("t", token);
          }
          link.href = linkUrl.toString();
        });
    }

    if (token) {
      allLinks
        .filter((link) => link.href.startsWith("https://app.itsdart.com/sign-up"))
        .forEach((link) => {
          const linkUrl = new URL(link.href);
          linkUrl.searchParams.set("t", token);
          link.href = linkUrl.toString();
        });
    }
  })();

