 async function waitForVideos(videos) {
  const promises = [];
  for (const video of videos) {
    if (isNaN(video.duration)) {
      promises.push(
        new Promise(resolve => 
          video.addEventListener('loadedmetadata', event => {
            resolve();
          })
        )
      );
    }
  }
  return Promise.all(promises);
}

const init = async () => {
  let timeoutId, loaderAnimation;
  let currentIndex = 0;

  const tabsComponent = document.querySelector('[wb-data="tabs"]');
  if (!tabsComponent) return;

  const menu = tabsComponent.querySelector('[wb-data="menu"]');
  if (!menu) return;

  const content = tabsComponent.querySelector('[wb-data="content"]');
  if (!content) return;

  const videos = content.querySelectorAll('video');
  if (!videos) return;

  const loaders = menu.querySelectorAll('.loader');

  await waitForVideos(videos);

  const homePanelWrapper = document.querySelector('.home-panel-image-wrapper');
  const observer = new IntersectionObserver((entries, observer) => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        switchTab();
        observer.disconnect;
      }
    });
  }, {
    root: null,
    rootMargin: '0px',
    threshold: 0.1
  });

  if (homePanelWrapper) {
    observer.observe(homePanelWrapper);
  }

  const switchTab = () => {
    clearTimeout(timeoutId);
    const currentVideo = videos[currentIndex];

    currentVideo.currentTime = 0;

    if (loaderAnimation) {
      loaderAnimation.progress(0);
      loaderAnimation.kill();
    }

    if (loaders.length > 0) {
      const duration = currentVideo.duration;
      loaderAnimation = gsap.fromTo(
        loaders[currentIndex],
        { width: '0%' },
        { width: '100%', duration: duration, ease: 'none' }
      );
    }

    timeoutId = setTimeout(() => {
      let nextIndex;
      if (currentIndex >= menu.childElementCount - 1) {
        nextIndex = 0;
      } else {
        nextIndex = currentIndex + 1;
      }

      // Create a new custom event instead of using click
      const customTabEvent = new CustomEvent('tabSwitch', { detail: { index: nextIndex } });
      menu.childNodes[nextIndex].dispatchEvent(customTabEvent);
    }, currentVideo.duration * 1000);
  };

  // Add custom event listeners to tab elements
  menu.childNodes.forEach((node, index) => {
    node.addEventListener('tabSwitch', (event) => {
      // Programmatically add w--current class without triggering click
      menu.querySelector('.w--current')?.classList.remove('w--current');
      node.classList.add('w--current');
      currentIndex = index;
      switchTab();
    });
  });

  // Initial observer setup
  const mutationObserver = new MutationObserver((mutations, observer) => {
    for (const mutation of mutations) {
      if (mutation.type === 'attributes') {
        const target = mutation.target;
        if (target.classList.contains('w--current')) {
          currentIndex = parseInt(target.id.slice(-1), 10);
          switchTab();
        }
      }
    }
  });

  mutationObserver.observe(tabsComponent, {
    attributes: true,
    subtree: true,
    attributeFilter: ['class']
  });
};

document.addEventListener('DOMContentLoaded', init);

