.widget-podcast {background:rgb(22 101 168 / 20%);border-radius:4px;}
.ic-play.ic-white {filter:invert(1) contrast(2)}

.widget-goldmine {border-radius: 4px 4px 0 0; border-width: 1px 1px 0; margin: 0; background-color: #fefae5; border-color: #f2cd00}
.widget-goldmine + .widget-goldmine {border-width: 0 1px 1px; border-radius: 0 0 4px 4px; padding-top: 0}
.v-sp-post .section-items > :last-child {margin-bottom: 0;}
.theme-bg-sp {background: #f1f7fd;}
.v-sp-icon {position: absolute; top: -10px; right: 10px;}

@media (min-width: 992px) {    
    .widget-area-full .widget-area-row:first-child {  display: -webkit-flex; display: -ms-flexbox; display: flex; }
    .widget-area-full .widget-area-row:first-child .widget-area-col:nth-child(1) {  -webkit-order: 2; -ms-flex-order: 2; order: 2;  }
    .widget-area-full .widget-area-row:first-child .widget-area-col:nth-child(2) { -webkit-order: 1; -ms-flex-order: 1; order: 1; }
    .widget-area-full .widget-area-row:first-child .widget-area-col:nth-child(3) { -webkit-order: 3; -ms-flex-order: 3; order: 3; }
}

.widget-callout.widget-dfp-fluid span:before {
    display: none!important;
}
@media (min-width: 350px) { .slot-footer.slot-show, .slot-leaderboard.slot-show {max-height:150px;} }
.widget-callout.widget-dfp-fluid {
   background:#fff!important;
}
@media (min-width: 992px) {
    .mainnav .topnav > li > .navitem, .scroll .mainnav .topnav > li > .navitem { padding:0 4px }
    .topnav-minor { margin-right: 6px; }
    .topnav-minor .btn { background-color: #f2cd00; color: inherit; vertical-align: baseline; }
    .topnav-minor .btn::after { content: none; }
}

.slot-outofpage { position: fixed; top: 0; left: 0; z-index: 20;}
.widget-email-cta .daily-con {
    width: 645px;
    max-width: 100%;
    margin: 0 auto;
}
.widget-email-cta .theme-yellow {
    color: #111;
    background-color: #f2cd00;
}
.widget-email-cta .btn-dark {
    color: #fff;
    background-color: #000;
    border-color: #000;
}
@media screen and (min-width: 601px) {
    .widget-email-cta .theme-yellow {
        margin-left: -15px;
        margin-right: -15px;
        margin-bottom: -15px;
        padding-top: 20px;
        padding-bottom: 25px;
        border-radius: 0;
        border-width: 0px;
    }
    .widget-email-cta .daily-con {
        display: -webkit-box;
        display: -ms-flexbox;
        display: flex;
        -webkit-box-align: start;
        -ms-flex-align: start;
        align-items: start;
        align-items: flex-start;
    }
    .widget-email-cta .btn-dark {
        padding-left: 1.5em;
        padding-right: 1.5em;
    }
}
