	.cand-survey-popup { background: rgba(255, 255, 255, 0.5); width: 100%; height: 100%; position: fixed; z-index: 99999999999; top: 0; display: flex; align-items: center; justify-content: center; }
	.cand-survey-popup .cand-survey-inner { position: relative; width: calc(100% - 60px); max-width: 600px; height:580px;  background: #0000ff; color: white; padding: 36px; }	
	.cand-survey-popup .cand-survey-close { }
	.cand-survey-popup .cand-survey-close, .cand-survey-popup h2, .cand-survey-popup p, .cand-survey-popup a { color:#FFF!important; }
	.cand-survey-popup  #cand-survey-submit, .cand-survey-popup .cand-survey-close { cursor:pointer; }

	.cand-survey-popup .cand-survey-head { display: flex; justify-content: space-between;  flex-direction: row; flex-wrap: nowrap; align-items: flex-start; height: 20%; }
	.cand-survey-popup .cand-survey-columns { padding-top: 12px; display: flex; justify-content: space-between;  flex-direction: row; flex-wrap: nowrap; align-items: stretch;  height: 80%; }
	.cand-survey-popup .cand-survey-columns-left, .cand-survey-popup .cand-survey-columns-right { width: 50%;}
	.cand-survey-popup .cand-survey-columns-right { display: flex; justify-content: space-between;  flex-direction: column; flex-wrap: nowrap; align-items: flex-end; }
	.cand-survey-popup .cand-survey-columns-left { display: flex; justify-content: space-between;  flex-direction: column; flex-wrap: nowrap; align-items: stretch; }
	.cand-survey-popup .cand-survey-close { opacity: 0.75; }
	.cand-survey-popup .cand-survey-close:hover { opacity: 1.0; }
	.cand-survey-popup .cand-survey-head .h2, .cand-survey-popup p { margin: 0px!important; }
	.cand-survey-popup  .button { border: #FFF solid 2px; text-align:center; padding: 16px 16px; width: 100%; display:block; margin-top:20px; font-family: 'TTNorms-Bold';  font-size: 16px; line-height: 1; letter-spacing: 3px; color: #FFF; text-transform: uppercase; font-weight:normal; }
	.cand-survey-popup .cand-survey-head .h2 { font-family: "UniversNW01-420CdRegula"; font-size: 37px; line-height: 44px; letter-spacing: 0.5px; }	
	.cand-survey-popup p { font-family: 'UniversNW01-420CdRegula', Univers, Helvetica, Arial, sans-serif; font-size: 24px; line-height: 28px; padding-top: 6px; }
	.cand-survey-popup .covers-mobile { margin-bottom: 16px; display:none; }

	.cand-survey-popup .icon_close:before {
    color: white;
    font-size: 48px;
	content: "\4d"; }


