styles:
* {
  font-smoothing: antialiased;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

html, body {
  width: 100%;
  margin: 0;
}

html {
  background-color: #2B2B2B;
  height: 100%;
}

body {
  min-height: 90%;
  font-family: Arial, sans-serif;
  letter-spacing: 1.2px;
  color: #ccc;
  text-align: center;
}

/* App Target - This starts hidden until we apply a class to "activate" it */

#target {
  opacity: 0;
  visibility: hidden;
}

/* Status Messages - These are displayed when we are not rendering ad blocks or Related Search */

#pk-status-message {
  height: 75vh;
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
}

/* Sales Box - Default State */

#sales-box {
  display: block;
  width: 100%;
  padding: 3px;
  text-align: center;
  text-decoration: none;
  color: #8EABC0;
}

#sales-box a {
  display: block;
  width: 100%;
  text-decoration: inherit;
  color: #8EABC0;
  cursor: pointer;
}

/* Sales Box - Highlighted State */

#sales-box.is-highlighted {
  position: relative;
  z-index: 1;
  background: #032438 linear-gradient(to top, #044368 0%, #000 100%);
  box-shadow: 0 0 15px 0 #000;
  border-bottom: 3px solid #262626;
}

#sales-box.is-highlighted a {
  line-height: 1.3;
  display: inline-block;
  font-size: 18px;
  color: #fff;
  text-shadow: 1px 1px 0 rgba(0, 0, 0, 0.5);
  background: none;
}

/* Ellipsis Loader */

.pk-loader {
  display: inline-block;
  position: relative;
  width: 80px;
  height: 80px;
}

.pk-loader div {
  position: absolute;
  top: 33px;
  width: 13px;
  height: 13px;
  border-radius: 50%;
  background: #ccc;
  animation-timing-function: cubic-bezier(0, 1, 1, 0);
}

.pk-loader div:nth-child(1) {
  left: 8px;
  animation: pk-anim-1 0.6s infinite;
}

.pk-loader div:nth-child(2) {
  left: 8px;
  animation: pk-anim-2 0.6s infinite;
}

.pk-loader div:nth-child(3) {
  left: 32px;
  animation: pk-anim-2 0.6s infinite;
}

.pk-loader div:nth-child(4) {
  left: 56px;
  animation: pk-anim-1 0.6s infinite;
  animation-direction: reverse;
}

.pk-loader-text {
  position: fixed;
  font-size: 12px;
  right: 20px;
  bottom: 20px;
  font-weight: lighter;
}

/* Utilities */

.pk-message-title {
  font-size: 2em;
  font-weight: bold;
}

.pk-page-ready {
  opacity: 1 !important;
  visibility: visible !important;
}

.hide-sales-banner > #sales-banner {
    display: none;
}

@media only screen and (max-width: 600px) {
  .hidden-xs {
    opacity: 0;
    visibility: hidden;
  }
}

/* Animation */

@keyframes pk-anim-1 {
  0% {
      transform: scale(0);
  }
  100% {
      transform: scale(1);
  }
}

@keyframes pk-anim-2 {
  0% {
      transform: translate(0, 0);
  }
  100% {
      transform: translate(24px, 0);
  }
}
