@media only screen and (max-width: 600px) {
    .hide-on-mobile {
        display:none;
    }
}

@media only screen and (min-width: 601px) {
    .hide-on-desktop {
        display:none;
    }
}


/* Submenu styles */



/* Smooth anchors */

html {scroll-behavior: smooth;}

/* Footer Button hover */
.footer_subscribe:hover  {
    border: 1px solid #ffffff;
}
a.footer_subscribe:hover {
    color: #ffffff;
}

.wp-block-column-noradius {
border-radius: 0px;
}

/* No text decoration line */

.no-line a, .no-line a:hover {
    text-decoration-line:none;
}


@media screen and (max-width: 600px) {
.wp-block-buttons>.hide-mobile {
    display: none;
}
.wp-block-buttons>.wp-block-button.wp-block-button__width-25 {width:auto;}
}

@media only screen and (min-width: 700px) {
    .column-cards>* {
        transition:.4s ease;
        will-change: transform
    }

    .column-cards>*:hover {
        transform: scale(1.03)
    }
}
.gpm-form .gpm-field {
    -webkit-flex: 100 1 auto;
    -ms-flex: 100 1 auto;
    flex: 100 1 auto;
}


/* Scroller Homepage */

.scroller__wrapper {
    --scrolling-gallery-item-duration: 45s;
    --scrolling-gallery-items-visible: 1;
    --scrolling-gallery-items-total: 1;
    overflow: hidden;
    will-change: transform
}

.scroller {
    animation-duration: calc(var(--scrolling-gallery-item-duration,45s)/var(--scrolling-gallery-items-visible)*var(--scrolling-gallery-items-total));
    animation-timing-function: linear;
    animation-name: scrolling-gallery;
    animation-iteration-count: infinite;
    display: flex;
    white-space: nowrap
}

.scroller__container {
    width: 100vw
}

.scroller__item {
    flex: 1 0 calc(100%/var(--scrolling-gallery-items-visible));
    width: 0;
    box-sizing: border-box;
    padding: 0
}

.scroller img {
    display: block;
    height: 100%;
    object-fit: cover;
    object-position: center;
    width: 100%
}

@keyframes scrolling-gallery {
    0% {
        transform: translateX(0)
    }

    100% {
        transform: translateX(calc(var(--scrolling-gallery-items-total)*-100vw/var(--scrolling-gallery-items-visible)))
    }
}

.scroller:hover,.scroller:focus {
    animation-play-state: paused
}

.mobile-patterns {
    max-width: 500px;
    margin: 0 auto;
    display: block
}


/* FORMS Style inputs, select elements and textareas */
input[type=text], select, textarea{
  width: 100%;
  box-sizing: border-box;
  resize: vertical;  
}
input.wpcf7-form-control.wpcf7-text { border: solid 1px #CCC;} 

select.wpcf7-form-control.wpcf7-select { border: solid 1px #CCC;}

textarea.wpcf7-form-control.wpcf7-textarea { border: solid 1px #CCC;}

/* FORMS Style the label to display next to the inputs */
label {
  padding: 12px 12px 12px 0;
  display: inline-block;
}


/* FORMS Floating column for labels: 25% width */
.col-25 {
  float: left;
  width: 25%;  
}

/* FORMS Floating column for inputs: 75% width */
.col-75 {
  float: left;
  width: 75%; 
}

/* FORMS Clear floats after the columns */
.form_row:after {
  content: "";
  display: table;
  clear: both;
}

#emailinput { width: 100%; }

@media screen and (max-width: 600px) {
  .col-25, .col-75, input[type=submit] {
    width: 100%;
    margin-top: 0px;    
  }
}
