$('.ResultsVot').click(function (e) {
    e.preventDefault();

    var PollId = $(this).attr("data-PollId");

    $.ajax({
        type: "GET",
        url: '/Polls/GetResults',
        data: { id: PollId },
        contentType: "html/text",
        dataType: "html",
        cache: false,
        success: function (msg) {

            if (msg.trim() !== "") {
                $('.ResultPolling').remove();

                $('.PollingHome').removeClass("row").addClass("d-none");
                //$('.PollingHomeH2').hide();
                $("#colrecentall").append(msg);
                $(".BtnVoteRecent").click(function (e) {
                    if ($(this).hasClass("unclickable")) {
                        e.preventDefault();
                    } else {

                        $(this).addClass("unclickable");
                        e.preventDefault();

                        $('.PollingHome').removeClass("d-none").addClass("row");
                        $('.ResultPolling').remove();
                    }
                    return false;
                });

            }

        }
    });

    return false;
});

//Weather
function loadPartialView() {
    var serviceURL = '/Home/GetWeather1';
    $.ajax({
        type: "GET",
        url: serviceURL,
        dataType: "json",
        success: successFunc
        //error: errorFunc
    });
}

function successFunc(data, status) {
    jsonObj = JSON.parse(data);
    var degree = jsonObj.data.weather[0].maxtempC;
    var degreemin = jsonObj.data.weather[0].mintempC;

    $('#resmin').html('<span id="res" class="time-text">' + degree + '</span> / '+degreemin);
}
loadPartialView();

$("#EmailSend1").click(function (event) {
    debugger;
    event.preventDefault();

    var term = $("#TextEmail").val();
    var filter = /^([\w-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([\w-]+\.)+))([a-zA-Z]{2,4}|[0-9]{1,3})(\]?)$/;

    if (!filter.test(term)) {
        $("#TextEmail").val("");
        $("#TextEmail").attr('placeholder', 'الايميل غير صحيح');

        $('#TextEmail').css('color', 'red');
    }
    else {

        $.ajax({
            method: 'Get',

            url: "/Home/SendEmail",
            data: { name: term },
            context: document.body
        }).done(function (data) {
            $("#TextEmail").val('');
            $("#TextEmail").attr('placeholder', data);
            if (data == 'تم التسجيل بنجاح') {
                $('#TextEmail').css('color', 'green');
            }


        });

    }
});
