document.addEventListener("DOMContentLoaded", function () {

  const menuToggle = document.querySelector('.menu-toggle');
  const menu = document.querySelector('.menu');
  menuToggle.addEventListener('click', () => {
    menu.classList.toggle('active');
  });

  document.querySelectorAll('.menu a').forEach(link => {
    link.addEventListener('click', () => {
      menu.classList.remove('active');
    });
  });

  const searchInput = document.getElementById('searchInput');
  const liveOnlyToggle = document.getElementById('liveOnly');
  const hotOnlyToggle = document.getElementById('hotOnly');

  function filterMatches() {
    const searchTerm = searchInput.value.toLowerCase();
    const liveOnly = liveOnlyToggle.checked;
    const hotOnly = hotOnlyToggle.checked;
    const hotCategories = [4, 5, 10, 12];

    document.querySelectorAll('.category-block').forEach(category => {
      let visibleMatches = 0;

      category.querySelectorAll('.match-item').forEach(item => {
        const text = item.querySelector('.match-link').textContent.toLowerCase();
        const isLive = item.querySelector('.match-extra').innerHTML.includes('LIVE');

        const matchesSearch = text.includes(searchTerm);
        const matchesLive = !liveOnly || isLive;
        const matchCategoryId = parseInt(item.closest('.category-block').id.replace('category-', ''));
        const matchesHot = !hotOnly || hotCategories.includes(matchCategoryId);

        if (matchesSearch && matchesLive && matchesHot) {
          item.style.display = 'flex';
          visibleMatches++;
        } else {
          item.style.display = 'none';
        }
      });

      category.style.display = visibleMatches > 0 ? 'block' : 'none';
    });
  }

  if (searchInput) {
    searchInput.addEventListener('keyup', filterMatches);
    liveOnlyToggle.addEventListener('change', filterMatches);
    hotOnlyToggle.addEventListener('change', filterMatches);
  }

  document.querySelectorAll('.faq-question').forEach(item => {
    item.addEventListener('click', () => {
      const faqItem = item.parentElement;
      faqItem.classList.toggle('active');
    });
  });

  function updateCountdowns() {
    const nowUTC = Math.floor(new Date().getTime() / 1000);

    document.querySelectorAll('.today-badge[data-starttime]').forEach(el => {
      const matchStartTR = parseInt(el.getAttribute('data-starttime'));
      const diff = matchStartTR - nowUTC;

      if (diff > 0) {
        const nowDate = new Date();
        const matchDate = new Date((matchStartTR + 3 * 3600) * 1000);

        const localMatchDate = new Date(matchDate.getTime() - nowDate.getTimezoneOffset() * 60000);
        const localNow = new Date(nowDate.getTime() - nowDate.getTimezoneOffset() * 60000);

        const daysDiff = Math.floor((localMatchDate - localNow) / (1000 * 60 * 60 * 24));
        let hours = localMatchDate.getHours();
        const minutes = localMatchDate.getMinutes().toString().padStart(2, '0');

        const ampm = hours >= 12 ? 'PM' : 'AM';
        hours = hours % 12;
        hours = hours ? hours : 12;

        const formattedTime = `${hours}:${minutes} ${ampm}`;

        if (daysDiff === 0) {
          const hoursLeft = Math.floor(diff / 3600);
          const minutesLeft = Math.floor((diff % 3600) / 60);

          if (hoursLeft > 0) {
            el.textContent = `Starts in: ${hoursLeft}h ${minutesLeft}m`;
          } else {
            el.textContent = `Starts in: ${minutesLeft}m`;
          }
        } else if (daysDiff === 1) {
          el.textContent = `Tomorrow, ${formattedTime}`;
        } else {
          const weekdayNames = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
          const dayName = weekdayNames[localMatchDate.getDay()];
          el.textContent = `${dayName}, ${formattedTime}`;
        }
      } else {
        el.textContent = 'LIVE';
      }
    });
  }

  updateCountdowns();
  setInterval(updateCountdowns, 60000);

});

styles:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}
html, body {
  font-family: 'Poppins', sans-serif;
  background: #0D1117;
  color: #ffffff;
  scroll-behavior: smooth;
}
a {
  text-decoration: none;
  color: inherit;
}
.filter-toggles {
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 15px;
  margin-top: 10px;
  flex-wrap: wrap;
}
.live-toggle, .hot-toggle {
  display: flex;
  align-items: center;
  gap: 8px;
  background: #1E293B;
  padding: 8px 14px;
  border-radius: 20px;
  font-size: 14px;
  color: #00FFAA;
  font-weight: 600;
  transition: background 0.3s ease;
  cursor: pointer;
  min-height: 40px;
}
.hot-toggle {
  color: #FF7F50;
}
.live-toggle:hover, .hot-toggle:hover {
  background: #263344;
}
.slider, .hot-slider {
  width: 40px;
  height: 20px;
  background-color: #ccc;
  border-radius: 20px;
  position: relative;
  flex-shrink: 0;
}

.slider::before, .hot-slider::before {
  content: "";
  position: absolute;
  width: 16px;
  height: 16px;
  top: 2px;
  left: 2px;
  background: #fff;
  border-radius: 50%;
  transition: 0.3s;
}

.live-toggle input:checked + .slider {
  background-color: #00FFAA;
}

.live-toggle input:checked + .slider::before {
  transform: translateX(20px);
}

.hot-toggle input:checked + .hot-slider {
  background-color: #FF7F50;
}

.hot-toggle input:checked + .hot-slider::before {
  transform: translateX(20px);
}

.live-toggle input,
.hot-toggle input {
  display: none;
}
@media (max-width: 768px) {
  .filter-toggles {
    flex-direction: column;
    gap: 12px;
    margin-top: 10px;
  }
}
