}
.back_semi_opacity:before
{
  content: '';
  z-index: 0;
  background-color:#260b0c;
  opacity: .2;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: -1;
}
.back_opacityMedium:before
{
  content: '';
  z-index: 0;
  background-color:#000000;
  opacity: .4;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: -1;
}
.back_opacity:before
{
  content: '';
  z-index: 0;
  background-color:#000000;
  opacity: .6;
  position: absolute;
  top: 0;
  bottom: 0;
  left: 0;
  right: 0;
  z-index: -1;
}
