.checkbox_filter {
    width: 30%;
    margin: 0px 5px;
    position: relative;
    display: inline-block;
}
.checkbox_filter input[type="checkbox"] {
    width: auto;
    opacity: 0.00000001;
    position: absolute;
    left: 0;
    margin-left: -20px;
}
.checkbox_filter label {
    position: relative;
}
.checkbox_filter label:before {
    content: '';
    position: absolute;
    left: 0;
    top: 0;
    margin: 4px;
    width: 22px;
    height: 22px;
    transition: transform 0.28s ease;
    border-radius: 3px;
    border: 2px solid #da222e;
    background: #fff;
}
.checkbox_filter label:after {
  content: '';
    display: block;
    width: 10px;
    height: 5px;
    border-bottom: 3px solid #da222e;
    border-left: 3px solid #da222e;
    -webkit-transform: rotate(-45deg) scale(0);
    transform: rotate(-45deg) scale(0);
    transition: transform ease 0.25s;
    will-change: transform;
    position: absolute;
    top: 12px;
    left: 10px;
}
.checkbox_filter input[type="checkbox"]:checked ~ label::before {
    color: #da222e;
}
.checkbox_filter input[type="checkbox"]:checked ~ label::after {
    -webkit-transform: rotate(-45deg) scale(1);
    transform: rotate(-45deg) scale(1);
}
.checkbox_filter label {
    min-height: 34px;
    display: block;
    padding-left: 40px;
    margin-bottom: 0;
    font-weight: normal;
    cursor: pointer;
    vertical-align: sub;
}
.checkbox_filter label span {
    position: absolute;
    top: 50%;
    font-size: 14px;
    font-weight: bold;
    -webkit-transform: translateY(-50%);
    transform: translateY(-50%);
}
.checkbox_filter input[type="checkbox"]:focus + label::before {
    outline: 0;
}

