/**
 * Datamodel Signify
 *
 */
var version = "1.1.0",
  datalayerName = "eventDataQueue",
  datamodelName = "_signifyDM";


window[datalayerName] = window[datalayerName] || [];
window[datamodelName] = window[datamodelName] || [];

(function (datalayer, datamodel) {
  var pageViewAlikeEvents = {
      'page/view': {
        _reset: true
      },
      'component/product-detail/update-product/view': {
        _reset: false,
        _mergeRawPV: true,
        overwrite: {
          'page.pageInfo.pageName': 'event.attributes.pageInfo.pageName',
          'page.products': 'event.products'
        }
      }
    }

    isolatedEvents = [
      "page/load"
    ],
    consentEvents = [
      "consent/init", // NOTE: for current implementation of consent aware datalayer push trigger, it is mandatory to pass through the consent related events!
      "consent/update"
    ],
    blockedEvents = [
      // no blocked events (yet)
    ];

    var lastPV = {}, // Storage of last pageview data
        lastRawPV = {}, // Storage of last pageview data (before translations)
        lastConsentLevels = {}; // Storage of last consentLevels

  // Create the callback function which contains all our magic
  var callback = function (event, data, queue) {
    // Stop directly in case of a blocked event
    if (blockedEvents.indexOf(event) >= 0) return;

    // Setup data._siginfyDM for debugging purposes alowing us to understand what operations have been applied in the datamodel.
    var _signifyDM = {
      datamodelVersion: version,
      operations: []
    }

    var logOP = function (desc) {
      _signifyDM.operations.push(desc);
    }

    var mergeObj = function () {
      for (var a = {}, b, c, d = [].splice.call(arguments, 0); 0 < d.length;)
        if (b = d.splice(0, 1)[0],
          "[object Object]" == Object.prototype.toString.call(b))
          for (c in b)
            b.hasOwnProperty(c) && ("[object Object]" == Object.prototype.toString.call(b[c]) ? a[c] = mergeObj(a[c] || {}, b[c]) : a[c] = b[c]);
      return a
    }

    // Check if a path exists. If doReturnValue is true, the actual value is returned when the path exists. (Carefull with Falsy values!)
    var pathExists = function (path, doReturnValue, subject) {
      if (subject === undefined) subject = data;
      if (typeof (path) == "string") path = path.split(".");
      if (path.length == 0) {
        if (doReturnValue) return subject;
        else return true;
      }
      var next = path.shift();
      if (subject.hasOwnProperty(next)) {
        return pathExists(path, doReturnValue, subject[next]);
      } else {
        return false;
      }
    }

    // Alias for pathExist(path, true)
    var getVal = function(path, subject) {
      return pathExists(path, true, subject);
    }

    var setValue = function (value, path, subject) {
      if (typeof (path) == "string") {
        oringalPath = path;
        path = path.split(".");
      }
      var length = path.length;
      if (length > 0) {
        var next = path.shift();
        if (length == 1) {
          subject[next] = value;
          logOP("Set value `" + value + "` for `" + oringalPath + "`");
        } else {
          if (typeof(subject) == "undefined") subject = {};
          if (!subject.hasOwnProperty(next)) subject[next] = {};
          setValue(value, path, subject[next]);
        }
      }
    }

    // Polyfill isArray
    if (!Array.isArray) {
      Array.isArray = function (arg) {
        return Object.prototype.toString.call(arg) === '[object Array]';
      };
    }

    //#region DISABLED DUE TO NO LONGER LOADING LAUNCH FOR EMBEDDED PAGES...
    // // In case of this being an embedded iframe, postfix the evenname with 'embedded/' and move the data to a deeper property embedded.
    // if (window.self !== window.top) {
    //   logOP('Postfix eventname received from embedded iframe');
    //   event = ['embedded', event].join('/');
    //   data = {
    //     embedded: data
    //   };
    // }
    //#endregion

    // Handle passthrough events
    if (consentEvents.indexOf(event) >= 0) {
      lastConsentLevels = data.consentLevels;

      (function () {
        var eventSplit = event.split("/");
        var enrichments = {
          event: {
            component: eventSplit[0],
            action: eventSplit[1]
          }
        }

        datamodel.push({
          action: "publish",
          event: event,
          data: mergeObj(lastPV, data, enrichments)
        });
      })();

      return 
    }

    //#region enrich with native information
    var applyTransformations = function() {
      var enrichments = {
        consentLevels: lastConsentLevels
      };

      // Make sure page URL and referrer are always populated.
      if (!pathExists("page.pageInfo.destinationURL"))
        setValue(document.location.toString(), "page.pageInfo.destinationURL", enrichments); // page.pageInfo.destinationURL
      if (!pathExists("page.pageInfo.referringURL"))
        setValue(document.referrer.toString(), "page.pageInfo.referringURL", enrichments); // page.pageInfo.referringURL


      //#region hotfix determine hue brand from domain if non-existing
      if (!getVal("page.attributes.brand")) {
        if (/hue.*\.com(\.[a-z]{2})?$/i.test(document.location.hostname)) { // Hostname ends with hue.com (or a country-selector after)
          setValue("hue", "page.attributes.brand", enrichments);
          setValue("hue", "page.attributes.subbrand", enrichments);
        }
      }
      //#endregion
      // Adjust brand and subbrand - Lowercase and copy subbrand from brand if empty
      (function () {
        var brand = getVal("page.attributes.brand");
        // Lowercase if not
        if (typeof (brand) == "string" && brand != brand.toLowerCase()) {
          brand = brand.toLowerCase();
          setValue(brand, "page.attributes.brand", enrichments);
        }
        // Copy brand to subbrand in case of empty subbrand
        if (brand && !getVal("page.attributes.subbrand")) {
          setValue(brand, "page.attributes.subbrand", enrichments);
        }
      })();

      // Adjust page categories and pagename
      (function () {
        var category = getVal("page.category.categories");
        if (Array.isArray(category)) {
          // Set default category to home
          if (category.length == 0) {
            category.push("home");
            setValue(category, "page.category.categories", enrichments);
          }
          // Set primaryCategory based on the categories list.
          setValue(category[0], "page.category.primaryCategory", enrichments);

    // Check if hostname contains lighting.philips. If it does, it removes the initial pageName variable to be filled with all the categories. 
      if (window.location.hostname.indexOf("lighting.philips") == -1){
        setValue("", "page.pageInfo.pageName", enrichments);
      }
          // Update pagename to include the categories (and fix pagename being language code)
          var pageName = getVal("page.pageInfo.pageName") || "";
          if (pageName == getVal("page.attributes.language")) {
            // Language as pagename? --> homepage
            setValue("homepage", "page.pageInfo.pageName", enrichments);
          } else {
            var pageNameConstruct = [];
            if (pageName != category[category.length - 1]) { // if pagename is already in category, remove it.
              pageNameConstruct.push(pageName);
            }
            pageNameConstruct = category.concat(pageNameConstruct);
            setValue(pageNameConstruct.join(":"), "page.pageInfo.pageName", enrichments);
          }
        }
      })();


      //#region HOTFIX pagename for PDP
      (function () {
        var pageType = getVal("page.category.pageType");
        var pos;
        if (pageType == "product-page") {
          var categories = getVal("page.category.categories");

          if (Array.isArray(categories)) {
            // Remove incorrect values
            // Remove pageType
            pos = categories.indexOf(pageType);
            if (~pos) {
              categories.splice(pos, 1);
            }
            // Remove "all-products"
            pos = categories.indexOf("all-products");
            if (~pos) {
              categories.splice(pos, 1);
            }
            // Remove duplicate product family
            var len = categories.length;
            if (categories[len - 2] == categories[len - 1]) {
              categories.splice(len - 1, 1);
            }
            // Set fixed value
            setValue(categories, "page.category.categories", enrichments);

            // Fix pageName
            var pageName = getVal("page.pageInfo.pageName") || "";
            if (pageName) {
              if (!~pageName.indexOf(".")) { // If pageName does not contain CTN, add it
                var products = getVal("page.products") || [];
                var ctn = "unknown";
                if (typeof (products[0]) == 'object' && products[0].hasOwnProperty('idCode') && products[0].idCode.hasOwnProperty('ctn')) {
                  ctn = products[0].idCode.ctn;
                }
                pageName = [pageName, ctn].join(".");
              }

              // And finaly (re-)build pageName
              pageName = categories.concat(pageName).join(":");
              setValue(pageName, "page.pageInfo.pageName", enrichments);
            }
          }

        }
      })();

      //#endregion
      // Adjust pageName - replace dashes by underscores
      (function () {
        var pageName = getVal("page.pageInfo.pageName", enrichments);
        if (pageName) {
          pageName = pageName.replace(/-/g, "_");
          setValue(pageName, "page.pageInfo.pageName", enrichments);
        }
      })();

      // Remove dash from pageType
      (function () {
        pageType = getVal("page.category.pageType") || "";
        pageTypeNew = pageType.replace(/-page$/i, "page");
        if (pageType !== pageTypeNew) {
          setValue(pageTypeNew, "page.category.pageType", enrichments);
        }
      })();

      // Auto set event.action and event.component in case they are not set.
      if (!getVal("event.action") || !getVal("event.component")) {
        (function () {
          var eventSplit = event.split("/");
          var eventLength = eventSplit.length;
          if (eventLength < 2)
            return;
          if (!getVal("event.action"))
            setValue(eventSplit[eventLength - 1], "event.action", enrichments);
          if (!getVal("event.component"))
            setValue(eventSplit[eventLength - 2], "event.component", enrichments);
        })();
      }

      // Augment product arrays to add usable helper properties
      (function () {
        // function createByContext(src, target) {
        function createByContext(products) {
          var EXTRACTS = {
            "ids": "id",
            "ctns": "idCode.ctn",
            "names": "name",
            "basePrices": "price.basePrice"
          };
          // var products = getVal(src);
          if (!Array.isArray(products))
            return products;

          // Add additional properties
          products["byContext"] = {};
          Object.keys(EXTRACTS).forEach(function (key) {
            products[key] = [];
          });

          // Populate additional properties by traversing the products array
          products.forEach(function (val, idx, arr) {
            // byContext
            var context = val["productContext"];
            if (!context || typeof (context) != "string") {
              // Make sure we have a productContext
              val["productContext"] = context = "_noContext";
            } else {
              // Replace any whitespaces
              context = context.replace(/\s/g, "_");
            }
            if (!arr["byContext"].hasOwnProperty(context)) {
              // Create new property in the byContext property.
              arr["byContext"][context] = [];
              Object.keys(EXTRACTS).forEach(function (key) {
                arr["byContext"][context][key] = [];
              });

            }
            // Add product to the contexted list
            arr["byContext"][context].push(val);

            // Create specific lists
            Object.keys(EXTRACTS).forEach(function (key) {
              // var val = getVal([src, idx, EXTRACTS[key]].join("."));
              var path = [idx, EXTRACTS[key]].join(".");
              var val = pathExists(path, false, arr) ? getVal([idx, EXTRACTS[key]].join("."), arr) : undefined;
              arr[key].push(val);
              arr["byContext"][context][key].push(val);
            });
          });
          return products;
        }

        function createDerivatives(val, idx, arr) {
          // in- or outOfStock booleans
          if (pathExists("availability.availabilityStatus", true, val)) {
            val["availability"]["isOutOfStock"] = val["availability"]["availabilityStatus"] == "outOfStock";
            val["availability"]["isInStock"] = val["availability"]["availabilityStatus"] !== "outOfStock";
          }
          // Create product lineprice
          (function (val) {
            var qnt = val["quantity"] || 1;
            if (pathExists("price.basePrice", true, val)) {
              val["price"]["baseLinePrice"] = qnt * val["price"]["basePrice"];
            }
            if (pathExists("price.specialPrice", true, val)) {
              val["price"]["specialLinePrice"] = qnt * val["price"]["specialPrice"];
            }
          })(val);

          return val;
        }

        // Create filtered views by context
        var pageProducts = getVal("page.products");
        if (Array.isArray(pageProducts)) {
          try {
            pageProducts = JSON.parse(JSON.stringify(pageProducts));
          } catch (e) {
            // Ignore
          }
          pageProducts = pageProducts.map(createDerivatives);
          pageProducts = createByContext(pageProducts);
          setValue(pageProducts, "page.products", enrichments);
        }
        var eventProducts = getVal("event.products");
        if (Array.isArray(eventProducts)) {
          try {
            eventProducts = JSON.parse(JSON.stringify(eventProducts));
          } catch (e) {
            // Ignore
          }
          eventProducts = eventProducts.map(createDerivatives);
          eventProducts = createByContext(eventProducts);
          setValue(eventProducts, "event.products", enrichments);
        }
        var cartProducts = getVal("cart.products");
        if (Array.isArray(cartProducts)) {
          try {
            cartProducts = JSON.parse(JSON.stringify(cartProducts));
          } catch (e) {
            // Ignore
          }
          cartProducts = cartProducts.map(createDerivatives);
          cartProducts = createByContext(cartProducts);
          setValue(cartProducts, "cart.products", enrichments);
        }
      })();
      return enrichments;
    }

    var enrichments = {};

    //#endregion

    // Finally, store for previous pageview or merge with previous pageview data
    if (Object.keys(pageViewAlikeEvents).indexOf(event) >= 0) {
      // Pageview event: store event data for next non-pageview events

      // Check and apply overwrite values for the specific event (set in pageViewAlikeEvents)
      var eventSettings = pageViewAlikeEvents[event];

      if (eventSettings._mergeRawPV) {
        data = data = mergeObj(lastRawPV, data);
      }

      if (eventSettings.overwrite) {
        var overwrite = {};
        // Capture values for the properties that need an overwrite and store in overwrite object
        Object.keys(eventSettings.overwrite).forEach(function(newPath){
          path = eventSettings.overwrite[newPath];
          setValue(getVal(path, data), newPath, overwrite)
        });
        // Merge overwrite object over data, to apply the overwriting
        data = mergeObj(data, overwrite);
      }

      // Store "raw" pageview data for later use
      lastRawPV = mergeObj(data);

      // Apply and merge enrichtments with data
      enrichments = applyTransformations();
      data = mergeObj(data, enrichments, {
        _signifyDM: _signifyDM  // also add information about the datamodel version and operations applied
      });

      if (!eventSettings._reset) {
        // In case of no reset, merge in the lastPV data
        data = mergeObj(lastPV, data);
      }

      // Save the resulting data to potentially being merged in later
      lastPV = mergeObj(data);
    } else if (isolatedEvents.indexOf(event) >= 0) {
      enrichments = applyTransformations();
      data = mergeObj(data, enrichments);
      data._signifyDM = _signifyDM;
    } else {
      // Non-pageview: merge with previous pageview data
      enrichments = applyTransformations();
      data = mergeObj(lastPV, data, enrichments);
      data._signifyDM.operations = lastPV._signifyDM.operations.concat(_signifyDM.operations);
    }

    // Passthrough to datamodel
    datamodel.push({
      action: 'publish',
      event: event,
      data: mergeObj(data, _signifyDM)
    });
  }

  // Register datamodel handler
  datalayer.push({
    action: 'subscribe',
    name: 'Datamodel handler',
    on: '.*',
    callback: callback
  });

})(window[datalayerName], window[datamodelName]);

});

