#Cart-Drawer {
  position: fixed;
  top: 0;
  right: 0;
  width: 400px;
  max-width: 100%;
  height: 100%;
  background: #fff;
  z-index: 10000;
  transition: right 0.35s ease;
  box-shadow: -2px 0 12px rgba(0, 0, 0, 0.2);
}

.cart-drawer[inert] {
  right: -100% !important;
  display:block !important;
}

.cart-drawer-overlay {
  position: fixed;
  top: 0;
  left: 0;
  height: 100vh;
  width: 100vw;
  background: rgba(0, 0, 0, 0.4);
  z-index: 9999;
  opacity: 0;
  visibility: hidden;
  transition: right 0.35s ease;
}

.cart-drawer-overlay.active {
  opacity: 1;
  visibility: visible;
}

