styles:
	.adthrive-ad {
		margin-top: 10px;
		margin-bottom: 10px;
		text-align: center;
		overflow-x: visible;
		clear: both;
		line-height: 0;
	}
	/* for final sticky SB */
.adthrive-sidebar.adthrive-stuck {
margin-top: 30px;
z-index: 9!important;
}
/* for ESSAs */
.adthrive-sticky-sidebar > div {
top: 30px!important;
}

.more-from-category {
clear:both;
position: relative;
margin-bottom: -30px;
top: -30px;
}

.sidebar-primary .widget.featuredpost .post {
float:left;
margin-right:25px;
}

.adthrive-device-desktop .adthrive-recipe, .adthrive-device-tablet .adthrive-recipe {
float: right;
margin-left: 10px;
}

.adthrive-player-close {
top: -8px !important;
}

.comment-content a {
overflow-wrap: break-word;
word-wrap: break-word;
}

/* too much padding on mobile fix 10-8-19 */
body.adthrive-device-phone .content {
padding-left:0px;
padding-right:0px;
}

body.adthrive-device-phone .mv-create-wrapper {
padding-left:5px !important;
padding-right:5px !important;
}

@media (max-width:414px) {
.site-inner {
padding-left: 3%;
padding-right:3%;
}
}

@media (max-width:360px) {
.site-inner {
padding-left: 1.7%;
padding-right:1.7%;
}
body.adthrive-device-phone .mv-create-wrapper {
padding-left:3px !important;
padding-right:3px !important;
}
}

body[data-new-gr-c-s-check-loaded] .adthrive-comscore {
  display: none;
}

/* confirm click footer ad fix test */
body.adthrive-device-phone .adthrive-footer.adthrive-sticky {
padding-top:0px;
overflow:visible !important;
border-top:0px !important;
}
body.adthrive-device-phone .adthrive-sticky.adthrive-footer>.adthrive-close {
top:-25px !important;
right:0px !important;
border-radius: 0px !important;
line-height: 24px !important;
font-size: 24px !important;
}
/* confirm click footer ad fix test  end */

#adthrive-collapse-title{
margin: 7px 0;
}

/* ZD 159072 - Override site transforms */
.site-container .site-inner {
    transform: none;
    -webkit-transform: none;
}
/* END override site transforms */

body.archive.hfeed .adthrive-content,
body.search .adthrive-content {
  grid-column: 1 / -1;
}

/* Fix tasty recipe ingredients clearing floats */
@media only screen and (min-width: 768px) {
  .tasty-recipes-ingredients li {
    clear: unset !important;
  }
}

@media print {
  .adthrive-ad, .adthrive-comscore {
    display: none;
    height: 0px;
    width: 0px;
    visibility:hidden;
  }
}

body.adthrive-device-phone .adthrive-recipe,
body.adthrive-device-phone .adthrive-content {
  width: 100vw;
  position: relative;
  left: 50%;
  right: 50%;
  margin-left: -50vw;
  margin-right: -50vw;
}

div[id^=cls-video-container-] {
margin-top:15px;
}

/* Top Center White Background */
.adthrive-collapse-mobile-background {
background-color: #fff!important;
}
.adthrive-top-collapse-close > svg > * {
stroke: black;
font-family: sans-serif;
}
.adthrive-top-collapse-wrapper-video-title,
.adthrive-top-collapse-wrapper-bar a a.adthrive-learn-more-link {
color: black!important;
}
/* END top center white background */

/* Top Center video and Slickstream - prevent video player shifting when scrolling */
body.slick-filmstrip-toolbar-showing.adthrive-device-phone .adthrive-player-position.adthrive-collapse-mobile.adthrive-collapse-top-center:not(.adthrive-player-without-wrapper-text),
body.slick-filmstrip-toolbar-showing.adthrive-device-phone .adthrive-collapse-mobile-background {
    transform: none!important;
}
body.slick-filmstrip-toolbar-showing.adthrive-device-phone .adthrive-player-position.adthrive-collapse-mobile.adthrive-collapse-top-center.adthrive-player-without-wrapper-text {
transform: translateX(-50%)!important;
}
/* END Top Center video and Slickstream */

.adthrive-device-phone .adthrive-content {
   min-height: 300px !important;
}

/* Add 'Advertisement' before ads */
.adthrive-content:before,
.adthrive-sidebar:before,
.adthrive-recipe:before,
.adthrive-footer:before {
content: "ADVERTISEMENT";
font-family: var(--global-body-font-family);
font-size: 10px;
letter-spacing: 1px;
margin-top: 0;
margin-bottom: 10px;
display: block;
color: #888;
}
.adthrive-footer:before {
align-self: flex-start;
width: 100%;
}
.adthrive-footer {
padding-top: 10px;
}
.adthrive-parallax-slot {
padding-bottom: 10px; /* parallax ads need a bit more spacing */
}
/* END 'Advertisement' */

/* Ensure Top Center Mobile Playlist Player sticks */
#main .hentry .entry-content-wrap {
    transform: unset !important;
}
/* END Ensure Top Center Mobile Playlist Player sticks */

