@media screen and ( max-width : 720px ) {
	.dpsp-content-wrapper.dpsp-hide-on-mobile,
	.dpsp-share-text.dpsp-hide-on-mobile {
		display: none;
	}
	.dpsp-has-spacing .dpsp-networks-btns-wrapper li {
		margin:0 2% 10px 0;
