/* bắt đầu popup */
@keyframes rotate-forever {
  0% {
    transform: rotate(0);
  }
  100% {
    transform: rotate(360deg);
  }
}
@keyframes spinner-loader {
  0% {
    transform: rotate(0);
  }
  100% {
    transform: rotate(360deg);
  }
}
.pum-container,
.pum-content,
.pum-content + .pum-close,
.pum-content + .pum-close:active,
.pum-content + .pum-close:focus,
.pum-content + .pum-close:hover,
.pum-overlay,
.pum-title {
  background: 0 0;
  border: none;
  bottom: auto;
  clear: none;
  cursor: default;
  float: none;
  font-family: inherit;
  font-size: medium;
  font-style: normal;
  font-weight: 400;
  height: auto;
  left: auto;
  letter-spacing: normal;
  line-height: normal;
  max-height: none;
  max-width: none;
  min-height: 0;
  min-width: 0;
  overflow: visible;
  position: static;
  right: auto;
  text-align: left;
  text-decoration: none;
  text-indent: 0;
  text-transform: none;
  top: auto;
  visibility: visible;
  white-space: normal;
  width: auto;
  z-index: auto;
}
.pum-container .pum-content + .pum-close > span,
.pum-content,
.pum-title {
  position: relative;
  z-index: 1;
}
.pum-overlay,
html.pum-open.pum-open-overlay-disabled.pum-open-fixed .pum-container,
html.pum-open.pum-open-overlay.pum-open-fixed .pum-container {
  position: fixed;
}
.pum-overlay {
  height: 100%;
  width: 100%;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 1999999999;
  overflow: initial;
  display: flex;
  transition: 0.15s ease-in-out;
}
.pum-overlay.pum-form-submission-detected,
.pum-overlay.pum-preview {
  display: block;
}
.pum-overlay,
.pum-overlay *,
.pum-overlay :after,
.pum-overlay :before,
.pum-overlay:after,
.pum-overlay:before {
  box-sizing: border-box;
}
.pum-container {
  top: 100px;
  position: absolute;
  margin-bottom: 3em;
  z-index: 1999999999;
}
.pum-container.pum-responsive {
  left: 50%;
  margin-left: -47.5%;
  width: 95%;
  height: auto;
  overflow: visible;
}
.pum-container.pum-responsive img {
  max-width: 100%;
  height: auto;
}
