}
.pum-form--style-default label {
  font-size: 14px;
  font-weight: 700;
}
.pum-form--style-default input[type="email"],
.pum-form--style-default input[type="text"] {
  background-color: #f8f7f7;
  margin-bottom: 5px;
  font-size: 14px;
  padding: 10px 8px;
}
.pum-form--style-default button {
  font-size: 18px;
  margin: 10px 0 0;
  padding: 10px 5px;
  cursor: pointer;
}
.pum-sub-form p.pum-newsletter-error-msg {
  margin: 0;
}
.pum-sub-form .spinner-loader {
  right: 50%;
  position: absolute;
  bottom: 40%;
}
.pum-sub-form .spinner-loader:not(:required) {
  animation: 1.5s linear infinite spinner-loader;
  border-radius: 0.5em;
  box-shadow: rgba(0, 0, 51, 0.3) 1.5em 0 0 0,
    rgba(0, 0, 51, 0.3) 1.1em 1.1em 0 0, rgba(0, 0, 51, 0.3) 0 1.5em 0 0,
    rgba(0, 0, 51, 0.3) -1.1em 1.1em 0 0, rgba(0, 0, 51, 0.3) -1.5em 0 0 0,
    rgba(0, 0, 51, 0.3) -1.1em -1.1em 0 0, rgba(0, 0, 51, 0.3) 0 -1.5em 0 0,
    rgba(0, 0, 51, 0.3) 1.1em -1.1em 0 0;
  display: inline-block;
  font-size: 10px;
  width: 1em;
  height: 1em;
  margin: 1.5em;
  overflow: hidden;
  text-indent: 100%;
}
.pum-form__field--consent {
  text-align: left;
}
.pum-form__field--consent.pum-form__field--checkbox label,
.pum-form__field--consent.pum-form__field--radio
  .pum-form__consent-radios.pum-form__consent-radios--inline
  label {
  display: inline-block;
  vertical-align: middle;
}
.pum-form__field--consent.pum-form__field--checkbox label input {
  display: inline-block;
  width: inherit;
  margin: 0;
  vertical-align: middle;
}
.pum-form__field--consent.pum-form__field--radio
  .pum-form__consent-radios.pum-form__consent-radios--inline
  label
  input {
  display: inline-block;
  width: inherit;
  margin: 0;
  vertical-align: middle;
}
.pum-form__field--consent.pum-form__field--radio
  .pum-form__consent-radios.pum-form__consent-radios--inline
  label
  + label {
  margin-left: 1em;
}
.pum-form__field--consent.pum-form__field--radio
  .pum-form__consent-radios.pum-form__consent-radios--stacked
  label {
  display: block;
  vertical-align: middle;
}
.pum-form__field--consent.pum-form__field--radio
  .pum-form__consent-radios.pum-form__consent-radios--stacked
  label
  input {
  display: inline-block;
