            this._url = url;
            this._method = method;

            originalOpen.apply(this, [method, url, ...rest]);
          };

          const originalSend = xhr.send;
          xhr.send = function (body) {
            this.addEventListener('readystatechange', function () {
              if (this.readyState === 4 && this.status === 200 && cartRegex.test(this._url)) {
                monitorCartUpdate();
              }
            });

            originalSend.apply(this, arguments);
          };
          return xhr;
        }

        window.XMLHttpRequest = awCustomXHR;
      }

      let cartUpdateTimeout = null;
      function monitorCartUpdate() {
        if (cartUpdateTimeout) {
          clearTimeout(cartUpdateTimeout);
        }

        cartJsPending = false;

        cartUpdateTimeout = setTimeout(() => {
          if (!cartJsPending) {
            fetchCartData();
          }
        }, 200);
      }

      async function fetchCartData() {
        try {
          const response = await fetch('/cart.js');
          if (response.ok) {
            cartData = await response.json();
            compareCartData(cartData);
          } else {
            console.error('Failed to fetch /cart.js:', response.status);
          }
        } catch (error) {
          console.error('Error fetching /cart.js:', error);
        }
      }

      function compareCartData(newCartData) {
        const oldCartData = __AW__slaveShopifyCart || { items: [] };
        newCartData = newCartData || {items: []};
        const oldItems = new Map(oldCartData.items.map(item => [item.id, item]));
        const newItems = new Map(newCartData.items.map(item => [item.id, item]));

        newItems.forEach((newItem, key) => {
          const oldItem = oldItems.get(key);
          const eventName = "add_to_cart";
          if (!oldItem) {
            handleCartDataLayer(eventName, newItem.quantity, newItem);
          } else if (newItem.quantity > oldItem.quantity) {
            handleCartDataLayer(eventName, newItem.quantity - oldItem.quantity, newItem);
          }
        });

        oldItems.forEach((oldItem, key) => {
          const newItem = newItems.get(key);
          const eventName = "remove_from_cart";
          if (!newItem) {
            handleCartDataLayer(eventName, oldItem.quantity, oldItem);
          } else if (oldItem.quantity > newItem.quantity) {
            handleCartDataLayer(eventName, oldItem.quantity - newItem.quantity, newItem);
          }
        });

        __AW__slaveShopifyCart = newCartData;
      }

      function handleCartDataLayer(eventName, quantity, item) {
        const price = (item.discounted_price ?? item.price) / 100;
        const discount = (item.price - (item.discounted_price ?? 0)) / 100;
        const totalValue = price * quantity;
        let coupon = "";
        if(Array.isArray(item?.discounts)) {
          coupon = item.discounts
                  .filter(discount => typeof discount?.title === 'string')
                  .map(discount => discount.title)
                  .join(', ');
        }

        __AW__Ecommerce = {
          ecommerce: {
            currency: "KRW",
            value: totalValue,
            items: [{
              item_id: __AW__transformNumberToString(item.product_id),
              item_variant: item.variant_title || "Default Variant",
              item_variant_id: __AW__transformNumberToString(item.variant_id),
              item_variant_title: item.variant_title || "Default Variant",
              item_name: item.product_title,
              item_product_id: __AW__transformNumberToString(item.product_id),
              item_product_title: item.product_title,
              sku: __AW__transformNumberToString(item.sku),
              discount: discount,
              price: price,
              item_brand: item.vendor,
              item_category: item.product_type,
              quantity: quantity,
              coupon: coupon,
              url: item?.url
            }]
          }
        };

        window[__AW__settings.dataLayerVariableName].push({ ecommerce: null });
        window[__AW__settings.dataLayerVariableName].push({
          ...{ 'event': __AW__getEventNameWithSuffix(eventName) },
          ...__AW__Ecommerce
        });

      }
    }

    if (document.readyState === 'loading') {
