.time_switch_2_label {
    cursor: pointer;
    text-indent: -9999px;
    width: 60px;
    height: 30px;
    background: #34B5B5;
    display: block;
    border-radius: 100px;
    position: relative;
}

.time_switch_2_label:after {
    content: '';
    position: absolute;
    top: 5px;
    left: 5px;
    width: 20px;
    height: 20px;
    background: #fff;
    border-radius: 90px;
    transition: 0.3s;
}

.time_switch_2_input:checked + .time_switch_2_label {
    background: #34B5B5;
}

.time_switch_2_input:checked + .time_switch_2_label:after {
    left: calc(100% - 5px);
    transform: translateX(-100%);
}

.time_switch_2_label:active:after {
    width: 20px;
}

