styles:
.login-button{
    width: 100%;
    background: #002dff;
    color: #fff;
    padding: 40px 0;
    border-radius: 10px;
    text-transform: uppercase;
    font-weight: 900;
    font-size: 20px;
    margin-bottom:10px;
    background-image: linear-gradient(91deg, rgb(0, 221, 101), rgb(1, 103, 255));
    background: linear-gradient(315deg, rgb(20 30 65) 3%, rgb(13 199 85) 38%, rgb(246 163 5) 68%, rgb(25 166 226) 98%);
    animation: gradient 5s ease infinite;
    background-size: 400% 400%;
    background-attachment: fixed;
}

@keyframes gradient {
    0% {
        background-position: 0% 0%;
    }
    50% {
        background-position: 100% 100%;
    }
    100% {
        background-position: 0% 0%;
    }
}
