styles:
html,body{
  margin: 0;
  padding: 0;
  font-family: helvetica, sans-serif;
  color: #fff;
}
@keyframes animatedBackground {
	from { background-position: 0 0; }
	to { background-position: 100% 0; }
}

@keyframes fadein {
  from { opacity: 0; }
  to   { opacity: 1; }
}

/* Firefox < 16 */
@-moz-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Safari, Chrome and Opera > 12.1 */
@-webkit-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Internet Explorer */
@-ms-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

/* Opera < 12.1 */
@-o-keyframes fadein {
    from { opacity: 0; }
    to   { opacity: 1; }
}

#background-image	{
	width: 100%;
	height: 100%;
	background-image: url(images/VC999-Packaging-Systems-Panorama.jpg);
  background-size: cover;
	background-position: 0px 0px;
	background-repeat: repeat-x;
	animation: animatedBackground 200s linear infinite;
}

#logo{
  margin: 0;
  padding: 0;
  position: absolute;
   top: 7%;
   right: 5%;
 width: auto;
 height: 100%;
 max-height: 25vh;
}

#menu-block{
  height: 65%;
  width: 45%;
  position: absolute;
  bottom: 7%;
  left: 5%;
}

#menu-block a{
  display: block;
  width: 40%;
  height: 45%;
  background-size: contain;
  background-position: center;
  background-repeat: no-repeat;
  opacity: 0;
  -webkit-animation: fadein 2s; /* Safari, Chrome and Opera > 12.1 */
     -moz-animation: fadein 2s; /* Firefox < 16 */
      -ms-animation: fadein 2s; /* Internet Explorer */
       -o-animation: fadein 2s; /* Opera < 12.1 */
          animation: fadein 2s;
  animation-delay: 1s;
  animation-fill-mode: forwards;
}

#link-north-america{
  background-image: url('images/Americas-Map-Normal.png');
  float: left;
}
#link-eurasia-africa{
  background-image: url('images/Eastern-EurAsia-and-Africa-Map-Normal.png');
  float: right;
}
#link-medical{
  background-image: url('images/medical-doctor-symbol-normal.png');
  clear: both;
  margin: 0 auto;
}

#link-north-america:hover{
  background-image: url('images/Americas-Map-Over.png');
}
#link-medical:hover{
  background-image: url('images/medical-doctor-symbol-Over.png');
}
#link-eurasia-africa:hover{
  background-image: url('images/Eastern-EurAsia-and-Africa-Map-Over.png');
}

#contact-block{
  height: 30%;
  position: absolute;
    bottom: 11%;
    right: 5%;
  text-shadow: 1px 1px #000;
  opacity: 0;
  -webkit-animation: fadein 2s; /* Safari, Chrome and Opera > 12.1 */
     -moz-animation: fadein 2s; /* Firefox < 16 */
      -ms-animation: fadein 2s; /* Internet Explorer */
       -o-animation: fadein 2s; /* Opera < 12.1 */
          animation: fadein 2s;
  animation-delay: 1s;
  animation-fill-mode: forwards;
}
#contact-table{
  font-size: calc(10px + (16 - 10) * ((100vw - 300px) / (1600 - 300)));
  position:relative;
}
#contact-table th,td{
  padding: 1px 5px;
  white-space: nowrap;
}
#contact-table th{
  text-align: left;
}
#contact-table th span{
  border-radius: 25px;
  border: 1px solid #eee;
  display: block;
  padding: 3px 7px;
}
#contact-table tr:hover th span{
  border-color: red;
  background-color: red;
}
#contact-table td a{
  color: #fff;
  text-decoration: none;
}

.rowlink::before {
  content: "";
  display: block;
  position: absolute;
  left: 0;
  width: 100%;
  height: 1.5em; /* don't forget to set the height! */
}

@media screen and (max-device-width: 800px),(max-width: 800px) {
  #logo,#menu-block,#contact-block{
    display: table;
    position: static;
    margin: 0 auto 25px auto;
  }
  #menu-block{
    height: 25%;
    width: 95%;
  }
  #menu-block a{
    width: 33%;
    height: 100%;
  }
  #link-eurasia-africa,#link-medical{
    clear: none;
    float: left;
  }
  #contact-block{
    width: 60%;
  }
}
