styles:
.popbox.visible {
	opacity: 1;
	z-index: 999;
  transition: opacity 0.3s ease;
}

.popbox.opened {
	display: block;
}

.popbox_locked .popbox_main_content.popbox_blur {
	filter:blur(15px);
}

html.popbox_locked {
  overflow: hidden;
  -ms-touch-action: none;
  touch-action: none;
}

.popbox {
	z-index: -999;
	position: fixed;
	width: 100%;
	max-width: 100%;
	height: 100%;
	top: 0;
	left: 0;
  will-change: opacity;
  transition: opacity 0.3s ease;
  opacity: 0;
  will-change: opacity;
	background: rgba(60, 52, 66, 0.7);
	padding: 18px;
	padding-top: 5%;
	overflow-y: auto;
	display: none;
}

.popbox_container {
	border-radius: 5px;
	margin-bottom: 18px;
	padding: 18px;
	margin-left: auto;
	margin-right: auto;
	background:#fff;
	max-width:400px;
}

.c-header__options:before {
    content: '';
    height: 46px;
    width: 100%;
    position: absolute;
    top: 0px;
    left: 0;
    display: block;
    background-color: #000;
    z-index: -1;
}

.resumen-post {
    font-size: 20px;
    line-height: 28px;
    margin: 50px auto;
    color: #333333;
    font-weight: 400;
    max-width: 80%;
    width: 844px;
    text-align: center;
}

.resumen-post-title {
    font-size: 53px;
    line-height: 48px;
    color: #000;
    text-transform: uppercase;
}

@media (max-width:720px) {
    .resumen-post-title {
        font-size: 32px;
        line-height: 36px;
    }
    .resumen-post {

        max-width: 94%;
    }
}

.bg-inscripcion {
    display: none;
}

.o-section-inscripcion {
    background-color: #080808;
}

@media (min-width:1024px) {
    .bg-inscripcion {
        display: block;
        position: absolute;
        left: 0;
        top: 0;
        z-index: -1;
        overflow: hidden;
    }
}

.dreams-header {
    background-color: #080808;
    /*background: url(/Content/2025/img/bg/BG_DREAMS.png) no-repeat #080808;
    background-size: cover;
    background-position: top center;*/
    padding: 40px 20px 20px 20px;
    width: 100%;
    text-align: center;
    box-sizing: border-box;
}

.dreams-header-logo {
    max-width:190px;
}

.dreams-header-block {
    padding-top: 40px;
    margin: 0 auto;
    max-width: 1190px;
    width: 95%;
    display: block;
}

.dreams-header-block h2 {
    font-family: 'TWKLausanne',Arial, Baskerville, monospace;
    color: #fff;
    font-size: 34px;
    line-height: 36px;
    text-align: left;
    text-transform: uppercase;
}

.dreams-header-block p {
    margin-top: 30px;
    font-family: 'TWKLausanne',Arial, Baskerville, monospace;
    color: #fff;
    font-size: 18px;
    line-height: 22px;
    text-align: left;

        margin-bottom:30px;
}

@media (min-width:1190px) {

    .dreams-header {
        min-height: 890px;
        padding: 70px 20px 20px 20px;
    }

    .dreams-header-logo {
        max-width: 100%;
    }

    .dreams-header-back {
        background: url(/Content/2025/img/bg/circulos_derecha.svg) no-repeat #080808;
        background-position: right -890px bottom -400px;
        background-size: auto;
    }

    .dreams-header-block {
        padding-top: 100px;
        width: 100%;
        display: grid;
        -ms-grid-columns: calc(40% - 60px) calc(60% - 30px);
        grid-template-columns: calc(40% - 60px) calc(60% - 30px);
        grid-column-gap: 30px;
    }

    .dreams-header-block h2 {
        font-size: 34px;
        line-height: 36px;
    }

    .dreams-header-block p {
        margin-top: 40px;
        font-size: 20px;
        line-height: 28px;
    }
}

