let initATSlickTries = 20;
let initATSlickStarted = false;

function initATSlick(containerId) {
    try {
        if (typeof window.jQuery === 'undefined') throw new Error('jQuery not loaded yet');
        if (typeof $.fn.slick === 'undefined') throw new Error('Slick Slider not loaded yet');

        const atSlider = $('#' + containerId);
        if (atSlider.length === 0) throw new Error('#' + containerId + ' not find');

        const atSliderTarget = atSlider.find('.at-slider-products');
        if (atSliderTarget.length === 0) throw new Error('.at-slider-products not find');

        const atSliderConfig = {
            appendDots: atSlider.find('.at-slider-controls__dots'),
            arrows: true,
            dots: true,
            infinite: false,
            prevArrow: $('.at-slider-controls__arrows--left'),
            slidesToShow: 1,
            slidesToScroll: 1,
            mobileFirst: true,
            nextArrow: $('.at-slider-controls__arrows--right'),
            responsive: [
                {
                    breakpoint: 768,
                    settings: {
                        slidesToShow: 4,
                        slidesToScroll: 4,
                        dots: false
                    }
                }
            ]
        };

        atSliderTarget.on('init', function(event, slick) {
            if (slick.$dots) {
                slick.$dots.find('li').html('<span class="slider-controls__dots"></span>');
            }

            initATSlickStarted = true;
        });

        atSliderTarget.on('breakpoint', function(event, slick, breakpoint) {
            if (slick.$dots) {
                slick.$dots.find('li').html('<span class="slider-controls__dots"></span>');
            }
        });

        atSliderTarget.slick(atSliderConfig);
    } catch (error) {
        // console.error(666, error);

        setTimeout(function() {
            if (initATSlickTries !== 0 && !initATSlickStarted) {
                initATSlickTries--;
                initATSlick(containerId);
            }
        }, 1000);
    }
}

function TestAB(code, environment, segment, front, name, experience, dispatch_ga, dispatch_at) {
    this._initialized = false;
    this.code = code.toLowerCase();
    this.dispatch_ga = dispatch_ga;
    this.dispatch_at = dispatch_at;
    this.env = environment.toLowerCase();
    this.front = front.toLowerCase();
    this.front_letter = '';
    this.name = name.toLowerCase();
    this.segment = segment.toLowerCase();
    this.experience = experience.toLowerCase();

    this.initialization();
}

TestAB.prototype.initialization = function() {
    try {
        if (document.cookie.indexOf('vivo_reg_city') === -1 && window.location.pathname.indexOf('/para-voce') > -1) {
            return console.warn('Página de Bússola, execução do teste cancelado.');
        }

        if (!document.body) throw new Error('O elemento "body" não encontrado.');

        if (this._initialized) return _satellite.logger.warn('O teste já foi inicializado.');

        this._initialized = true;
        this.front_letter = this.getFrontLetter(this.front);
        document.body.classList.add(this.getInfo(2));

        _satellite.setDebug(this.env === 'dev' || this.env === 'qa');
        _satellite.logger.info('Teste inicializado:', this.getInfo('full'));
        window._uxa.push(['trackPageEvent', "ECID:"+_satellite.getVar("ECID")]);
    } catch (error) {
        console.error('initialization', error);
        setTimeout(() => this.initialization(), 100);
    }
}

TestAB.prototype.addQueryToURL = function(url, query) {
    if (!url) return false;
    if (!query) return url;
    return url + (url.indexOf('?') > -1 ? '&' : '?') + query;
}

TestAB.prototype.convertPriceToNumber = function(price) {
    let num = price.replace('R$', '').replace('.', '').replace(' ', '').replace(',', '.');
    num = parseFloat(num);
    if (isNaN(num)) return 0.00;
    return num;
}

TestAB.prototype.cookie = {
    get: function(name) {
        let v = document.cookie.match('(^|;) ?' + name + '=([^;]*)(;|$)');
        return v ? v[2] : null;
    },
    set: function(name, value, days) {
        let d = new Date();
        d.setTime(d.getTime() + 24*60*60*1000*days);
        document.cookie = name + "=" + value + ";path=/;domain=.vivo.com.br;expires=" + d.toGMTString();
        document.cookie = name + "=" + value + ";path=/;domain=.vivotv.com.br;expires=" + d.toGMTString();
    },
    remove: function(name) {
        if (!this.get(name)) return;
        document.cookie = name + "=;path=/;domain=.vivo.com.br;expires=Thu, 01 Jan 1970 00:00:01 GMT";
        document.cookie = name + "=;path=/;domain=.vivotv.com.br;expires=Thu, 01 Jan 1970 00:00:01 GMT";
    },
}

TestAB.prototype.createLocationChangeEvent = function() {
    history.pushState = (f => function pushState() {
        var ret = f.apply(this, arguments);
        window.dispatchEvent(new Event('pushstate'));
        window.dispatchEvent(new Event('locationchange'));
        return ret;
    })(history.pushState);

    history.replaceState = (f => function replaceState() {
        var ret = f.apply(this, arguments);
        window.dispatchEvent(new Event('replacestate'));
        window.dispatchEvent(new Event('locationchange'));
        return ret;
    })(history.replaceState);

    window.addEventListener('popstate', () => {
        window.dispatchEvent(new Event('locationchange'))
    });
}

TestAB.prototype.getDataLayerPurchaseForControle = function() {
    if (!dataLayer) return {data: 'no dataLayer'};

    const purchase = dataLayer.filter(data => data.event === 'purchase');

    if (purchase.length === 0) return {data: 'no purchase'};

    const actionField = purchase[0].ecommerce.purchase.actionField;
    const planFlow = purchase[0].ecommerce.planFlow;

    return {
        orderId: actionField.id !== '' ? actionField.id : '-',
        orderTotal: actionField.revenue !== '' ? actionField.revenue : '-',
        type: planFlow.type !== '' ? planFlow.type : '-',
        currentPlan: planFlow.currentPlan.name !== '' ? planFlow.currentPlan.name + '-' + planFlow.currentPlan.value : '-',
        newPlan: planFlow.newPlan.name !== '' ? planFlow.newPlan.name + '-' + planFlow.newPlan.value : '-',
    }
}

TestAB.prototype.getDataLayerPurchaseForFixa = function() {
    let purchase;

    if (window.dataLayer) {
        const event = dataLayer.filter(data => data.event === 'purchase');
        if (event.length > 0 && event[0].hasOwnProperty('ecommerce')) purchase = event;
    }

    if (window.dataLayerGauge) {
        const event = dataLayerGauge.filter(data => data.event === 'purchase');
        if (event.length > 0 && event[0].hasOwnProperty('ecommerce')) purchase = event;
    }

    if (!purchase) return {data: 'sem info'};

    const actionField = purchase[0].ecommerce.purchase.actionField;
    const products = purchase[0].ecommerce.purchase.products;
    const productsMapped = products.map(data => {
        let name = data.name.indexOf('iii') ? data.name.split('iii')[0] : data.name;
        return name.replace(/Vivo|Fibra|-| /gi, '') + '-' + data.price;
    });

    return {
        orderId: actionField.id !== '' ? actionField.id : '-',
        orderTotal: actionField.revenue !== '' ? actionField.revenue : '-',
        orderProd: productsMapped.length > 0 ? productsMapped.join('&') : '-',
    }
}

TestAB.prototype.getDevice = function() {
    return /Mobi/i.test(window.navigator.userAgent) ? 'mobile' : 'desktop';
}

TestAB.prototype.getFrontLetter = function(front) {
    const f = front.toLowerCase();
    if (f === 'pós' || f === 'pos') return 'o';
    return f[0];
}

TestAB.prototype.getInfo = function(type) {
    if (type === 'full' || type === 0) {
        return (this.segment + ':' + this.front + ':' + this.name.replace(/ /g, '-') + ':' + this.code + ':' + this.experience + ':' + this.env);
    }

    if (type === 'parcial' || type === 1) {
        return (this.code + this.front_letter + ':' + this.experience + ':' + this.env);
    }

    if (type === 'cookie' || type === 2) {
        return (this.code + this.front_letter);
    }

    if (type === 'param' || type === 3) {
        return (this.code + this.front_letter + '=' + this.experience);
    }

    return '';
}

TestAB.prototype.getQueryFromURL = function(url, query) {
    const queries = url.split('?')[1];
    const vars = queries.split('&');
    for (let i = 0; i < vars.length; i++) {
        let pair = vars[i].split('=');
        if (decodeURIComponent(pair[0]) == query) {
            return decodeURIComponent(pair[1]);
        }
    }
    _satellite.logger.log('Parâmetro não localizado:', query);
}

TestAB.prototype.getSession = function() {
    const ssn = this.cookie.get('mbox');
    if (!ssn) return 'rdm=' + Math.floor(Math.random() * (Math.pow(10, 20)));
    return 'ssn=' + ssn.split('session#')[1].split('#')[0];
}

TestAB.prototype.setMetricToAdobeTarget = function(obj) {
    _satellite.logger.info('PushTarget ~>', this.getInfo(1), obj);

    if (!this.dispatch_at) return false;

    if ('suffix' in obj) {
        const attributesToIgnore = ['suffix', 'orderTotal', 'fnSuccess', 'fnError'];
        const attributes = Object.entries(obj)
                          .filter(attribute => attributesToIgnore.indexOf(attribute[0]) === -1)
                          .reduce((acc, attribute) => acc.concat(attribute.join('=')), [])
                          .join('|');

        adobe.target.trackEvent({
            mbox: this.getInfo(2) + '-' + obj.suffix,
            params: {
                'orderId': this.getSession() + ('|dvc=' + this.getDevice()) + (attributes.length > 0 ? '|' + attributes : ''),
                'orderTotal': 'orderTotal' in obj ? obj.orderTotal : 0.00,
                'productPurchasedId': 0
            },
            success: function() {
                if (obj.fnSuccess) obj.fnSuccess.call();
                _satellite.logger.info('PushTarget Success ~> O disparo da métrica "' + obj.suffix + '" bem sucedida.');
            },
            error: function(error) {
                if (obj.fnError) obj.fnError.call();
                _satellite.logger.error('PushTarget Error ~> O disparo da métrica "' + obj.suffix + '" falhou.\n', error);
            }
        });
    }
}

TestAB.prototype.setMetricToDataLayer = function(obj) {
    _satellite.logger.info('PushGA ~>', this.getInfo(1), obj);

    if (!this.dispatch_ga || !obj) return false;

    const paramCategory = 'ec' in obj ? obj.ec : '';
    const paramAction = 'ea' in obj ? obj.ea : '';
    const paramLabel = 'el' in obj ? (obj.el + this.experience) : '';

    const info = {
        'event': 'eventCustomGA',
        'custom': {
            'category': paramCategory,
            'action': paramAction,
            'label': paramLabel,
            'mcid': this.getSession(),
            'value': 0
        },
        'page': {
            'variation': this.getInfo(0)
        }
    }

    window.dataLayer = window.dataLayer || [];
    window.dataLayer.push(info);

    window.dataLayerGauge = window.dataLayerGauge || [];
    window.dataLayerGauge.push(info);


}

