require(['jquery', 'jquery/ui'], function($) {
    $(function() {
        const $window = $(window);
        const $body = $('html, body');

        // Auto Scroll to avoid lazy load blank page while clicking on tabs
        $(".home-tabScroll").on("click", function() {
            $body.scrollTop($window.scrollTop() + 1);
        });

        // Initialize all tabs in one call
        $("#bestsellers_tabs, #newarrivals_tabs, #clearance_sale_tabs").tabs();

        // Optimized AJAX function to load category products and initialize carousel
        function handleCategoryTabClick(categoryId, type) {
            $.ajax({
                method: "POST",
                url: "https://www.tryloindia.com/mostpopular/index/categoryproducts/",
                data: {
                    categoryid: categoryId,
                    type: type
                },
                success: function(msg) {
                    const $target = $('.data-cat-id-' + categoryId);
                    $target.html(msg);

                    // Initialize the owlCarousel only if it hasn't been initialized already
                    const $owl = $target.find(".product-items");
                    if (!$owl.hasClass('owl-loaded')) {
                        $owl.owlCarousel({
                            margin: 26,
                            responsive: {
                                0: {
                                    items: 2,
                                    nav: true,
                                    dots: false
                                },
                                600: {
                                    items: 2,
                                    nav: true,
                                    dots: false
                                },
                                1000: {
                                    items: 4,
                                    nav: true,
                                    dots: false
                                }
                            }
                        });
                    }
                }
            });
        }

        // Event delegation for new arrivals and bestsellers tabs
        $(".home-tabScroll").on("click", function() {
            const $this = $(this);
            const catid = $this.data("cat-id");
            const type = $this.closest("#newarrivals_tabs").length ? "newarrival" : "bestseller";
            handleCategoryTabClick(catid, type);
        });
    });
});

