a.ssw-link-fave-menu i {
  color: #294131 !important;
}

.ssw-faveiticon {
  top: 0px;
  left: 5px;

}

.ssw-faveicon-custom .ssw-faveiticon {
  top: 50px;
  left: -5px;
  opacity: 0;
  visibility: hidden;
  transform: translate(50px);
  transition: .4s;
}

.t4s-product:hover .ssw-faveicon-custom .ssw-faveiticon {
  transform: translate(0);
  opacity: 1;
  visibility: visible;
} 

.ssw-fave-icon.ssw-wishlist-element.ssw-synchronized.ssw-icon-heart {
  color: rgb(34 34 34);
}

.ssw-fave-icon.ssw-wishlist-element.ssw-synchronized.ssw-icon-heart-o {
  color: rgb(34 34 34);
}

@media (max-width: 1024px) {
  .ssw-reward-tab-right .ssw-reward-tab-closed {
    right: 11px;
  }
  .ssw-reward-tab-right {
    bottom: 120px;
  }
  .ssw-faveicon-custom .ssw-faveiticon {
    opacity: 1;
    visibility: visible;
    transform: revert;
  }
}
