scripts:
document.addEventListener('DOMContentLoaded', function() {
    document.getElementById('gear-icon-button').addEventListener('click', function() {
        document.getElementById('change-color').classList.toggle('open');
    });
    document.querySelectorAll('.clr').forEach(function(element) {
        element.addEventListener('click', function(event) {
            let clickedDiv = event.currentTarget; // or use 'this'
            let mainColor = clickedDiv.querySelector('.main-color').getAttribute('data-color');
            let secondryColorChild = clickedDiv.querySelector('.secondry-color').getAttribute('data-color');
            document.getElementById("main-color").value = mainColor;
            document.getElementById("secondry-color").value = secondryColorChild;
            changeColor();
        });
    });
});
function changeColor(save) {
    var r = document.querySelector(":root");
    let mainColor = $("#main-color").val();
    let secondryColor = $("#secondry-color").val();
    r.style.setProperty("--background-color", secondryColor, "important");
    r.style.setProperty("--secondary-color", secondryColor, "important");
    r.style.setProperty("--main-color", mainColor);
    if (save) SaveColorServer(mainColor, secondryColor);
}

function SaveColorServer(mainColor, secondryColor) {
    $.ajax({
        type: "POST",
        url: "/change-color",
        data: {
        main_color: mainColor,
        secondry_color: secondryColor,
        _token: "isO8k7NN6GMfLNpE04xaTBwZXPoRhFE1DAo3ZSb4"
        },
        dataType: "JSON",
        success: function (response) {
            if(typeof Swal != "undefined") {
                Swal.fire({
                    title: "Color Changed!",
                    text: "Meet Your New Design.",
                    icon: "success"
                });
            }else {
                alert("Color Changed!");
            }
        }
    });
}

