; (function () {
  const prefersDark = window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches
  const setting = localStorage.getItem('vueuse-color-scheme') || 'auto'
  if (setting === 'dark' || (prefersDark && setting !== 'light'))
    document.documentElement.classList.toggle('dark', true)
})()

(function() {
  const referrer = document.referrer
  const domain = window.location.href
  const sb_referrer =  window.localStorage.getItem('sb_referrer') 
  const sb_domain = window.localStorage.getItem('sb_domain') 

  if(referrer && !sb_referrer){
    window.localStorage.setItem('sb_referrer',referrer)
  }
  if(domain && !sb_domain){
    window.localStorage.setItem('sb_domain',domain)
  }
})()
