styles:
.lenders-logo-container{
    max-width: 1316px;
    margin: 0 auto;
    padding: 20px;
}
.lenders-logo-list{
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    grid-column-gap: 34px;
    grid-row-gap: 24px;
    list-style: none;
    padding: 0;
    margin: 0;
}
.lenders-logo-list li{
    display: flex;
    justify-content: center;
    align-items: center;
}
.lenders-logo-list a img{
    width: auto;
    max-width: 100%;
    height: 80px;
    object-fit: contain;
    display: block;
    padding: 10px;
    box-sizing: border-box;
}
.lenders-logo-list li:nth-child(5n+1){
    justify-content: flex-start;
}
.lenders-logo-list li:nth-child(5n){
    justify-content: flex-end;
}
@media(max-width: 1200px){
    .lenders-logo-list{
        grid-template-columns: repeat(4, 1fr);
    }
    .lenders-logo-list li{
        justify-content: center;
    }
    .lenders-logo-list li:nth-child(4n+1){
    justify-content: flex-start;
    }
    .lenders-logo-list li:nth-child(4n){
        justify-content: flex-end;
    }
}
@media(max-width: 992px){
    .lenders-logo-list{
        grid-template-columns: repeat(3, 1fr);
        grid-column-gap: 40px;
        grid-row-gap: 30px;
    }
    .lenders-logo-list a img{
        max-width: 194px;
				height: 68px;
				min-width: unset !important;
    }
    .lenders-logo-list li{
        justify-content: center !important;
    }
    .lenders-logo-list li:nth-child(3n+1){
    justify-content: flex-start !important;
    }
    .lenders-logo-list li:nth-child(3n){
        justify-content: flex-end !important;
    }
}
@media(max-width: 768px){
    .lenders-logo-list{
        grid-template-columns: repeat(2, 1fr);
    }
    .lenders-logo-list li{
        justify-content: center !important;
    }
    .lenders-logo-list li:nth-child(2n+1){
    justify-content: flex-start !important;
    }
    .lenders-logo-list li:nth-child(2n){
        justify-content: flex-end !important;
    }
}
@media(max-width: 600px){
.lenders-logo-container{
	padding:16px;
}
    .lenders-logo-list{

        grid-column-gap: 16px;
        grid-row-gap: 14px;
    }
    .lenders-logo-list a img{
        max-width: 100%;
height: 80px;
min-width: unset !important;
max-height: 60px;
padding: 6px 0px;
    }
}

