styles:
     :root{
    --navy: #201E59;
    --orange: #9b7ff7;
}

#btn-1{
    background: #201E59 !important;
}
.hero-texts .orange{
    color: #201E59;
}
.premium .nav-link, .bi-fire{
    color: #201E59 !important;
}
body{
    margin: 0; padding: 0; box-sizing: border-box;
    background-color: #F9F9FB;
    overflow: auto !important;
}
.navbar-brand{
    display: flex;
    padding: 1% 5%;
    align-items: center;
}
.navbar-brand p{
    margin-top: 0;
    margin-left: 1rem;
}
.navbar-brand img{
    width: 3rem;
    height: 3rem;
}

.ma-img-sm{
    height: 8rem;
    position: absolute;
    z-index: 2;
    border-radius: 1.1rem;
    bottom: 1rem;
    right: 1rem;
}


.premium .nav-link, .bi-fire{
    color: var(--orange) !important;
}
#svgcolor{
    fill: var(--orange);
}
#hero{
    padding: 0% 0% 0% 5%;
    overflow: hidden;
    display: flex;
    margin: auto;
    justify-content: space-between;
    align-items: center;

}
.hero-texts{
    max-width:50%;
}
#free-games-page .free-games, #free-apps-page .free-apps{
    width: 90%;
    margin: auto;
}
