.hero-img img,.apps-hero-img img,.games-hero-img img{
    width: 80%;
    height: 100%;
    float: right;
}
.img-hero{
    width: 100%;
}
.bmixs-btn, #btn-1,#verifybtn{
    text-align: center;
    padding: 1.5rem 3rem;
    background-color: var(--orange);
    color: white;
    border: none;
    border-radius: .5rem;
    transition: .3s all;
    cursor: pointer !important;
    font-size: 1.8rem !important;
    margin-top: 3rem;
}

#footer{
    background-color: white;
    padding: 1% 5%;
}

.direct-links.horizontal{
    display: flex;
    flex-wrap: wrap;
    gap: 2rem;
}
.bmixs-btn:hover{
    background-color: var(--navy);
    color: white;

}
.bmixs-btn .bi-fire{
    color: white !important;
}
.direct-link.premium{
    color: var(--orange) !important;
}


.section-title{
    display: flex;
    align-items: center;
    margin-bottom: 2rem;
}
.section-title img, #svgcolor{
    width: 4rem;
    height: 4rem;
}

.img-cont img{
    width: 100%;
    height: 100%;
}
#free-apps{
    background-color: white;
    display: flex;
    padding: 3% 5%;
}
#free-games{
    background-color: var(--navy);
    display: flex;
    padding: 3% 5%;
}

#free-games p{
    color: white;
}
.section-desc   {
    width: 60%;
}

#footer .service-header {
    display: flex
}


.nav-link,.direct-link{
    cursor: pointer;
}

#footer .service-name {
    font-size: 3rem;
    color: var(--navy);
    margin-top: 0;
}

#footer .direct-link,
#footer .service-desc,
.contact-msg {
    font-size: 2rem;
    color: var(--navy)
}

#footer .direct-link {
    cursor: pointer;
    display: block;
    padding: .5rem 0
}


.service-logo {
    width: 4rem;
    height: 4rem;
    background-position: center;
    background-repeat: no-repeat;
    background-size: contain
}
.ma-card {
    width: 23rem;
    margin-inline-end: 3rem;
    margin-block-end: 3rem;
    padding: 1.5rem;
    border-radius: 2rem !important;
    min-height: 16rem !important;
    border: none;
    box-shadow: 0 0 10px rgb(0 0 0 / 10%);
    display: block;
}

.ma-images {
    display: flex;
    align-items: flex-start;
    justify-content: space-between;
    position: relative;
}
.ma-card-nologin .ma-images::before {
    content: "";
    position: absolute;
    width: 100%;
    height: 100%;
    bottom: 0%;
    left: 0%;
    display: block;
    z-index: 1;
    border-radius: 1rem;
    overflow: hidden;
    background: linear-gradient(45deg, rgba(0,0,0,0.9) 0%, rgba(0,0,0,0.2) 100%);
}
.ma-card-nologin .ma-img-100 {
    height: 19rem;
}

.ma-img-100 {
    height: 11rem;
    border-radius: 1rem;
    object-fit: cover;
    overflow: hidden;
    width: 100%;
}
.ma-card-body {
    padding: 1rem 1rem 1rem 0;
    color: #1B264F;
}
.ma-card-title {
    font-size: 1.8rem;
    -webkit-line-clamp: 1;
    display: -webkit-box;
    -webkit-box-orient: vertical;
    overflow: hidden;
    text-align: left;
}
.line-clamp-1 {
    display: -webkit-box !important;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
    overflow: hidden;
}
.card-text:last-child {
    margin-bottom: 0;
}
.line-clamp-2 {
    display: -webkit-box !important;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
}

.ma-card-text {
    font-size: 1.6rem;
    text-align: left;
}
.ma-card-buttons .btn-primary {
    padding: 1rem 0;
    font-size: 1.6rem;
    border-radius: 0.5rem;
    background-color: var(--orange);
    border: none;
    width: 100%;
    color: white !important;
    cursor: pointer;
    pointer-events: all;
    transition: .3s all;
    display: inline-block;
    text-align: center;
}
#free-games-here, #free-videos, #free-android {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
}
.ma-img {
    width: 9rem;
    height: 9rem;
    object-fit: cover;
    border-radius: 1rem;
}
@media (min-width: 1400px) {
    .container, .container-lg, .container-md, .container-sm, .container-xl, .container-xxl {
        max-width: 87%;
    }

}
@media (max-width:992px) {
    .direct-links.horizontal{
        display: flex;
        flex-direction: column;
        flex-wrap: wrap;
        gap: .5rem;
    }
    .hero-texts{
        max-width:unset;
    }
    #hero, #free-apps, #free-games{
        display: block;
    }
}
@media (max-width: 563px) {

    .fz-70{
        font-size: 4rem;
    }
    .fz-50{
        font-size:3.2rem;
    }
    .fz-30{
        font-size: 2rem;
    }
    #bmixs-nav .container{
        padding: 1rem;
    }
    .hero-container{
        margin-inline-start:unset;
    }
    .hero-texts {
        margin-top: 1rem;
        width: 100%;
    }
    .bmixs-btn{
        margin: 3rem 0;
    }
    .hero-img img{
        width: 100%;
        height: 100%;
    }
    #free-games-here, #free-videos, #free-android{
        justify-content: center;
    }
    .section-desc{
        width: 100%;
        padding: 0 0 5rem;
    }
    #OTPdiv{
        flex-direction: column;
    }
    #otpbutton{
        margin: 3rem 0;
    }
    #hero{
        padding: 3rem 1rem;
    }
}
