// Controllo se l'utente è loggato
const bodyClasses = document.body.classList;
const dwebIsUserLoggedIn = bodyClasses.contains('logged-in');
console.log("[DDW] User logged in:", dwebIsUserLoggedIn);

const ddwCurrentPageUrl = window.location.href;
const ddwIsPageRefresh = ddwCurrentPageUrl.includes('refresh_ce') ? 'si' : 'no';
console.log('[DDW] Page refresh:', ddwIsPageRefresh);

// Definizione dei nomi degli annunci
const adNames = [
  "div-gpt-ad-INARTICLE1",
  "div-gpt-ad-INARTICLE2",
  "div-gpt-ad-INARTICLE3"
];

// Ricerca degli annunci sulla pagina e creazione di un array di booleani
const adExists = adNames.map(name => !!document.getElementById(name));

// Output dei risultati della ricerca
adNames.forEach((name, i) => {
  console.log(`[DDW] ${name} is not null? ${adExists[i]}`);
});

// Ricevere il post_type
var ddwPostType = "page";
console.log('[DDW] Post Type: ', ddwPostType);

// Determinazione del numero di annunci InArticle stampati
var ddwAdvNumPrintedInArticle = 0;
if (adExists[2]) {
  ddwAdvNumPrintedInArticle = 3;
} else if (adExists[1]) {
  ddwAdvNumPrintedInArticle = 2;
} else if (adExists[0]) {
  ddwAdvNumPrintedInArticle = 1;
}

console.log('[DDW] Number InArticle: ', ddwAdvNumPrintedInArticle);

// Definizione della variabile ddwAdvPageType
var ddwAdvPageType;

// Controllo della pagina attuale
if (document.querySelector('body.home')) {
  ddwAdvPageType = "home";
} else if (document.querySelector('body.category')) {
  ddwAdvPageType = "category";
} else if (document.querySelector('body.single-post')) {
  ddwAdvPageType = "articolo_standard";
} else {
  ddwAdvPageType = "altro";
}

console.log("[DDW] Page Type: ", ddwAdvPageType);

var pbjs = pbjs || {};
pbjs.que = pbjs.que || [];

var ddwAdv = {
  cfg: {
    gam: {site: 'diariodelweb', regione: 'Italia'},
    device: {is_mobile: null},
    cur_page: {
      num_printed_in_article: typeof ddwAdvNumPrintedInArticle !== 'undefined' && !isNaN(ddwAdvNumPrintedInArticle) ? parseInt(ddwAdvNumPrintedInArticle) : 0,
      page_type: typeof ddwAdvPageType !== 'undefined' ? ddwAdvPageType : 'altro'
    },
    timeout: {bidder: 2000, failsafe: 3100},
    ad_request_sent: {gam: false, prebid: false},
    cmp_consent: {status: 'null'},
    prebid: {
      enable: true,
      priceGranularity: {
        "buckets": [
          {"precision": 2, "max": 5, "increment": 0.01},
          {"precision": 2, "max": 8, "increment": 0.05},
          {"precision": 2, "max": 20, "increment": 0.10},
          {"precision": 2, "max": 50, "increment": 0.50}
        ]
      },
      consentManagement: {gdpr: {cmpApi: 'iab', timeout: 500, defaultGdprScope: true}},
	    // 2024/08/22
      userSync: {
  		  filterSettings:{all:{bidders:'*',filter:'include'}},
        userIds: [
          {name: 'criteo'},
          {name: 'id5Id', params: {partner: 782, externalModuleUrl:'https://cdn.id5-sync.com/api/1.0/id5PrebidModule.js'}, storage: {type: 'html5', name: 'id5id', expires: 90, refreshInSeconds: 2*3600}},
          {name: 'quantcastId'},
          {name: 'sharedId', storage: {type: 'cookie', name: '_sharedID', expires: 365}},
          {name: 'teadsId', params: {pubId: 13239}}
        ]
      },
      schain: {validation: 'strict', config: {ver: '1.0', complete: 1, nodes: [{asi: 'diariodelwebsrl.it', sid: '3', hp: 1}]}},
      bids: {
        criteo: {"networkId": 7453, "pubid": "104249"},
        equativ: {"networkId": 2430},
        onetag: {"pubId": "56813a4511aa074"},
        rubicon: {"accountId": "11382", "siteId": "124580", "zoneId": "588760"}
      },
      slots: []
    },
    adunit_specs: {},
  },
  setCfgDevice: function() {
    ddwAdv.cfg.device.is_mobile = /Mobile|Android|iP(hone|od)|IEMobile|BlackBerry|Kindle|Silk-Accelerated|(hpw|web)OS|Opera M(obi|ini)/i.test(navigator.userAgent);
	  ddwAdv.cfg.device.type = ddwAdv.cfg.device.is_mobile ? 'mob': 'web';
	  console.log('[DDW] Device Type: ', ddwAdv.cfg.device.type);
  },
  setAdUnitsSpecs: function() {
    adunit_specs = {
      "LDB1": {
        "path": '/4435307/diariodelweb_'+ddwAdv.cfg.device.type+'_ldb1',
        "div": 'div-gpt-ad-LDB1',
    		"pos": 4,
        "name": 'ldb1',
        "collapseEmpty": false,
        "sizes": ddwAdv.cfg.device.is_mobile ? [
          [320, 100], [320, 50], [300, 100], [300, 50]
        ] : [
          [728, 90], [970, 90], [970, 250]
        ],
        "gtag_sizes": ddwAdv.cfg.device.is_mobile ? [
          [320, 100], [320, 50]
        ] : [
          [728, 90], [970, 250]
        ],
        "position": "atf",
        "prebid": {
          "enable": true,
          "bids": {
            "criteo": {"enable": true},
            "equativ": {"enable": true},
            "onetag": {"enable": true},
            "rubicon": {"enable": true}
          }
        },
        "adStatus": "request_no_sent"
      },
      "FloorAd": {
        "path": '/4435307/diariodelweb_'+ddwAdv.cfg.device.type+'_floorad',
        "div": 'div-gpt-ad-FLOORAD',
    		"pos": 1,
        "name": 'floorad',
        "collapseEmpty": true,
        "sizes": ddwAdv.cfg.device.is_mobile ? [
          [320, 100], [300, 100], [300, 50], [320, 50]
        ] : [
          [728, 90]
        ],
        "gtag_sizes": ddwAdv.cfg.device.is_mobile ? [
          [320, 100], [320, 50], 'fluid'
        ] : [
          [728, 90], 'fluid'
        ],
        "position": "atf",
        "prebid": {
          "enable": true,
          "bids": {
            "criteo": {"enable": true},
            "equativ": {"enable": true},
            "onetag": {"enable": true},
            "rubicon": {"enable": true}
          }
        },
        "adStatus": "request_no_sent"
      },
      "MPU1": {
        "path": '/4435307/diariodelweb_'+ddwAdv.cfg.device.type+'_mpu1',
        "div": 'div-gpt-ad-MPU1',
		    "pos": 6,
        "name": 'mpu1',
        "collapseEmpty": false,
        "sizes": ddwAdv.cfg.device.is_mobile ? [
          [300, 250], [300, 600]
        ] : [
          [300, 250]
        ],
        "gtag_sizes": ddwAdv.cfg.device.is_mobile ? [
          [300, 250], [300, 600]
        ] : [
          [300, 250]
        ],
        "position": ddwAdv.cfg.device.is_mobile ? "btf" : "atf",
        "prebid": {
          "enable": true,
          "bids": {
            "criteo": {"enable": true},
            "equativ": {"enable": true},
            "onetag": {"enable": true},
            "rubicon": {"enable": true}
          }
        },
        "adStatus": "request_no_sent"
      },
      "MPU2": {
        "path": '/4435307/diariodelweb_'+ddwAdv.cfg.device.type+'_mpu2',
        "div": 'div-gpt-ad-MPU2',
		    "pos": 6,
        "name": 'mpu2',
        "collapseEmpty": true,
        "sizes": [[300, 250]],
        "gtag_sizes": [[300, 250]],
        "position": "btf",
        "prebid": {
          "enable": true,
          "bids": {
            "criteo": {"enable": true},
            "equativ": {"enable": true},
            "onetag": {"enable": true},
            "rubicon": {"enable": true}
          }
        },
        "adStatus": "request_no_sent"
      }
    }
    if (ddwAdv.cfg.cur_page.num_printed_in_article >= 1) {
        adunit_specs = Object.assign({}, adunit_specs, {
	      "InArticle1": {
	        "path": '/4435307/diariodelweb_'+ddwAdv.cfg.device.type+'_inarticle1',
	        "div": 'div-gpt-ad-INARTICLE1',
			    "pos": 1,
	        "name": 'inarticle1',
	        "collapseEmpty": false,
	        "sizes": [[300, 250]],
	        "gtag_sizes": [[300, 250], 'fluid'],
          "position": "atf",
	        "prebid": {
	          "enable": true,
	          "bids": {
	            "criteo": {"enable": true},
              "equativ": {"enable": true},
              "onetag": {"enable": true},
              "rubicon": {"enable": true},
              "teads": {"enable": true, placementId: 153296, pageId: 139663} // smart6
	          }
	        },
	        "adStatus": "request_no_sent"
          }
        });
    }
    if (ddwAdv.cfg.cur_page.num_printed_in_article >= 2) {
        adunit_specs = Object.assign({}, adunit_specs, {
	      "InArticle2": {
	        "path": '/4435307/diariodelweb_'+ddwAdv.cfg.device.type+'_inarticle2',
	        "div": 'div-gpt-ad-INARTICLE2',
			    "pos": 1,
	        "name": 'inarticle2',
	        "collapseEmpty": false,
	        "sizes": [[300, 250]],
	        "gtag_sizes": [[300, 250], 'fluid'],
          "position": "atf",
	        "prebid": {
	          "enable": true,
	          "bids": {
	            "criteo": {"enable": true},
              "equativ": {"enable": true},
              "onetag": {"enable": true},
              "rubicon": {"enable": true},
              "teads": {"enable": true, placementId: 100801, pageId: 93037}
	          }
	        },
	        "adStatus": "request_no_sent"
          }
		});
	}
    if (ddwAdv.cfg.cur_page.num_printed_in_article >= 3) {
        adunit_specs = Object.assign({}, adunit_specs, {
	      "InArticle3": {
	        "path": '/4435307/diariodelweb_'+ddwAdv.cfg.device.type+'_inarticle3',
	        "div": 'div-gpt-ad-INARTICLE3',
			    "pos": 3,
	        "name": 'inarticle3',
	        "collapseEmpty": false,
	        "sizes": [[300, 250]],
	        "gtag_sizes": [[300, 250], 'fluid'],
          "position": "atf",
	        "prebid": {
	          "enable": true,
	          "bids": {
	            "criteo": {"enable": true},
              "equativ": {"enable": true},
              "onetag": {"enable": true},
              "rubicon": {"enable": true},
              "teads": {"enable": true, placementId: 100801, pageId: 93037}
	          }
	        },
	        "adStatus": "request_no_sent"
          }
        });
    }
    ddwAdv.cfg.adunit_specs = adunit_specs;
  },

 	// NEW
 	initCfg: function() {
		ddwAdv.setCfgDevice();
		ddwAdv.setAdUnitsSpecs();
		ddwAdv.setPrebidSlots();
	},
  // NEW
  setPrebidSlots: function() {
    Object.keys(ddwAdv.cfg.adunit_specs).forEach(function(key) {
      if (ddwAdv.cfg.adunit_specs[key]['prebid']['enable']) {
        var bids = [];
        if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['appnexus'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['appnexus']['enable']) {
          bids.push({
            bidder: "appnexus",
            params: {
              "placementId": ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['appnexus']['placementId']
            }
          });
        }
		    if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['connectad'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['connectad']['enable']) {
          bids.push({
            bidder: "connectad",
            params: {
              "networkId": ddwAdv.cfg.prebid.bids.connectad.networkId,
			  "siteId": ddwAdv.cfg.prebid.bids.connectad.siteId
            }
          });
        }
        if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['criteo'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['criteo']['enable']) {
          bids.push({
            bidder: "criteo",
            params: {
              "networkId": ddwAdv.cfg.prebid.bids.criteo.networkId,
			  "pubid": ddwAdv.cfg.prebid.bids.criteo.pubid
            }
          });
        }
		    if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['equativ'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['equativ']['enable']) {
          bids.push({
            bidder: "equativ",
            params: {
              "networkId": ddwAdv.cfg.prebid.bids.equativ.networkId
            }
          });
        }
		    if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['invibes'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['invibes']['enable']) {
          bids.push({
            bidder: "invibes",
            params: {
			  "placementId": ddwAdv.cfg.prebid.bids.invibes.placementId,
			  "customEndpoint": ddwAdv.cfg.prebid.bids.invibes.customEndpoint
            }
          });
        }
		    if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['ogury'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['ogury']['enable']) {
          bids.push({
            bidder: "ogury",
            params: {
			  "adUnitId": ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['ogury']['adUnitId'],
			  "assetKey": ddwAdv.cfg.prebid.bids.ogury.assetKey
            }
          });
        }
		    if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['onetag'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['onetag']['enable']) {
            bids.push({
                bidder: "onetag",
                params: {
                    "pubId": ddwAdv.cfg.prebid.bids.onetag.pubId,
                }
            });
        }
		    if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['richaudience'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['richaudience']['enable']) {
            bids.push({
                bidder: "richaudience",
                params: {
                    "pid": ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['richaudience']['pid'],
                    "supplyType": "site"
                }
            });
        }
		    if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['rubicon'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['rubicon']['enable']) {
          bids.push({
            bidder: "rubicon",
            params: {
              "accountId": ddwAdv.cfg.prebid.bids.rubicon.accountId,
              "siteId": ddwAdv.cfg.prebid.bids.rubicon.siteId,
              "zoneId": ddwAdv.cfg.prebid.bids.rubicon.zoneId,
              "position": ddwAdv.cfg.adunit_specs[key]['position']
            }
          });
        }
        if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['seedtag'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['seedtag']['enable']) {
          bids.push({
            bidder: "seedtag",
            params: {
              publisherId: ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['seedtag']['publisherId'],
              adUnitId: ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['seedtag']['adUnitId'],
              placement: ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['seedtag']['placement']
            }
          });
        }
		    if (ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['teads'] && ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['teads']['enable']) {
          bids.push({
            bidder: "teads",
            params: {
              placementId: ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['teads']['placementId'],
              pageId: ddwAdv.cfg.adunit_specs[key]['prebid']['bids']['teads']['pageId']
            }
          });
        }

        ddwAdv.cfg.prebid.slots.push({
          code: ddwAdv.cfg.adunit_specs[key]['div'],
          mediaTypes: {
            banner: {
              pos: ddwAdv.cfg.adunit_specs[key]['pos'],
              sizes: ddwAdv.cfg.adunit_specs[key]['sizes']
            }
          },
          bids: bids
        });
      }
    });
  },
  // NEW
	initGam: function() {
    console.log('[DDW] initGAM');
    window.googletag = window.googletag || {
      cmd: []
    };
	    googletag.cmd.push(function() {
	      Object.keys(ddwAdv.cfg.adunit_specs).forEach(function(key) {
	        googletag.defineSlot(
	          ddwAdv.cfg.adunit_specs[key]['path'],
	          ddwAdv.cfg.adunit_specs[key]['gtag_sizes'],
	          ddwAdv.cfg.adunit_specs[key]['div']
	        ).addService(
	          googletag.pubads()
	        ).setTargeting(
	          "pos", ddwAdv.cfg.adunit_specs[key]['name']
	        ).setTargeting(
	          "area", ddwAdv.cfg.adunit_specs[key]['position']
	        ).setCollapseEmptyDiv(ddwAdv.cfg.adunit_specs[key]['collapseEmpty'],ddwAdv.cfg.adunit_specs[key]['collapseEmpty']);
	      });
	      googletag.pubads().setTargeting("regione", ddwAdv.cfg.gam.regione);
	      googletag.pubads().setTargeting("sito", ddwAdv.cfg.gam.site);
	      googletag.pubads().setTargeting("pagina", ddwAdv.cfg.cur_page.page_type);
	      googletag.pubads().setTargeting("refresh", window.location.search.includes('refresh_ce') ? 'si' : 'no');
		    googletag.pubads().setTargeting("cmp_consent", ddwAdv.cfg.cmp_consent.status);

	      googletag.pubads().collapseEmptyDivs();
	      googletag.pubads().disableInitialLoad();
	      googletag.pubads().setCentering(true);
	      googletag.pubads().enableSingleRequest();

	      googletag.enableServices();
	    });
	},
  initPrebid: function() {
    console.log('[DDW] initPrebid');
    if(!ddwAdv.cfg.prebid.enable) return;

		pbjs.bidderSettings = {
			standard: {storageAllowed:true},
		    appnexus: {bidCpmAdjustment: function(bidCpm, bid) {return bidCpm * 0.97;}},
			  criteo: {bidCpmAdjustment: function(bidCpm, bid) {return bidCpm;}},
		    ogury: {bidCpmAdjustment: function(bidCpm, bid) {return bidCpm * 0.9;}},
        onetag: {bidCpmAdjustment: function(bidCpm, bid) {return bidCpm * 0.9;}},
		    rubicon: {bidCpmAdjustment: function(bidCpm, bid) {return bidCpm;}},
		    seedtag: {bidCpmAdjustment: function(bidCpm, bid) {return bidCpm * 0.9;}},
		    teads: {bidCpmAdjustment: function(bidCpm, bid) {return bidCpm * 0.97;}}
		};

    pbjs.que.push(function() {
	    pbjs.setConfig({
			  enableTIDs:true,
			  enableSendAllBids: true,
	      useBidCache: true,
	      bidderTimeout: ddwAdv.cfg.timeout.bidder,
	      priceGranularity: ddwAdv.cfg.prebid.priceGranularity,
			  targetingControls: {allowTargetingKeys: ['BIDDER', 'AD_ID', 'PRICE_BUCKET', 'SIZE', 'DEAL', 'SOURCE', 'FORMAT', 'UUID', 'CACHE_ID', 'CACHE_HOST', 'ADOMAIN']},
			  pageUrl: window.location.href,
        gptPreAuction: {
    			enabled: false, // enabled by default
   				useDefaultPreAuction: false
    		},
        paapi: {
          enabled: false,
          defaultForSlots: 1
    		},
        currency: {adServerCurrency: 'EUR', conversionRateFile: 'https://www.diariodelwebsrl.it/_adv/currency/latest.json', defaultRates:{USD:{EUR:0.9262}}},
	      rubicon: {singleRequest: true, netRevenue: true},
			  criteo: {fastBidVersion: 'none'},
			  consentManagement: {gdpr: {cmpApi: 'iab', timeout: 8000, defaultGdprScope: true}},
	      schain: ddwAdv.cfg.prebid.schain,
	      userSync: ddwAdv.cfg.prebid.userSync
	    });
	    pbjs.addAdUnits(ddwAdv.cfg.prebid.slots);
		});
	},
	// 2025/04/30
	sendGamRequest: function() {
		console.log('[DDW] Send GAM');
		if (ddwAdv.cfg.ad_request_sent.gam) {
		  	return;
	    }
	    ddwAdv.cfg.ad_request_sent.gam = true;

	    googletag.cmd.push(function() {
	      googletag.pubads().refresh();
	    });

		let interStyle = 'si';

		const slotData = {
			'div-gpt-ad-INTER': {winner: null, bidder: null, cpm: null},
			'div-gpt-ad-FLOORAD': {winner: null, bidder: null, cpm: null}
		};

		function handleSlotRenderEnded(event) {
			const slotId = event.slot.getSlotElementId();
			const advertiserId = event.advertiserId;

			console.log('[DDW] Render ended slotId:', slotId);

			if (!(slotId in slotData)) return;

			const data = slotData[slotId];

			if (event.isEmpty) {
				data.winner = data.bidder = data.cpm = false;
			} else {
				switch (advertiserId) {
				case 18517747: // Rubicon
					data.winner = 'rubicon';
					data.bidder = data.cpm = false;
					if (slotId === 'div-gpt-ad-INTER') interStyle = 'no';
					break;

				case 4835358924: // Prebid
					data.winner = 'prebid';
					const bids = pbjs.getHighestCpmBids([slotId]);
					if (bids && bids.length > 0) {
						data.bidder = bids[0].bidder;
						data.cpm = bids[0].cpm;
					}
					break;

				default:
					data.winner = data.bidder = data.cpm = false;
					break;
				}
			}

			console.log(`[DDW] ${slotId} advId:`, advertiserId);
			console.log(`[DDW] ${slotId} winner:`, data.winner);
			console.log(`[DDW] ${slotId} bidder:`, data.bidder);
			console.log(`[DDW] ${slotId} cpm:`, data.cpm);

			if (slotId === 'div-gpt-ad-INTER') {
				if (interStyle === 'si' && typeof ddwAdv?.activateInterstitial === 'function') {
					ddwAdv.activateInterstitial();
				}
				if (typeof aggiornaUltimaVisita === 'function') {
					aggiornaUltimaVisita();
				}
			}

			if (slotId === 'div-gpt-ad-FLOORAD' && !event.isEmpty) {
				const blockedBidders = ['seedtag', 'ogury'];
				const isBlocked = data.winner === 'prebid' && blockedBidders.includes(data.bidder);

				if (!isBlocked && typeof ddwAdv?.activateStyleFloorAd === 'function') {
					ddwAdv.activateStyleFloorAd('#div-gpt-ad-FLOORAD');
					console.log('[DDW] Activate Style FloorAd');
				} else {
					console.log(`[DDW] Style FloorAd skipped (Prebid / ${data.bidder})`);
				}
			}
		}

		googletag.cmd.push(function () {
			googletag.pubads().addEventListener('slotRenderEnded', handleSlotRenderEnded);
		});

	},
	// 2025/04/30
	sendPrebidRequest: function () {
		if (!ddwAdv.cfg.prebid.enable) {
			ddwAdv.cfg.ad_request_sent.prebid = true;
			return Promise.resolve();
		}

		return new Promise(function (resolve, reject) {
			if (typeof pbjs === 'undefined') {
			console.error('[DDW] pbjs non definito, salto Prebid');
			ddwAdv.cfg.ad_request_sent.prebid = true;
			return resolve();
			}

			try {
			console.log('[DDW] Invio richiesta Prebid');
			pbjs.requestBids({
				bidsBackHandler: function () {
				console.log('[DDW] Risposte ricevute da Prebid');
				pbjs.setTargetingForGPTAsync();
				ddwAdv.cfg.ad_request_sent.prebid = true;
				resolve();
				}
			});
			} catch (e) {
			console.error('[DDW] Errore durante requestBids Prebid:', e);
			ddwAdv.cfg.ad_request_sent.prebid = true;
			resolve(); // fallback: non bloccare il flusso
			}
		});
	},
	// 2025/04/30
	sendParallelBidsRequests: function () {
		var prebidPromise = ddwAdv.sendPrebidRequest();

		var timer = setTimeout(function () {
			console.warn('[DDW] Timeout Prebid raggiunto, invio fallback GAM');
			ddwAdv.sendGamRequest();
		}, ddwAdv.cfg.timeout.failsafe);

		Promise.all([prebidPromise])
			.then(function () {
			clearTimeout(timer);
			console.log('[DDW] Tutti i bidding completati, invio GAM');
			ddwAdv.sendGamRequest();
		})
		.catch(function (error) {
			console.error('[DDW] Errore durante le richieste di bidding:', error);
			ddwAdv.sendGamRequest();
		});
	},
	activateAds: function() {
		ddwAdv.sendParallelBidsRequests();
    },
  	// 2025/04/30
	closeInterstitial: function() {
		const interstitial = document.getElementById('div-gpt-ad-INTER');
		if (interstitial) {
			interstitial.style.display = 'none';
			interstitial.classList.remove('interstitial-active');

			const closer = document.getElementById('div-gpt-ad-INTER-Closer');
			if (closer) closer.remove();

			const background = document.getElementById('div-gpt-ad-INTER-Bkg');
			if (background) background.remove();
		}
	},
	activateInterstitial: function() {
		const eleInter = document.getElementById('div-gpt-ad-INTER');
		if (!eleInter) return;

		// Crea il background
		const eleInterBg = document.createElement('div');
		eleInterBg.id = 'div-gpt-ad-INTER-Bkg';
		eleInterBg.classList.add('interstitial-background');
		document.body.appendChild(eleInterBg);

		// Attiva l'interstitial
		eleInter.classList.add('interstitial-active');

		// Crea pulsante chiusura
		const eleInterClose = document.createElement('a');
		eleInterClose.id = 'div-gpt-ad-INTER-Closer';
		eleInterClose.classList.add('interstitial-closer');
		document.body.appendChild(eleInterClose);

		// Countdown chiusura
		let countdown_s = 3;

		const closerCountdown = function() {
			countdown_s--;
			if (countdown_s > 0) {
			eleInterClose.textContent = countdown_s;
			setTimeout(closerCountdown, 1000);
			} else {
			eleInterClose.textContent = 'X';
			eleInterClose.onclick = ddwAdv.closeInterstitial;
			setTimeout(ddwAdv.closeInterstitial, 10000);
			}
		};

		eleInterClose.textContent = countdown_s;
		setTimeout(closerCountdown, 1000);
	},

	// 2025/04/30
	activateStyleFloorAd: function(flooradId) {
		const el = document.querySelector(flooradId);
		if (!el) return;

		el.classList.add('floorad-active');

		if (!el.querySelector('.floorad-closer')) {
			const elCloser = document.createElement('a');
			elCloser.textContent = 'X';
			elCloser.classList.add('floorad-closer');

			elCloser.addEventListener('click', () => {
			el.classList.remove('floorad-active');
			elCloser.remove();
			});

			el.appendChild(elCloser);
		}
	},  

  run: function() {
    ddwAdv.initCfg();
    ddwAdv.initGam();
    ddwAdv.initPrebid();
	ddwAdv.activateAds();
  }
};
