function initFAQ10() {

  let toggles = document.querySelectorAll('.faq-tab-toggle-template--23397892718897__ss_faq_10_YNcfDD');
  let tabs = document.querySelectorAll('.faq-tab-template--23397892718897__ss_faq_10_YNcfDD');

  if(toggles) {
    toggles.forEach(el => {
      el.addEventListener('click', () => {
        toggles.forEach(element => {
          element.classList.remove('active');
        });

        el.classList.add('active');

        if(tabs) {
          tabs.forEach(element => {
            let dataId = element.getAttribute('data-id');

            if(el.id == dataId) {
              element.classList.add('active');
            } else {
              element.classList.remove('active');
            }
          })
        }
      })
    })
  }

  const answerPaddingTop = 24;
  const answerPaddingBottom = 0;

  const faqs = document.querySelectorAll(
    '.faq-item-template--23397892718897__ss_faq_10_YNcfDD'
  );
  const buttons = document.querySelectorAll(
    '.faq-toggle-template--23397892718897__ss_faq_10_YNcfDD'
  );
  const answers = document.querySelectorAll(
    '.faq-answer-template--23397892718897__ss_faq_10_YNcfDD'
  );
  const answersHeightCalElements = document.querySelectorAll(
    '.faq-height-cal-template--23397892718897__ss_faq_10_YNcfDD'
  );
  const icons = document.querySelectorAll(
    '.faq-arrow-template--23397892718897__ss_faq_10_YNcfDD'
  );

  let activeIndex = -1;

  function openAnswer(index) {
    console.log( answersHeightCalElements[index]);
    const height = answersHeightCalElements[index].offsetHeight;

    answers[index].style.display = 'block';
    setTimeout(() => {
      answers[index].style.paddingTop = `${answerPaddingTop}px`;
      answers[index].style.paddingBottom = `${answerPaddingBottom}px`;
      answers[index].style.height = `${height }px`;
      icons[index].style.setProperty('--rotate', '180deg');
    });
  }
  function closeAnswer(index) {
    answers[index].style.paddingTop = 0;
    answers[index].style.paddingBottom = 0;
    answers[index].style.height = 0;
    icons[index].style.setProperty('--rotate', '0deg');

    setTimeout(() => {
      answers[index].style.display = 'none';
    }, 300);
  }
  function toggleAnswer(index) {
    if (answers[index].style.display === 'none') {
      openAnswer(index);
      activeIndex = index;
    } else {
      closeAnswer(index);
      activeIndex = -1;
    }
  }
  function closeAllAnswerBut(index) {
    answers.forEach((answer, i) => {
      if (i !== index) {
        closeAnswer(i);
      }
    });
  }

  // When clicked, toggle the answer, animate the answer's height:
  buttons.forEach((button, index) => {
    button.addEventListener('click', () => {
      toggleAnswer(index);
      closeAllAnswerBut(index);
    });
  });

  // When the window is resized, debounce the answer's height:
  let resizeTimer;
  window.addEventListener('resize', () => {
    clearTimeout(resizeTimer);
    resizeTimer = setTimeout(() => {
      if (activeIndex !== -1) {
        const height = answersHeightCalElements[activeIndex].offsetHeight;
        answers[activeIndex].style.height = `${height}px`;
      }
    }, 250);
  });
}

document.addEventListener('DOMContentLoaded', initFAQ10);

if (Shopify.designMode) {
   document.addEventListener('shopify:section:unload', initFAQ10);
   document.addEventListener('shopify:section:load', initFAQ10);
}

