var mi_version = '9.5.3';
var mi_track_user = true;
var mi_no_track_reason = '';
				var MonsterInsightsDefaultLocations = {"page_location":"https:\/\/expatliving.sg\/"};
if ( typeof MonsterInsightsPrivacyGuardFilter === 'function' ) {
	var MonsterInsightsLocations = (typeof MonsterInsightsExcludeQuery === 'object') ? MonsterInsightsPrivacyGuardFilter( MonsterInsightsExcludeQuery ) : MonsterInsightsPrivacyGuardFilter( MonsterInsightsDefaultLocations );
} else {
	var MonsterInsightsLocations = (typeof MonsterInsightsExcludeQuery === 'object') ? MonsterInsightsExcludeQuery : MonsterInsightsDefaultLocations;
}

				var disableStrs = [
						'ga-disable-G-50XPB4L2VR',
					];

/* Function to detect opted out users */
function __gtagTrackerIsOptedOut() {
	for (var index = 0; index < disableStrs.length; index++) {
		if (document.cookie.indexOf(disableStrs[index] + '=true') > -1) {
			return true;
		}
	}

	return false;
}

/* Disable tracking if the opt-out cookie exists. */
if (__gtagTrackerIsOptedOut()) {
	for (var index = 0; index < disableStrs.length; index++) {
		window[disableStrs[index]] = true;
	}
}

/* Opt-out function */
function __gtagTrackerOptout() {
	for (var index = 0; index < disableStrs.length; index++) {
		document.cookie = disableStrs[index] + '=true; expires=Thu, 31 Dec 2099 23:59:59 UTC; path=/';
		window[disableStrs[index]] = true;
	}
}

if ('undefined' === typeof gaOptout) {
	function gaOptout() {
		__gtagTrackerOptout();
	}
}
				window.dataLayer = window.dataLayer || [];

window.MonsterInsightsDualTracker = {
	helpers: {},
	trackers: {},
};
if (mi_track_user) {
	function __gtagDataLayer() {
		dataLayer.push(arguments);
	}

	function __gtagTracker(type, name, parameters) {
		if (!parameters) {
			parameters = {};
		}

		if (parameters.send_to) {
			__gtagDataLayer.apply(null, arguments);
			return;
		}

		if (type === 'event') {
										parameters.send_to = monsterinsights_frontend.v4_id;
			var hookName = name;
			if (typeof parameters['event_category'] !== 'undefined') {
				hookName = parameters['event_category'] + ':' + name;
			}

			if (typeof MonsterInsightsDualTracker.trackers[hookName] !== 'undefined') {
				MonsterInsightsDualTracker.trackers[hookName](parameters);
			} else {
				__gtagDataLayer('event', name, parameters);
			}

		} else {
			__gtagDataLayer.apply(null, arguments);
		}
	}

	__gtagTracker('js', new Date());
	__gtagTracker('set', {
		'developer_id.dZGIzZG': true,
							});
	if ( MonsterInsightsLocations.page_location ) {
		__gtagTracker('set', MonsterInsightsLocations);
	}
						__gtagTracker('config', 'G-50XPB4L2VR', {"forceSSL":"true","anonymize_ip":"true","link_attribution":"true","logged_in":"false","tags":"untagged","focus_keyword":"Expat Living","seo_score":"44","page_path":location.pathname + location.search + location.hash} );
								__gtagTracker( 'config', 'AW-920340318', {"allow_enhanced_conversions":true} );
	window.gtag = __gtagTracker;										(function () {
		/* https://developers.google.com/analytics/devguides/collection/analyticsjs/ */
		/* ga and __gaTracker compatibility shim. */
		var noopfn = function () {
			return null;
		};
		var newtracker = function () {
			return new Tracker();
		};
		var Tracker = function () {
			return null;
		};
		var p = Tracker.prototype;
		p.get = noopfn;
		p.set = noopfn;
		p.send = function () {
			var args = Array.prototype.slice.call(arguments);
			args.unshift('send');
			__gaTracker.apply(null, args);
		};
		var __gaTracker = function () {
			var len = arguments.length;
			if (len === 0) {
				return;
			}
			var f = arguments[len - 1];
			if (typeof f !== 'object' || f === null || typeof f.hitCallback !== 'function') {
				if ('send' === arguments[0]) {
					var hitConverted, hitObject = false, action;
					if ('event' === arguments[1]) {
						if ('undefined' !== typeof arguments[3]) {
							hitObject = {
								'eventAction': arguments[3],
								'eventCategory': arguments[2],
								'eventLabel': arguments[4],
								'value': arguments[5] ? arguments[5] : 1,
							}
						}
					}
					if ('pageview' === arguments[1]) {
						if ('undefined' !== typeof arguments[2]) {
							hitObject = {
								'eventAction': 'page_view',
								'page_path': arguments[2],
							}
						}
					}
					if (typeof arguments[2] === 'object') {
						hitObject = arguments[2];
					}
					if (typeof arguments[5] === 'object') {
						Object.assign(hitObject, arguments[5]);
					}
					if ('undefined' !== typeof arguments[1].hitType) {
						hitObject = arguments[1];
						if ('pageview' === hitObject.hitType) {
							hitObject.eventAction = 'page_view';
						}
					}
					if (hitObject) {
						action = 'timing' === arguments[1].hitType ? 'timing_complete' : hitObject.eventAction;
						hitConverted = mapArgs(hitObject);
						__gtagTracker('event', action, hitConverted);
					}
				}
				return;
			}

			function mapArgs(args) {
				var arg, hit = {};
				var gaMap = {
					'eventCategory': 'event_category',
					'eventAction': 'event_action',
					'eventLabel': 'event_label',
					'eventValue': 'event_value',
					'nonInteraction': 'non_interaction',
					'timingCategory': 'event_category',
					'timingVar': 'name',
					'timingValue': 'value',
					'timingLabel': 'event_label',
					'page': 'page_path',
					'location': 'page_location',
					'title': 'page_title',
					'referrer' : 'page_referrer',
				};
				for (arg in args) {
														if (!(!args.hasOwnProperty(arg) || !gaMap.hasOwnProperty(arg))) {
						hit[gaMap[arg]] = args[arg];
					} else {
						hit[arg] = args[arg];
					}
				}
				return hit;
			}

			try {
				f.hitCallback();
			} catch (ex) {
			}
		};
		__gaTracker.create = newtracker;
		__gaTracker.getByName = newtracker;
		__gaTracker.getAll = function () {
			return [];
		};
		__gaTracker.remove = noopfn;
		__gaTracker.loaded = true;
		window['__gaTracker'] = __gaTracker;
	})();
					} else {
						console.log("");
	(function () {
		function __gtagTracker() {
			return null;
		}

		window['__gtagTracker'] = __gtagTracker;
		window['gtag'] = __gtagTracker;
	})();
					}

if (window.hasOwnProperty('MonsterInsightsDualTracker')){
	window.MonsterInsightsDualTracker.trackers['form:impression'] = function (parameters) {
		__gtagDataLayer('event', 'form_impression', {
			form_id: parameters.event_label,
			content_type: 'form',
			non_interaction: true,
			send_to: parameters.send_to,
		});
	};

	window.MonsterInsightsDualTracker.trackers['form:conversion'] = function (parameters) {
		__gtagDataLayer('event', 'generate_lead', {
			form_id: parameters.event_label,
			send_to: parameters.send_to,
		});
	};
}

//<![CDATA[
  function external_links_in_new_windows_loop() {
    if (!document.links) {
      document.links = document.getElementsByTagName('a');
    }
    var change_link = false;
    var force = '';
    var ignore = '';

    for (var t=0; t<document.links.length; t++) {
      var all_links = document.links[t];
      change_link = false;

      if(document.links[t].hasAttribute('onClick') == false) {
        // forced if the address starts with http (or also https), but does not link to the current domain
        if(all_links.href.search(/^http/) != -1 && all_links.href.search('expatliving.sg') == -1 && all_links.href.search(/^#/) == -1) {
          // console.log('Changed ' + all_links.href);
          change_link = true;
        }

        if(force != '' && all_links.href.search(force) != -1) {
          // forced
          // console.log('force ' + all_links.href);
          change_link = true;
        }

        if(ignore != '' && all_links.href.search(ignore) != -1) {
          // console.log('ignore ' + all_links.href);
          // ignored
          change_link = false;
        }

        if(change_link == true) {
          // console.log('Changed ' + all_links.href);
          document.links[t].setAttribute('onClick', 'javascript:window.open(\'' + all_links.href.replace(/'/g, '') + '\', \'_blank\', \'noopener\'); return false;');
          document.links[t].removeAttribute('target');
        }
      }
    }
  }

  // Load
  function external_links_in_new_windows_load(func)
  {  
    var oldonload = window.onload;
    if (typeof window.onload != 'function'){
      window.onload = func;
    } else {
      window.onload = function(){
        oldonload();
        func();
      }
    }
  }

  external_links_in_new_windows_load(external_links_in_new_windows_loop);
  //]]>

window.addEventListener('DOMContentLoaded', function() {
			function monsterinsights_forms_record_impression(event) {
				monsterinsights_add_bloom_forms_ids();
				var monsterinsights_forms = document.getElementsByTagName("form");
				var monsterinsights_forms_i;
				for (monsterinsights_forms_i = 0; monsterinsights_forms_i < monsterinsights_forms.length; monsterinsights_forms_i++) {
					var monsterinsights_form_id = monsterinsights_forms[monsterinsights_forms_i].getAttribute("id");
					var skip_conversion = false;
					/* Check to see if it's contact form 7 if the id isn't set */
					if (!monsterinsights_form_id) {
						monsterinsights_form_id = monsterinsights_forms[monsterinsights_forms_i].parentElement.getAttribute("id");
						if (monsterinsights_form_id && monsterinsights_form_id.lastIndexOf('wpcf7-f', 0) === 0) {
							/* If so, let's grab that and set it to be the form's ID*/
							var tokens = monsterinsights_form_id.split('-').slice(0, 2);
							var result = tokens.join('-');
							monsterinsights_forms[monsterinsights_forms_i].setAttribute("id", result);/* Now we can do just what we did above */
							monsterinsights_form_id = monsterinsights_forms[monsterinsights_forms_i].getAttribute("id");
						} else {
							monsterinsights_form_id = false;
						}
					}

					/* Check if it's Ninja Forms & id isn't set. */
					if (!monsterinsights_form_id && monsterinsights_forms[monsterinsights_forms_i].parentElement.className.indexOf('nf-form-layout') >= 0) {
						monsterinsights_form_id = monsterinsights_forms[monsterinsights_forms_i].parentElement.parentElement.parentElement.getAttribute('id');
						if (monsterinsights_form_id && 0 === monsterinsights_form_id.lastIndexOf('nf-form-', 0)) {
							/* If so, let's grab that and set it to be the form's ID*/
							tokens = monsterinsights_form_id.split('-').slice(0, 3);
							result = tokens.join('-');
							monsterinsights_forms[monsterinsights_forms_i].setAttribute('id', result);
							/* Now we can do just what we did above */
							monsterinsights_form_id = monsterinsights_forms[monsterinsights_forms_i].getAttribute('id');
							skip_conversion = true;
						}
					}

					if (monsterinsights_form_id && monsterinsights_form_id !== 'commentform' && monsterinsights_form_id !== 'adminbar-search') {
						__gtagTracker('event', 'impression', {
							event_category: 'form',
							event_label: monsterinsights_form_id,
							value: 1,
							non_interaction: true
						});

						/* If a WPForms Form, we can use custom tracking */
						if (monsterinsights_form_id && 0 === monsterinsights_form_id.lastIndexOf('wpforms-form-', 0)) {
							continue;
						}

						/* Formiddable Forms, use custom tracking */
						if (monsterinsights_forms_has_class(monsterinsights_forms[monsterinsights_forms_i], 'frm-show-form')) {
							continue;
						}

						/* If a Gravity Form, we can use custom tracking */
						if (monsterinsights_form_id && 0 === monsterinsights_form_id.lastIndexOf('gform_', 0)) {
							continue;
						}

						/* If Ninja forms, we use custom conversion tracking */
						if (skip_conversion) {
							continue;
						}

						var custom_conversion_mi_forms = false;
						if (custom_conversion_mi_forms) {
							continue;
						}

						if (window.jQuery) {
							(function (form_id) {
								jQuery(document).ready(function () {
									jQuery('#' + form_id).on('submit', monsterinsights_forms_record_conversion);
								});
							})(monsterinsights_form_id);
						} else {
							var __gaFormsTrackerWindow = window;
							if (__gaFormsTrackerWindow.addEventListener) {
								document.getElementById(monsterinsights_form_id).addEventListener("submit", monsterinsights_forms_record_conversion, false);
							} else {
								if (__gaFormsTrackerWindow.attachEvent) {
									document.getElementById(monsterinsights_form_id).attachEvent("onsubmit", monsterinsights_forms_record_conversion);
								}
							}
						}

					} else {
						continue;
					}
				}
			}

			function monsterinsights_forms_has_class(element, className) {
				return (' ' + element.className + ' ').indexOf(' ' + className + ' ') > -1;
			}

			function monsterinsights_forms_record_conversion(event) {
				var monsterinsights_form_conversion_id = event.target.id;
				var monsterinsights_form_action = event.target.getAttribute("miforms-action");
				if (monsterinsights_form_conversion_id && !monsterinsights_form_action) {
					document.getElementById(monsterinsights_form_conversion_id).setAttribute("miforms-action", "submitted");
					__gtagTracker('event', 'conversion', {
						event_category: 'form',
						event_label: monsterinsights_form_conversion_id,
						value: 1,
					});
				}
			}

			/* Attach the events to all clicks in the document after page and GA has loaded */
			function monsterinsights_forms_load() {
				if (typeof (__gtagTracker) !== 'undefined' && __gtagTracker) {
					var __gtagFormsTrackerWindow = window;
					if (__gtagFormsTrackerWindow.addEventListener) {
						__gtagFormsTrackerWindow.addEventListener("load", monsterinsights_forms_record_impression, false);
					} else {
						if (__gtagFormsTrackerWindow.attachEvent) {
							__gtagFormsTrackerWindow.attachEvent("onload", monsterinsights_forms_record_impression);
						}
					}
				} else {
					setTimeout(monsterinsights_forms_load, 200);
				}
			}

			/* Custom Ninja Forms impression tracking */
			if (window.jQuery) {
				jQuery(document).on('nfFormReady', function (e, layoutView) {
					var label = layoutView.el;
					label = label.substring(1, label.length);
					label = label.split('-').slice(0, 3).join('-');
					__gtagTracker('event', 'impression', {
						event_category: 'form',
						event_label: label,
						value: 1,
						non_interaction: true
					});
				});
			}

			/* Custom Bloom Form tracker */
			function monsterinsights_add_bloom_forms_ids() {
				var bloom_forms = document.querySelectorAll('.et_bloom_form_content form');
				if (bloom_forms.length > 0) {
					for (var i = 0; i < bloom_forms.length; i++) {
						if ('' === bloom_forms[i].id) {
							var form_parent_root = monsterinsights_find_parent_with_class(bloom_forms[i], 'et_bloom_optin');
							if (form_parent_root) {
								var classes = form_parent_root.className.split(' ');
								for (var j = 0; j < classes.length; ++j) {
									if (0 === classes[j].indexOf('et_bloom_optin')) {
										bloom_forms[i].id = classes[j];
									}
								}
							}
						}
					}
				}
			}

			function monsterinsights_find_parent_with_class(element, className) {
				if (element.parentNode && '' !== className) {
					if (element.parentNode.className.indexOf(className) >= 0) {
						return element.parentNode;
					} else {
						return monsterinsights_find_parent_with_class(element.parentNode, className);
					}
				}
				return false;
			}

			monsterinsights_forms_load();
		});

			var monsterinsights_tracked_video_marks = {};
			var monsterinsights_youtube_percentage_tracking_timeouts = {};

			/* Works for YouTube and Vimeo */
			function monsterinsights_media_get_id_for_iframe( source, service ) {
				var iframeUrlParts = source.split('?');
				var stripedUrl = iframeUrlParts[0].split('/');
				var videoId = stripedUrl[ stripedUrl.length - 1 ];

				return service + '-player-' + videoId;
			}

			function monsterinsights_media_record_video_event( provider, event, label, parameters = {} ) {
				__gtagTracker('event', event, {
					event_category: 'video-' + provider,
					event_label: label,
					non_interaction: event === 'impression',
					...parameters
				});
			}

			function monsterinsights_media_maybe_record_video_progress( provider, label, videoId, videoParameters ) {
				var progressTrackingAllowedMarks = [10,25,50,75];

				if ( typeof monsterinsights_tracked_video_marks[ videoId ] == 'undefined' ) {
					monsterinsights_tracked_video_marks[ videoId ] = [];
				}

				var { video_percent } = videoParameters;

				if ( progressTrackingAllowedMarks.includes( video_percent ) && !monsterinsights_tracked_video_marks[ videoId ].includes( video_percent ) ) {
					monsterinsights_media_record_video_event( provider, 'video_progress', label, videoParameters );

					/* Prevent multiple records for the same percentage */
					monsterinsights_tracked_video_marks[ videoId ].push( video_percent );
				}
			}

			/* --- Vimeo --- */
            var monsterinsights_media_vimeo_plays = {};

            function monsterinsights_setup_vimeo_events_for_iframe(iframe, title, player) {
                var playerId = iframe.getAttribute('id');
                var videoLabel = title || iframe.title || iframe.getAttribute('src');

                if ( !playerId ) {
                    playerId = monsterinsights_media_get_id_for_iframe( iframe.getAttribute('src'), 'vimeo' );
                    iframe.setAttribute( 'id', playerId );
                }

                monsterinsights_media_vimeo_plays[playerId] = 0;

                var videoParameters = {
                    video_provider: 'vimeo',
                    video_title: title,
                    video_url: iframe.getAttribute('src')
                };

                /**
                 * Record Impression
                 **/
                monsterinsights_media_record_video_event( 'vimeo', 'impression', videoLabel, videoParameters );

                /**
                 * Record video start
                 **/
                player.on('play', function(data) {
                    let playerId = this.element.id;
                    if ( monsterinsights_media_vimeo_plays[playerId] === 0 ) {
                        monsterinsights_media_vimeo_plays[playerId]++;

                        videoParameters.video_duration = data.duration;
                        videoParameters.video_current_time = data.seconds;
                        videoParameters.video_percent = 0;

                        monsterinsights_media_record_video_event( 'vimeo', 'video_start', videoLabel, videoParameters );
                    }
                });

                /**
                 * Record video progress
                 **/
                player.on('timeupdate', function(data) {
                    var progress = Math.floor(data.percent * 100);

                    videoParameters.video_duration = data.duration;
                    videoParameters.video_current_time = data.seconds;
                    videoParameters.video_percent = progress;

                    monsterinsights_media_maybe_record_video_progress( 'vimeo', videoLabel, playerId, videoParameters );
                });

                /**
                 * Record video complete
                 **/
                player.on('ended', function(data) {
                    videoParameters.video_duration = data.duration;
                    videoParameters.video_current_time = data.seconds;
                    videoParameters.video_percent = 100;

                    monsterinsights_media_record_video_event( 'vimeo', 'video_complete', videoLabel, videoParameters );
                });
            }

			function monsterinsights_on_vimeo_load() {

				var vimeoIframes = document.querySelectorAll("iframe[src*='vimeo']");

				vimeoIframes.forEach(function( iframe ) {
                    //  Set up the player
					var player = new Vimeo.Player(iframe);

                    //  The getVideoTitle function returns a promise
                    player.getVideoTitle().then(function(title) {
                        /*
                         * Binding the events inside this callback guarantees that we
                         * always have the correct title for the video
                         */
                        monsterinsights_setup_vimeo_events_for_iframe(iframe, title, player)
                    });
				});
			}

			function monsterinsights_media_init_vimeo_events() {
				var vimeoIframes = document.querySelectorAll("iframe[src*='vimeo']");

				if ( vimeoIframes.length ) {

					/* Maybe load Vimeo API */
					if ( window.Vimeo === undefined ) {
						var tag = document.createElement("script");
						tag.src = "https://player.vimeo.com/api/player.js";
						tag.setAttribute("onload", "monsterinsights_on_vimeo_load()");
						document.body.append(tag);
					} else {
						/* Vimeo API already loaded, invoke callback */
						monsterinsights_on_vimeo_load();
					}
				}
			}

			/* --- End Vimeo --- */

			/* --- YouTube --- */
			function monsterinsights_media_on_youtube_load() {
				var monsterinsights_media_youtube_plays = {};

				function __onPlayerReady(event) {
					monsterinsights_media_youtube_plays[event.target.h.id] = 0;

					var videoParameters = {
						video_provider: 'youtube',
						video_title: event.target.videoTitle,
						video_url: event.target.playerInfo.videoUrl
					};
					monsterinsights_media_record_video_event( 'youtube', 'impression', videoParameters.video_title, videoParameters );
				}

				/**
				 * Record progress callback
				 **/
				function __track_youtube_video_progress( player, videoLabel, videoParameters ) {
					var { playerInfo } = player;
					var playerId = player.h.id;

					var duration = playerInfo.duration; /* player.getDuration(); */
					var currentTime = playerInfo.currentTime; /* player.getCurrentTime(); */

					var percentage = (currentTime / duration) * 100;
					var progress = Math.floor(percentage);

					videoParameters.video_duration = duration;
					videoParameters.video_current_time = currentTime;
					videoParameters.video_percent = progress;

					monsterinsights_media_maybe_record_video_progress( 'youtube', videoLabel, playerId, videoParameters );
				}

				function __youtube_on_state_change( event ) {
					var state = event.data;
					var player = event.target;
					var { playerInfo } = player;
					var playerId = player.h.id;

					var videoParameters = {
						video_provider: 'youtube',
						video_title: player.videoTitle,
						video_url: playerInfo.videoUrl
					};

					/**
					 * YouTube's API doesn't offer a progress or timeupdate event.
					 * We have to track progress manually by asking the player for the current time, every X milliseconds, using an
    interval
					 **/

					if ( state === YT.PlayerState.PLAYING) {
						if ( monsterinsights_media_youtube_plays[playerId] === 0 ) {
							monsterinsights_media_youtube_plays[playerId]++;
							/**
							 * Record video start
							 **/
							videoParameters.video_duration = playerInfo.duration;
							videoParameters.video_current_time = playerInfo.currentTime;
							videoParameters.video_percent = 0;

							monsterinsights_media_record_video_event( 'youtube', 'video_start', videoParameters.video_title, videoParameters );
						}

						monsterinsights_youtube_percentage_tracking_timeouts[ playerId ] = setInterval(
							__track_youtube_video_progress,
							500,
							player,
							videoParameters.video_title,
							videoParameters
						);
