styles:
      #cookiescript_injected {
    background-color: #1C1C1C;
    z-index: 999997;
    opacity: 1;
    font-size: 14px;
    font-weight: normal;
    font-family: 'Open Sans', Arial, 'Trebuchet MS', 'Segoe UI', 'Helvetica', sans-serif;
    box-shadow: 1px 2px 8px 0 rgba(0, 0, 0, 0.35);
    color: #FFFFFF;
    box-sizing: border-box;
}
.cookiescript_checkbox_label {
    cursor: pointer;
    display: flex;
    align-items: center;
    padding:0 4px;
    line-height: 1.5;
    margin:0;
	text-align: left;
}
.cookiescript_pre_header {
    display: flex;
    justify-content: flex-end;
    align-items: center;
    position: absolute;
    top: 15px;
    right: 15px;
}
#cookiescript_close {
    font-size: 29px;
    line-height: 13px;
    cursor: pointer;
    color: #FFFFFF;
    height: 15px;
    width: 15px;
    margin: 0 0 0 10px;
    padding: 0;
    overflow: hidden;
    letter-spacing: 0;
    font-family: 'Trebuchet MS', 'Arial', sans-serif;
    font-weight: 100;
    opacity: 0.85;
    z-index: 999999;
    position: relative;
    top: -1px;
}

#cookiescript_buttons {
    display: flex;
    flex-direction: row;
    font-weight: 700;
}
#cookiescript_manage_wrap {
    text-transform: uppercase;
    text-align: center;
    font-size: 12px;
    letter-spacing: 0.4px;
    font-weight: 700;
}
#cookiescript_manage {
    display: inline;
    cursor: pointer;
    color: #FFFFFF;
    opacity:0.85;
    margin-left:-15px;
}
#cookiescript_manage #cookiescript_manageicon .cookiescript_gear {
    fill: #FFFFFF;
}
#cookiescript_manage:hover #cookiescript_manageicon .cookiescript_gear {
    fill: #6BBE6B;;
}

svg#cookiescript_manageicon {
    width: 15px;
    height: 15px;
    display: inline;
    margin: 0 5px 0 0;
    padding: 0;
    position: relative;
    top: 3px;
    vertical-align: baseline;
}
#cookiescript_header {
    background-color: transparent;
    z-index: 999998;
    color: #FFFFFF;
    font-size: 18px;
    line-height: 1.3;
    font-weight: 600;
    letter-spacing: 0.4px;
    opacity:1;
}
.cookiescript_checkbox {
    display: flex;
    flex-direction: row;
}
input.cookiescript_checkbox_input {
}
span.cookiescript_checkbox_text {
    display: inline-block;
    font-size: 12px;
    margin: 0;
    text-transform: uppercase;
    font-weight: 600;
    color: #FFFFFF;
    letter-spacing: 1px;
}

#cookiescript_save {
    border: 0;
    transition: all 0.25s ease 0s;
    background-color: #6BBE6B;
    color: #FFFFFF;
    text-transform: uppercase;
    font-size: 12px;
    text-align: center;
    line-height: 3.2;
    letter-spacing: 0.4px;
}
/*IE 9 fixes*/
@media screen and (min-width:0\0) and (min-resolution: .001dpcm) {
    .cookiescript_checkbox_label {
        position: relative;
        top:-10px;
    }
    #cookiescript_accept, #cookiescript_reject, #cookiescript_save{
    	display: inline-block;
    }
    #cookiescript_buttons{
    	text-align:center;
    }
}
#cookiescript_save{
    display: none;
}
#cookiescript_reject {
    border: 1px solid #FFFFFF;
    text-align: center;
    line-height: 3;
    font-size: 12px;
    text-transform: uppercase;
    letter-spacing: 0.4px;
    color: #FFFFFF;
    background: #1C1C1C;
}
#cookiescript_accept {
	transition: all 0.25s ease 0s;
	text-transform: uppercase;
	font-size: 12px;
	text-align: center;
	letter-spacing: 0.4px;
	line-height: 3;
	border: 1px solid #FFFFFF;
	color: #FFFFFF;
	background-color: #1C1C1C;
}

#cookiescript_accept, #cookiescript_reject, #cookiescript_save {
    font-weight: 700;
    cursor: pointer;
    white-space: nowrap;
    transition-duration: 100ms;
    transition-timing-function: ease-in-out;
    min-width: 103px;
}
.cookiescript_bigger {
    transform: scale(1.1);
}
#cookiescript_link {
    text-decoration: none;
    color: #FFFFFF;
    font-size: 11px;
    text-align: center;
    font-weight: 400;
    text-transform: uppercase;
    opacity: 0.8;
	display:inline !important;
}

#cookiescript_readmore,
#cookiescript_reportlink,
#cookiescript_cookiescriptlink {
    border: 0;
    padding: 0;
    cursor: pointer;
    margin: 0;
    transition: all 100ms ease 0s;
    background-color: transparent;
    display: inline;
    font-size: 12px;
    text-decoration: none;
}

#cookiescript_readmore{
    color: #FFFFFF;
}
#cookiescript_reportlink,
#cookiescript_cookiescriptlink {
    color: #FFFFFF;
}


#cookiescript_readmore:hover,
#cookiescript_reportlink:hover,
#cookiescript_cookiescriptlink:hover {
    text-decoration: underline;
    color: #FFFFFF;

}

#cookiescript_description {
    color: #FFFFFF;
    font-size: 12px;
    letter-spacing: 0.3px;
    line-height: 1.65;
    font-weight: 400;
}
#cookiescript_checkboxs {
}
#cookiescript_close:hover,
#cookiescript_manage:hover,
#cookiescript_link:hover
{
    opacity: 1;
}

#cookiescript_reject:hover {
    background-color: #2f2f2f;
}

#cookiescript_accept:hover{
	background-color: #2f2f2f;
}
#cookiescript_save:hover {
    background-color: #63af63;
}

#cookiescript_readmore:hover,
#cookiescript_reportlink:hover,
#cookiescript_cookiescriptlink:hover
{
    color: #63af63;
}

#cookiescript_badge {
    position: fixed;
    line-height: 0;
    cursor: pointer;
    z-index: 99999;
    font-size: 0;
    color: #999;
left: 10px;
    display: flex;
    flex-direction: row;
    opacity: 1;
}

#cookiescript_badgetext{
    text-transform: uppercase;
    font-weight: 600;
    font-family: 'Open Sans', Arial, 'Trebuchet MS', 'Segoe UI', 'Helvetica', sans-serif;
    overflow: hidden;
    transition-duration: 500ms;
    white-space: nowrap;
    padding-right: 0;
    color: #FFFFFF;
}

#cookiescript_badgesvg{
    width:40px;
    height: 40px;
}



#cookiescript_badge {
bottom: 10px;
    border-radius:25px;
    padding:3px;
    transition-duration: 500ms;
    box-shadow: 1px 2px 8px 0 rgba(0, 0, 0, 0.2);
    background: #1C1C1C;
}
#cookiescript_badge:hover #cookiescript_badgetext{
    max-width: 300px;
    padding-right: 15px;
    padding-left: 12px;
}
#cookiescript_badgetext {
    font-size: 16px;
    line-height: 2.5;
    max-width: 0;
}
#cookiescript_badgeimage {
    width: 40px;
    height: 40px;
}
@media only screen and (max-width: 414px) {
    #cookiescript_badgeimage {
        width: 30px;
        height: 30px;
    }
    #cookiescript_badgesvg{
        width:30px;
        height: 30px;
    }
    #cookiescript_badgetext{
        display: none;
    }
}
/*IE 9 fixes*/
@media screen and (min-width:0\0) and (min-resolution: .001dpcm) {
	#cookiescript_badgeimage{
    	float:left;
    }
}

@media print{
    #cookiescript_injected{
        display:none;
    }
}


.cookiescript-iab-tabContent {
    margin: 0 5px 10px 5px;
}

.cookiescript-iab-headerContainer {
    align-items: center;
    display: flex;
    justify-content: space-between;
    margin: 15px 2px 10px 2px;
}

.cookiescript-iab-header {
    text-transform: uppercase;
    font-weight: bold;
    color: #4b4b4b;
}

.cookiescript-iab-headerActions {
    align-items: center;
    display: flex;
}

.cookiescript-iab-headerAction {
    cursor: pointer;
    text-transform: uppercase;
    font-weight: bold;
    color: #4b4b4b;
}

.cookiescript-iab-headerActionSeparator {
    margin: 0 5px;
}

.cookiescript-iab-itemContainer {
    border: 1px solid #CCC;
    padding: 4px 4px 4px 8px;
    margin-bottom: 8px;
    border-radius: 4px;
}

.cookiescript-iab-itemHeader {
    align-items:center;
    display:flex;
    justify-content: space-between;
    color: #4b4b4b;
}

.cookiescript-iab-itemHeaderContent {
    align-items:center;
    display:flex;
    justify-content: space-between;
    width: 100%
}

.cookiescript-iab-itemCheckboxContainerLI {
    min-width: 125px;
    width: 125px;
}

.cookiescript-iab-itemCheckboxContainer {
    align-items:center;
    display:flex;
}

.cookiescript-iab-itemCheckbox {
    margin-right: 8px;
}

.cookiescript-iab-itemLI {
    font-size: 10px;
}

.cookiescript-iab-itemHeaderAction {
    margin: 0 5px 0 5px;
    cursor: pointer;
}

.cookiescript-iab-itemContent {
    margin-top: 10px;
}

.cookiescript-iab-itemContentList {
    list-style-type: disc;
    margin: 3px 0 0;
    padding-inline-start: 18px;
}

.cookiescript-iab-policyUrl {
    margin: 5px 0;
}

.cookiescript-iab-policyLink {
    text-decoration: underline;
    color: #6f6f6f;
}

.cookiescript-iab-itemContentVendorInfo {
    margin-top: 10px;
}


.cookiescript-iab-itemHeaderContent{order:2;}
.cookiescript-iab-itemHeaderAction{order:1;}
.cookiescript-iab-itemHeaderAction {
	width: 18px;
	height: 18px;
	text-align: center;
	order: 1;
	border: 1px solid #4b4b4b;
	border-radius: 5px;
	font-size: 16px;
	line-height: 16px;
	font-weight: normal;
	margin: 0 10px 0 0;
	padding: 0;
	vertical-align: middle;
	color: #4b4b4b;
	font-family: monospace;
    display: flex;
    justify-content: center;
    align-items: center;
}
.cookiescript-iab-itemCheckboxContainerLI{width:auto}
.cookiescript-iab-itemHeader {justify-content: flex-start;}
.cookiescript-iab-itemHeaderText {
	order: 2;
}
.cookiescript-iab-itemCheckboxContainer input.cookiescript-iab-itemCheckbox[type=checkbox]{
	letter-spacing: normal;
	word-spacing: normal;
	text-transform: none;
	text-indent: 0px;
	text-shadow: none;
	display: inline-block;
	text-align: start;
	background-color: initial;
	cursor: default;
	appearance: auto;
	margin: 0 8px 0 0 ;
}

.cookiescript-iab-showButton {
    color: #3fb6ee;
    text-transform: uppercase;
    font-size: 11px;
    font-weight: 700;
    position: relative;
    display: flex;
    align-items: center;
    margin-right: auto;
    margin-top: 10px;
    cursor: pointer;
}

.cookiescript-iab-showButton.cookiescript_hidden {
    margin-top: 0;
}

.cookiescript-iab-vendor-cookie-table {
    border-collapse: collapse;
    width: 100%;
    margin-top: 15px;
}

.cookiescript-iab-vendor-cookie-table th {
    text-align: left;
    padding-bottom: 10px;
}

.cookiescript-iab-vendor-cookie-table td {
    text-align: left;
    padding-right: 10px;
    vertical-align: top;
}

#cookiescript_show_all_partners_button {
    color: #6BBE6B;
    margin-bottom: 12px;
    font-weight: bold;
    text-transform: uppercase;
    cursor: pointer;
    font-size: 14px;
}
#cookiescript_show_all_partners_button:hover {
    color: #63af63;
}

.cookiescript-iab-loader-container {
    margin: 15px 0;
    text-align: center;
}

#cookiescript_injected_fsd {
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(17, 17, 17, 0.5) !important;
    z-index: 999996;
	overflow-y: auto;
}

#cookiescript_fsd_wrapper {
    /*animation: expandBox 2s forwards;*/
    max-width: 950px;
	width: 95%;
    margin: 0 auto 3% auto;
	top: 5%;
    line-height: 18px;
    letter-spacing: normal;
    position: relative;
    display: flex;
    flex-direction: column;
	background-color: #282828;
    z-index: 999997;
    opacity: 1;
    font-size: 14px;
    font-weight: normal;
    font-family: 'Open Sans', Arial, 'Trebuchet MS', 'Segoe UI', 'Helvetica', sans-serif;
    box-shadow: 1px 2px 8px 0 rgba(0, 0, 0, 0.35);
    color: #FFFFFF;
    align-items: center;
    overflow: visible;
    outline: none;
    max-height: 90%;
    box-sizing: border-box;
	cursor: default;
	min-height: 500px;
}
@keyframes expandBox {
    from {
        width: 10%;
        margin-left:0;
        overflow: hidden;
        top:90%;
        height: 5%;
    }
    to {
        width: 95%;
        margin-left:auto;
        overflow: hidden;
        top:5%;
        height: 90%;
    }
}

.cookiescript_fsd_header {
	min-height: 57px;
	border-bottom: 1px solid #383838;
	width: 100%;
	display: flex;
	flex-direction: row;
	align-items: center;
	background-color: #1C1C1C;
}
.cookiescript_fsd_logo_a{
	margin-left: 23px;
    text-decoration: none;
}
.cookiescript_fsd_logo{
	height: 27px;
	width: auto;
	display: block;
	max-width: 100%;
}
.cookiescript_fsd_powered {
    color: #666666;
    font-size: 11px;
    position: relative;
    left: 31px;
    top: 0;
    line-height: 1;
    text-decoration: none;
    display: block;
    height: 8px;
}
.cookiescript_fsd_language_drop_down {
    position: absolute;
    right: 67px;
	top: 19px;
}

.cookiescript_fsd_main {
    width: 100%;
	background-color: #1C1C1C;
}

.cookiescript_fsd_main_info {
    padding: 20px 53px 30px 53px;
    }

.cookiescript_fsd_title {
    background-color: transparent;
    z-index: 999998;
    color: #FFFFFF;
    font-size: 24px;
    line-height: 1.3;
    font-weight: 700;
    opacity:1;
}

.cookiescript_fsd_description {
    color: #FFFFFF;
    font-size: 14px;
    line-height: 1.6;
    font-weight: 400;
    opacity: 0.85;
	margin-top: 9px;
}


#cookiescript_fsd_wrapper #cookiescript_readmore{
	font-size: 13px;
	color: #3fb6ee;
	text-decoration: none;
}
#cookiescript_fsd_wrapper #cookiescript_readmore:hover{
	text-decoration: underline;
}

.cookiescript_fsd_tabs {
    display: flex;
    justify-content: space-around;
    align-items: stretch;
	flex-direction: row;
    width: 100%;
}

.cookiescript_fsd_tabs > div {
	cursor: pointer;
	width: 50%;
	text-transform: uppercase;
	font-size: 16px;
	text-align: center;
	line-height: 1.6;
	font-weight: bold;
	border-top: 2px solid transparent;
	transition: all 300ms ease 0s;
	padding: 10px 20px;
	display: flex;
	justify-content: center;
	align-items: center;
	background-color: #1C1C1C;
}

.cookiescript_fsd_tabs > .cookiescript_active {
    border-top: 2px solid #3fb6ee;
    background-color: #282828;
    color: #3fb6ee;
}

.cookiescript_fsd_tabs_content {
    background-color: #282828;
    padding: 5px 50px 10px 50px;
    width: 100%;
    overflow-y: auto;
    box-sizing: border-box;
    flex-grow: 1;
	--scrollbar-width: 8px;
	--mask-height: 35px;
	--mask-image-content: linear-gradient( to bottom, transparent, black var(--mask-height), black calc(100% - var(--mask-height)), transparent );
	--mask-size-content: calc(100% - var(--scrollbar-width)) 100%;
	--mask-image-scrollbar: linear-gradient(black, black);
	--mask-size-scrollbar: var(--scrollbar-width) 100%;
	mask-image: var(--mask-image-content), var(--mask-image-scrollbar);
	mask-size: var(--mask-size-content), var(--mask-size-scrollbar);
	mask-position: 0 0, 100% 0;
	mask-repeat: no-repeat, no-repeat;
	-webkit-mask-image: var(--mask-image-content), var(--mask-image-scrollbar);
	-webkit-mask-size: var(--mask-size-content), var(--mask-size-scrollbar);
	-webkit-mask-position: 0 0, 100% 0;
	-webkit-mask-repeat: no-repeat, no-repeat;
}

.cookiescript_fsd_tabs_content .cookiescript_hidden {
    opacity: 0;
    height: 0 !important;
    overflow: hidden;
	padding: 0 !important;
	min-height: 0 !important;
}

.cookiescript_fsd_category {
    border-bottom: 1px solid #383838;
    padding-top: 23px;
    padding-bottom: 17px;
    display: flex;
    flex-direction: column;
}

.cookiescript_fsd_category:last-child {
    border-bottom: 0;
}

.cookiescript_fsd_category .cookiescript_hidden {
    opacity: 0;
	overflow-y: auto;
	overflow-x: hidden;
	padding: 0 !important;
	max-height: 0;
	margin: 0;
	height: auto !important;
	}

.cookiescript_fsd_category_main {
    align-items: center;
    display: flex;
    justify-content: space-between;
}

.cookiescript_fsd_category_info {
    flex: 9;
    padding-right: 10px;
}

.cookiescript_fsd_category_control {
    flex: 1;
    display: flex;
    justify-content: center;
}

.cookiescript_fsd__category_name {
    font-size: 16px;
    font-weight: 700;
	margin-bottom: 6px;
}

.cookiescript_category_description {
	font-size: 13px;
	padding-bottom: 5px;
	line-height: 1.6;
}

.cookiescript_fsd_cookies {
	border: 1px solid #383838;
	border-radius: 10px;
	overflow-y: auto;
	margin-top: 15px;
	max-height: 222px;
	}

.cookiescript_fsd_cookies caption {
    font-weight: 600;
    padding: 10px;
    text-align: left;
    color: #FFFFFF;
    caption-side: top;
}


.cookiescript_fsd_cookies_control {
    color: #3fb6ee;
    text-transform: uppercase;
    font-size: 14px;
    font-weight: 700;
    position: relative;
    display: flex;
    align-items: center;
    margin-right: auto;
    margin-top: 10px;
    cursor: pointer;
}

.cookiescript_fsd_cookies_control .cookiescript_hidden {
    display: none;
}

.cookiescript_fsd_cookies_control  span{
	margin-right: 7px;
}

.cookiescript_fsd_cookies_control svg {
    width: 10px;
    height: 10px;
    font-size: 10px;
}

.cookiescript_fsd_cookies_control.active svg {
    width: 10px;
    height: 10px;
    font-size: 10px;
    transform: rotate(180deg);
}

.cookiescript_fsd_cookies_table {
    width: 100%;
}

.cookiescript_fsd_cookies_table,
.cookiescript_fsd_cookies_table tbody,
.cookiescript_fsd_cookies_table thead,
.cookiescript_fsd_cookies_table tr,
.cookiescript_fsd_cookies_table th,
.cookiescript_fsd_cookies_table td {
    margin: 0;
    padding: 0;
    font: inherit;
    vertical-align: baseline;
    background-color: #1C1C1C;
    border-spacing: 0;
    border-collapse: collapse;
}


.cookiescript_fsd_cookies_table tr:last-child td{
    border-bottom: 0;
}

.cookiescript_fsd_cookies_table td, .cookiescript_fsd_cookies_table th {
    white-space: normal;
    font-size: 13px;
    text-align: left;
    margin: 0;
}
.cookiescript_fsd_cookies_table th {
	font-weight: 600;
	padding: 8px 8px;
	line-height: 1.4;
	word-break: keep-all;
	vertical-align: middle;
}

.cookiescript_fsd_cookies_table td {
	padding: 7px 8px;
	line-height: 1.6;
	vertical-align: top;
	font-weight: 400;
	border: 0;
	border-top: 1px solid #383838;
	color: #FFFFFF;
}

.cookiescript_fsd_cookies_table th:last-child {
    padding-right: 10px;
}

    .cookiescript_fsd_cookies_table td:nth-child(1), .cookiescript_fsd_cookies_table th:nth-child(1) {
    padding-left: 15px;
    word-break: normal;
}

.cookiescript_fsd_cookies_table td:nth-child(2) {
    color: #3fb6ee;
}

.cookiescript_fsd_cookies_table td:nth-child(3), .cookiescript_fsd_cookies_table th:nth-child(3) {
    text-align: center;
}

.cookiescript_fsd_cookies_table.cookiescript_fsd_storage_table td:nth-child(2) {
     color: #FFFFFF;
}

.cookiescript_vendor_name a{
	text-decoration: underline;
	font-weight: bold;
	color: #3fb6ee;
	line-height: normal;
	border: 0;
	padding: 0;
	margin: 0;
	background: transparent;
	font-size: 13px;
}
.cookiescript_vendor_name a:hover {
	text-decoration: underline;
	color: #3fb6ee;
}

.cookiescript_fsd_footer {
    border-top: 1px solid #383838;
    width: 100%;
	background-color: #1C1C1C;
    position: relative;
}
#cookiescript_fsd_wrapper #cookiescript_declarationwrap{
	}
#cookiescript_fsd_wrapper #cookiescript_aboutwrap{
	font-size: 13px;
	padding: 23px 0;
	line-height: 1.6;
	text-align: left;
	font-weight: normal;
	box-sizing: border-box;
	transition: opacity 200ms ease 0ms;
	overflow: auto;
	min-height: 181px;
}

#cookiescript_setting_advertising_wrap {
    font-size: 12px;
}

#cookiescript_iab_type {
    display: flex;
    justify-content: center;
    padding: 20px 0;
    border-bottom: 1px solid #383838;
    flex-wrap: wrap;
}

#cookiescript_iab_type > div {
    cursor: pointer;
    padding: 0 9px;
    font-size: 11px;
    font-weight: 600;
    background: #f5f5f5;
    color: #FFFFFF;
    line-height: 2;
    margin: 3px 5px;
    white-space: nowrap;
}

#cookiescript_iab_type .cookiescript_active {
    background: #3fb6ee;
    color: #FEFEFE;
