/*End Toggle*/
#cookiescript_injected_wrapper {
    background-color: rgba(17, 17, 17, 0.5) !important;
    position: fixed;
    padding: 0;
    width: 100%;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    z-index: 999996;
}
#cookiescript_injected {
    width: 70%;
    margin: 0 auto;
    line-height: 18px;
    letter-spacing: normal;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    padding: 15px 20px 15px 20px;
    display: flex;
    flex-direction: column;
    text-align: center;
    background-color: #1C1C1C;
    z-index: 999997;
    opacity: 1;
    font-size: 14px;
    font-weight: normal;
    font-family: 'Open Sans', Arial, 'Trebuchet MS', 'Segoe UI', 'Helvetica', sans-serif;
    box-shadow: 1px 2px 8px 0 rgba(0, 0, 0, 0.35);
    color: #FFFFFF;
    align-items: center;
	max-height: 80%;
    overflow-y: auto;
    outline: none;
	max-width: 800px;
}
#cookiescript_copyright {
    text-align: center;
    line-height: 1;
}
#cookiescript_buttons {
    flex-wrap: wrap;
    justify-content: center;
    margin: 0 0 15px 0;
}
#cookiescript_manage_wrap {
    margin: 0 0 10px 0;
}
#cookiescript_header {
    padding: 0;
    margin-top: 20px;
    margin-bottom: 10px;
    text-align: center;
    margin-right: 25px;
    margin-left: 25px;
}
#cookiescript_checkboxs {
    margin: 0 auto 15px auto;
    display: flex;
    flex-wrap: wrap;
}
.cookiescript_checkbox {
    margin: 0;
}
.cookiescript_checkbox_text {
    margin: 0 20px 0 5px;
}
#cookiescript_accept, #cookiescript_reject, #cookiescript_save {
    margin: 5px 7px;
    flex-shrink: 1;
    padding: 0 30px;
}
#cookiescript_description {
    margin: 0 auto 15px;
    text-align: center;
    max-width: 600px;
}

.cookiescript_checkbox_label{
    padding: 0;
    margin: 0 10px 0 -2px;
}
#cookiescript_cookietablewrap {
    transition: height 300ms 0ms, min-height 300ms 0ms, max-height 300ms 0ms, opacity 200ms 300ms;
}
#cookiescript_cookietablewrap.cookiescript_hidden {
    transition: height 300ms 0ms, min-height 300ms 0ms, max-height 300ms 0ms, opacity 200ms 0ms;
}

#cookiescript_tabscontent{
    position: relative;
    top: -1px;
}

@media only screen and (max-width: 414px) {
    #cookiescript_injected{
        padding:15px;
        width: 95%;
    }

    #cookiescript_header{
        margin: 0 0 10px 0;
        text-align: left;
            margin-right: 25px;
        }
    #cookiescript_description{
        text-align: left;
    }
    #cookiescript_show_all_partners_button{
        width: 100%;
        text-align: left;
    }
    #cookiescript_description,
    #cookiescript_buttons{
        margin-bottom:10px;
    }

    #cookiescript_checkboxs{
        flex-direction: column;
    }
    #cookiescript_buttons{
        flex-wrap: wrap;
    }
    #cookiescript_checkboxs{
        margin: 0 auto 15px 0;
    }
    .cookiescript_checkbox {
        margin: 0 0 -10px 0;
    }
    #cookiescript_accept, #cookiescript_reject, #cookiescript_save {
		width:100%;
	}
	#cookiescript_injected {
		max-height: 100%;
	}
}
#cookiescript_accept, #cookiescript_reject, #cookiescript_save {
	border-radius: 20px;
}

#cookiescript_injected {
	border-radius: 20px;
}


/*IE 9 fixes*/
@media screen and (min-width:0\0) and (min-resolution: .001dpcm) {
	.cookiescript_checkbox{
		display: inline-block;
	}
}

#cookiescript_iab_type > div {color: #000;}
