styles:
/* SF Pro Display */
@font-face {
  font-family: 'SFProDisplay';
  font-style: normal;
  font-weight: 300;
  font-display: swap;
  src: url(/_nuxt/fonts/SF-UI-Display-Light.95c05a5.ttf);
}

@font-face {
  font-family: 'SFProDisplay';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(/_nuxt/fonts/SF-UI-Display-Regular.64924ba.ttf);
}

@font-face {
  font-family: 'SFProDisplay';
  font-style: normal;
  font-weight: 500;
  font-display: swap;
  src: url(/_nuxt/fonts/SF-UI-Display-Semibold.43b6b0c.ttf);
}

@font-face {
  font-family: 'SFProDisplay';
  font-style: normal;
  font-weight: 800;
  font-display: swap;
  src: url(/_nuxt/fonts/SF-UI-Display-Bold.30f8651.ttf);
}

@font-face {
  font-family: 'SFProDisplay';
  font-style: normal;
  font-weight: 900;
  font-display: swap;
  src: url(/_nuxt/fonts/SF-Pro-Display-Heavy.cfa3bd7.otf);
}

/* YEAR IN REVIEW FONTS */

@font-face {
  font-family: 'SVNAvo';
  font-style: normal;
  font-weight: 700;
  font-display: swap;
  src: url(/_nuxt/fonts/SVN-Avo-bold.857baa6.ttf);
}

@font-face {
  font-family: 'SVNAvo';
  font-style: normal;
  font-weight: 400;
  font-display: swap;
  src: url(/_nuxt/fonts/SVN-Avo.6116a39.ttf);
}

@font-face {
  font-family: 'Akira';
  font-style: normal;
  font-weight: 800;
  font-display: swap;
  src: url(/_nuxt/fonts/YIR24.91d9db5.ttf);
}
/*! @license
 * Shaka Player
 * Copyright 2016 Google LLC
 * SPDX-License-Identifier: Apache-2.0
 */
.shaka-hidden {
  display: none !important;
}
.shaka-video-container {
  position: relative;
  top: 0;
  left: 0;
  display: flex;
}
.shaka-video-container .material-icons-round {
  font-family: 'Material Icons Round';
  font-size: 24px;
}
.shaka-video-container * {
  -webkit-font-smoothing: antialiased;
}
.shaka-video-container:fullscreen {
  width: 100%;
  height: 100%;
  background-color: #000;
}
.shaka-video-container:fullscreen .shaka-text-container {
  font-size: 24px;
}
.shaka-video-container:-webkit-full-screen {
  width: 100%;
  height: 100%;
  background-color: #000;
}
.shaka-video-container:-webkit-full-screen .shaka-text-container {
  font-size: 24px;
}
.shaka-video-container:-moz-full-screen {
  width: 100%;
  height: 100%;
  background-color: #000;
}
.shaka-video-container:-moz-full-screen .shaka-text-container {
  font-size: 24px;
}
.shaka-video-container:-ms-fullscreen {
  width: 100%;
  height: 100%;
  background-color: #000;
}
.shaka-video-container:-ms-fullscreen .shaka-text-container {
  font-size: 4.4vmin;
}
.shaka-controls-container {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  align-items: center;
  z-index: 1;
}
.shaka-video-container:not([shaka-controls='true']) .shaka-controls-container {
  display: none;
}
.shaka-controls-container * {
  flex-shrink: 0;
}
.shaka-controls-container[casting='true'] .shaka-fullscreen-button {
  display: none;
}
.shaka-canvas-container {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
}
.shaka-vr-canvas-container {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
  pointer-events: none;
}
.shaka-bottom-controls {
  width: 96%;
  padding: 0;
  padding-bottom: 2.5%;
  z-index: 1;
}
.shaka-controls-button-panel {
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  align-items: center;
  overflow: hidden;
  min-width: 48px;
  font-size: 12px;
  font-weight: 400;
  font-style: normal;
  user-select: none;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  opacity: 0;
  transition: opacity cubic-bezier(0.4, 0, 0.6, 1) 0.6s;
}
.shaka-controls-container[casting='true'] .shaka-controls-button-panel,
.shaka-controls-container[shown='true'] .shaka-controls-button-panel {
  opacity: 1;
}
.shaka-controls-button-panel > * {
  color: #fff;
  height: 32px;
  line-height: 0.5;
  margin: 1px;
  padding: 0 5px;
  background: 0 0;
  border: 0;
  cursor: pointer;
}
.shaka-controls-button-panel .shaka-overflow-menu-only {
  display: none;
}
.shaka-play-button-container {
  margin: 0;
  width: 100%;
  height: 100%;
  flex-shrink: 1;
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  display: flex;
  justify-content: center;
  align-items: center;
}
.shaka-statistics-container {
  overflow-x: hidden;
  overflow-y: auto;
  min-width: 300px;
  color: #fff;
  background-color: rgba(35 35 35 / 90%);
  font-size: 14px;
  padding: 5px 10px;
  border-radius: 2px;
  position: absolute;
  z-index: 2;
  left: 15px;
  top: 15px;
  opacity: 0;
  transition: opacity cubic-bezier(0.4, 0, 0.6, 1) 0.6s;
}
.shaka-controls-container[casting='true'] .shaka-statistics-container,
.shaka-controls-container[shown='true'] .shaka-statistics-container {
  opacity: 1;
}
.shaka-statistics-container div {
  display: flex;
  justify-content: space-between;
}
.shaka-statistics-container span {
  color: #969696;
}
.shaka-ad-statistics-container {
  overflow-x: hidden;
  overflow-y: auto;
  min-width: 150px;
  color: #fff;
  background-color: rgba(35 35 35 / 90%);
  font-size: 14px;
  padding: 5px 10px;
  border-radius: 2px;
  position: absolute;
  z-index: 2;
  right: 15px;
  top: 15px;
  opacity: 0;
  transition: opacity cubic-bezier(0.4, 0, 0.6, 1) 0.6s;
}
.shaka-controls-container[casting='true'] .shaka-ad-statistics-container,
.shaka-controls-container[shown='true'] .shaka-ad-statistics-container {
  opacity: 1;
}
.shaka-ad-statistics-container div {
  display: flex;
  justify-content: space-between;
}
.shaka-ad-statistics-container span {
  color: #969696;
}
.shaka-context-menu {
  background-color: rgba(35 35 35 / 90%);
  border-radius: 2px;
  position: absolute;
  z-index: 3;
}
.shaka-context-menu button {
  padding: 5px 10px;
  width: 100%;
  display: flex;
  align-items: center;
  color: #fff;
  background: 0 0;
  border: 0;
  cursor: pointer;
}
.shaka-context-menu button:hover {
  background-color: rgba(50 50 50 / 90%);
}
.shaka-context-menu label {
  padding: 0 20px;
  align-items: flex-start;
  color: #fff;
  cursor: pointer;
}
.shaka-context-menu .shaka-current-selection-span {
  align-items: flex-start;
  color: #fff;
  cursor: pointer;
}
.shaka-scrim-container {
  margin: 0;
  width: 100%;
  height: 100%;
  flex-shrink: 1;
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  opacity: 0;
  transition: opacity cubic-bezier(0.4, 0, 0.6, 1) 0.6s;
  background: linear-gradient(to top, #000 0, transparent 15%);
}
.shaka-controls-container[casting='true'] .shaka-scrim-container,
.shaka-controls-container[shown='true'] .shaka-scrim-container {
  opacity: 1;
}
.shaka-text-container {
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  pointer-events: none;
  /* bottom: 0; */
  width: 100%;
  /* min-width: 48px; */
  transition: bottom cubic-bezier(0.4, 0, 0.6, 1) 0.1s;
  transition-delay: 0.5s;
  font-size: 37px;
}

.shaka-text-container span {
  background-color: rgba(0, 0, 0, 0) !important;
  font-size: 37px;
  filter: drop-shadow(0px 2px 2px rgba(0, 0, 0, 1));
  color: #fff;
}

.shaka-text-container span.shaka-text-wrapper {
  background: 0 0;
}
.shaka-controls-container[shown='true'] ~ .shaka-text-container {
  bottom: 15%;
  transition-delay: 0s;
}
.shaka-spinner-container {
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  width: 100%;
  height: 100%;
  flex-shrink: 1;
  display: flex;
  justify-content: center;
  align-items: center;
}
.shaka-video-container:not([shaka-controls='true']) .shaka-spinner-container {
  display: none;
}
.shaka-spinner {
  position: relative;
  top: 0;
  left: 0;
  margin: 0;
  box-sizing: border-box;
  padding: calc(15.6% / 2);
  width: 0;
  height: 0;
  filter: drop-shadow(0 0 2px rgba(255 255 255 / 50%));
}
.shaka-hidden-fast-forward-container,
.shaka-hidden-rewind-container {
  height: 100%;
  width: 40%;
  flex-shrink: 1;
}
.shaka-hidden-fast-forward-container {
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  left: 60%;
}
.shaka-hidden-rewind-container {
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
}
.shaka-play-button {
  box-sizing: border-box;
  padding: calc(15% / 2);
  width: 0;
  height: 0;
  margin: 0;
  border-radius: 50%;
  box-shadow: rgba(0 0 0 / 10%) 0 0 20px 0;
  border: none;
  background-size: 50%;
  background-repeat: no-repeat;
  background-position: center center;
  background-color: rgba(255 255 255 / 90%);
  opacity: 0;
  transition: opacity cubic-bezier(0.4, 0, 0.6, 1) 0.6s;
}
.shaka-controls-container[casting='true'] .shaka-play-button,
.shaka-controls-container[shown='true'] .shaka-play-button {
  opacity: 1;
}
.shaka-play-button[icon='play'] {
  background-image: url(data:image/svg+xml,%3Csvg%20fill%3D%22%23000000%22%20height%3D%2224%22%20viewBox%3D%220%200%2024%2024%22%20width%3D%2224%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%3E%0A%20%20%20%20%3Cpath%20d%3D%22M8%205v14l11-7z%22%2F%3E%0A%20%20%20%20%3Cpath%20d%3D%22M0%200h24v24H0z%22%20fill%3D%22none%22%2F%3E%0A%3C%2Fsvg%3E);
}
.shaka-play-button[icon='pause'] {
  background-image: url(data:image/svg+xml,%3Csvg%20fill%3D%22%23000000%22%20height%3D%2224%22%20viewBox%3D%220%200%2024%2024%22%20width%3D%2224%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%3E%0A%20%20%20%20%3Cpath%20d%3D%22M6%2019h4V5H6v14zm8-14v14h4V5h-4z%22%2F%3E%0A%20%20%20%20%3Cpath%20d%3D%22M0%200h24v24H0z%22%20fill%3D%22none%22%2F%3E%0A%3C%2Fsvg%3E);
}
.shaka-current-time {
  font-size: 14px;
  color: #fff;
  cursor: pointer;
}
.shaka-current-time[disabled] {
  background-color: transparent;
  color: #fff;
  cursor: default;
}
.shaka-controls-container button:focus,
.shaka-controls-container input:focus {
  outline: 1px solid Highlight;
}
.shaka-controls-container button:-moz-focus-inner,
.shaka-controls-container input:-moz-focus-outer {
  outline: 0;
  border: 0;
}
.shaka-controls-container:not(.shaka-keyboard-navigation) button:focus,
.shaka-controls-container:not(.shaka-keyboard-navigation) input:focus {
  outline: 0;
}
.shaka-fast-foward-container,
.shaka-rewind-container {
  height: 100%;
  width: 100%;
  flex-shrink: 1;
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  margin: 0;
  border: none;
  color: #fff;
  background-color: rgba(0, 0, 0, 0.5);
  cursor: default;
  font-size: 20px;
  opacity: 0;
  user-select: none;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
}
.shaka-fast-foward-container {
  border-radius: 40% 0 0 40%;
}
.shaka-rewind-container {
  border-radius: 0 40% 40% 0;
}
.shaka-forward-rewind-container-icon {
  font-family: 'Material Icons Round';
  font-size: 34px;
}
.shaka-range-container {
  position: relative;
  top: 0;
  left: 0;
  margin: calc((12px - 4px) / 2) 6px;
  height: 4px;
  border-radius: 4px;
  background: #fff;
}
.shaka-volume-bar-container {
  width: 100px;
  padding: 0;
}
.shaka-range-element {
  -webkit-appearance: none;
  background: 0 0;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
  height: 12px;
  top: calc((4px - 12px) / 2);
  z-index: 1;
}
.shaka-range-element::-webkit-slider-runnable-track {
  width: 100%;
  cursor: pointer;
  height: 12px;
  background: 0 0;
  color: transparent;
  border: none;
}
.shaka-range-element::-webkit-slider-thumb {
  -webkit-appearance: none;
  border: none;
  border-radius: 12px;
  height: 12px;
  width: 12px;
  background: #fff;
}
.shaka-range-element::-moz-range-track {
  width: 100%;
  cursor: pointer;
  height: 12px;
  background: 0 0;
  color: transparent;
  border: none;
}
.shaka-range-element::-moz-range-thumb {
  -webkit-appearance: none;
  border: none;
  border-radius: 12px;
  height: 12px;
  width: 12px;
  background: #fff;
}
.shaka-seek-bar-container {
  opacity: 0;
  transition: opacity cubic-bezier(0.4, 0, 0.6, 1) 0.6s;
}
.shaka-controls-container[casting='true'] .shaka-seek-bar-container,
.shaka-controls-container[shown='true'] .shaka-seek-bar-container {
  opacity: 1;
}
.shaka-ad-markers {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100%;
} /*!
