.shaka-spinner-svg {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  animation: rotate 2s linear infinite;
  transform-origin: center center;
  width: 100%;
  height: 100%;
  margin: 0;
  padding: 0;
}
.shaka-spinner-path {
  stroke: #202124;
  stroke-dasharray: 20, 200;
  stroke-dashoffset: 0;
  animation: dash 1s ease-in-out infinite;
  stroke-linecap: round;
}
@keyframes rotate {
  100% {
    transform: rotate(360deg);
  }
}
@keyframes dash {
  0% {
    stroke-dasharray: 1, 200;
    stroke-dashoffset: 0;
  }
  50% {
    stroke-dasharray: 89, 200;
    stroke-dashoffset: -35px;
  }
  100% {
    stroke-dasharray: 89, 200;
    stroke-dashoffset: -124px;
  }
}
.shaka-spacer {
  cursor: default;
  flex-shrink: 1;
  flex-grow: 1;
  margin: 0;
}
.shaka-overflow-menu,
.shaka-settings-menu {
  overflow-x: hidden;
  overflow-y: auto;
  white-space: nowrap;
  background: #fff;
  box-shadow: 0 1px 9px 0 rgba(0 0 0 / 40%);
  border-radius: 2px;
  max-height: 250px;
  min-width: 180px;
  opacity: 0;
  transition: opacity cubic-bezier(0.4, 0, 0.6, 1) 0.6s;
  display: flex;
  flex-direction: column;
  position: absolute;
  z-index: 2;
  right: 15px;
  bottom: 30px;
}
.shaka-controls-container[casting='true'] .shaka-overflow-menu,
.shaka-controls-container[casting='true'] .shaka-settings-menu,
.shaka-controls-container[shown='true'] .shaka-overflow-menu,
.shaka-controls-container[shown='true'] .shaka-settings-menu {
  opacity: 1;
}
.shaka-overflow-menu button,
.shaka-settings-menu button {
  font-size: 14px;
  background: 0 0;
  color: #000;
  border: none;
  min-height: 30px;
  padding: 3.5px 6px;
  display: flex;
  align-items: center;
  cursor: pointer;
}
.shaka-overflow-menu button:hover,
.shaka-settings-menu button:hover {
  background: #e0e0e0;
}
.shaka-overflow-menu button label,
.shaka-settings-menu button label {
  cursor: pointer;
}
.shaka-keyboard-navigation .shaka-overflow-menu button:focus,
.shaka-keyboard-navigation .shaka-settings-menu button:focus {
  background: #e0e0e0;
}
.shaka-overflow-menu i,
.shaka-settings-menu i {
  padding-left: 10px;
  padding-right: 10px;
}
.shaka-overflow-menu.shaka-low-position,
.shaka-settings-menu.shaka-low-position {
  bottom: 15px;
}
.shaka-overflow-menu span {
  text-align: left;
}
.shaka-overflow-button-label {
  position: relative;
  display: flex;
  flex-direction: column;
}
.shaka-current-selection-span {
  color: rgba(0 0 0 / 54%);
}
.shaka-settings-menu span {
  margin-left: 54px;
}
.shaka-back-to-overflow-button span {
  margin-left: 0;
}
.shaka-back-to-overflow-button i {
  padding-right: 20px;
}
.shaka-auto-span {
  left: 17px;
}
.shaka-controls-container[ad-active='true'] {
  pointer-events: none;
}
.shaka-controls-container[ad-active='true'] .shaka-bottom-controls {
  pointer-events: auto;
}
.shaka-client-side-ad-container,
.shaka-server-side-ad-container {
  position: absolute;
  left: 0;
  right: 0;
  top: 0;
  bottom: 0;
}
.shaka-video-container[shaka-controls='true']
  .shaka-client-side-ad-container
  iframe,
.shaka-video-container[shaka-controls='true']
  .shaka-server-side-ad-container
  iframe {
  height: 90%;
}
.shaka-server-side-ad-container {
  width: 100%;
  height: 100%;
  flex-shrink: 1;
}
.shaka-server-side-ad-container:not([ad-active='true']) {
  pointer-events: none;
}
.shaka-ad-controls {
  display: flex;
  flex-direction: row;
  z-index: 1;
  padding-bottom: 1%;
}
.shaka-video-container:not([shaka-controls='true']) .shaka-ad-controls {
  display: none;
}
.shaka-ad-controls button,
.shaka-ad-controls div {
  color: #fff;
  font-size: initial;
}
.shaka-ad-controls
  div:not(.shaka-skip-ad-counter):not(.shaka-skip-ad-container) {
  margin: 1px;
}
.shaka-ad-counter,
.shaka-ad-position {
  display: flex;
  justify-content: flex-end;
  flex-direction: column;
  text-shadow: 1px 1px 4px #000;
}
.shaka-ad-controls .shaka-ad-position:not(.shaka-hidden) + .shaka-ad-counter {
  margin-left: 0.75em;
}
.shaka-skip-ad-container {
  position: relative;
  right: calc((100% - 96%) / 2 * -1);
  display: flex;
  flex-direction: row;
  margin: 0;
  margin-left: auto;
}
.shaka-skip-ad-button {
  padding: 5px 15px;
  background: rgba(0 0 0 / 70%);
  border: none;
  cursor: pointer;
}
.shaka-skip-ad-button:disabled {
  background: rgba(0 0 0 / 30%);
}
.shaka-skip-ad-counter {
  padding: 5px;
  background: rgba(0 0 0 / 70%);
  margin: 0;
