bootstrap.onCollectConsentReady.then(function () {
    function loadCustomStyling() {
        var link = document.createElement( "link" );
        link.href = "/css/collectconsent.less";
        link.type = "text/css";
        link.rel = "stylesheet";
        link.media = "screen,print";
        document.head.appendChild(link);
    }
    function getRandomSmFallback() {
        var photoArray = [
            '/img/ccimages/sitemanager_fallback1.jpg',
            '/img/ccimages/sitemanager_fallback2.jpg',
            '/img/ccimages/sitemanager_fallback3.jpg',
            '/img/ccimages/sitemanager_fallback4.jpg',
            '/img/ccimages/sitemanager_fallback5.jpg'
        ];
        var randomNum = Math.floor(Math.random() * photoArray.length);
        var chosenPhoto = photoArray[randomNum];
        return chosenPhoto;
    }
    loadCustomStyling();
    try {
        var isAuthenticated = false,
            launchYear = '2022' ? '2022' : '2015',
            siteManagerPhoto = '' || getRandomSmFallback();
        // Tell collect consent to wait until a user is there or has been created (only when authenticated)!
        CollectConsent.getCore().setWaitForUser(isAuthenticated);
        // Pass on extra page specific variables to Collect Consent
        CollectConsent.getCore().setPageVariables({
            'partnerUrlAppend'        : '',
            'launchYear'              : launchYear,
            'priceTable'              : '<table border="1" cellspacing="0" cellpadding="3"><thead><tr><th>Credits</th><th>Price</th><th>Price per message</th></tr></thead><tr><td>16</td><td>$ 9.9</td><td>$ 0.6</td></tr><tr><td>3</td><td>$ 6</td><td>$ 2</td></tr><tr><td>300</td><td>$ 299</td><td>$ 1</td></tr><tr><td>100</td><td>$ 149</td><td>$ 1.5</td></tr><tr><td>52</td><td>$ 99</td><td>$ 1.9</td></tr><tr><td>25</td><td>$ 49.9</td><td>$ 2</td></tr><tr><td>8</td><td>$ 19.9</td><td>$ 2.5</td></tr><tr><td>4</td><td>$ 9.95</td><td>$ 2.5</td></tr></table>',
            'premiumPriceTable'       : '<table border="1" cellspacing="0" cellpadding="3"><thead><tr><th>Price</th><th>Price per month</th><th>Duration</th></tr></thead><tr><td>$ 9.99</td><td>1</td><td>$ 0</td></tr></table>',
            'transactionCost'         : '$ 0',
            'siteManagerPhoto'        : siteManagerPhoto,
            'authenticatedClientName' : ''
        });
        // Implement specific behaviour to handle refuted consent that is required
        CollectConsent.getCore().requiredConsentRefuted = function() {
            return new Promise(function(resolve, reject) {
                if (false === isAuthenticated) {
                    resolve();
                    return;
                }
                window.application.callAjax(
                    'auth',
                    'logoff'
                ).then(
                    function() {
                        resolve();
                    },
                    function(err) {
                        reject(err);
                    }
                );
            });
        };
        // Listen to consent state changes and store there with the current authenticated user
        CollectConsent.getCore().consentStateChanged = function(consentStates) {
            return new Promise(function(resolve, reject) {
                if (false === isAuthenticated) {
                    resolve();
                    return;
                }
                window.application.callAjax(
                    'auth',
                    'setCollectConsentConsentStates',
                    {consentStates: consentStates}
                ).then(
                    function() {
                        if ('true' === 'true' && window.location.pathname !== '/sponsors') {
                            for (var i = 0, s = consentStates.length; i < s; i++) {
                                if (
                                    consentStates[i].key   === 'multiConsent' &&
                                    consentStates[i].state === 'confirmed' &&
                                    CollectConsent.getCore().hasExplicitConsent('multiConsent', 'stepStyle')
                                ) {
                                    // Init loading class
                                    var holdPage = new holdClass({onHold: true});
                                    // Start loading animation
                                    holdPage.hold();
                                    // Hide search result
                                    $('[data-cnt="search-result"]').hide();
                                    // Give a timeout of 2 seconds to delay the page reload
                                    // This was needed because in rare occasions the reload seemed to happen faster
                                    // than writing data to the CC (or WL) DB. This resulted in showing the popup twice.
                                    setTimeout(function() {
                                        window.location.reload(true);
                                        holdPage.release();
                                    }, 2000)
                                }
                            }
                        }
                        resolve();
                    },
                    function(err) {
                        reject(err);
                    }
                );
            });
        };
        window.addEventListener('load', function () {
            handleCollectConsent();
            if (true === isAuthenticated && false === CollectConsent.getCore().hasUser()) {
                CollectConsent.getCore().createUser().then(function(collectConsentUserId) {
                    window.application.callAjax('auth', 'setCollectConsentUserId', {collectConsentUserId : collectConsentUserId}).then(function() {
                        window.application.callAjax(
                            'auth',
                            'setCollectConsentConsentStates',
                            {consentStates: CollectConsent.getCore().getConsentStates()}
                        );
                    });
                });
            }
        });
    } catch (err) {
        console.error('Failed to execute required CollectConsent implementation code!', err);
    }
    function handleCollectConsent() {
        CollectConsent.getCore().getConsentCollectors().then(
            function(consentCollectors) {
                const consentStates = CollectConsent.getCore().getConsentStates();
                consentCollectors.forEach(collector => {
                    const consentGiven = consentKeyStateGiven(collector.consentKey, consentStates);
                    if (!consentGiven) {
                        showCollectConsentElement(collector.consentKey);
                    }
                });
                collectConsentClickEvents();
                $(`.cc-segment-key-cookieMoreOptions`).hide();
            }
        );
    }
    function consentKeyStateGiven(consentKey, consentStates) {
        const consentKeyStates = consentStates.filter(state => state.key === consentKey);
        return consentKeyStates.length > 0;
    }
    function showCollectConsentElement(elementID) {
        $(`#${elementID}Wrapper`).show();
        if (elementID === 'cookieBanner') {
            $(`.cc-segment-key-cookieMoreOptions`).hide();
        }
    }
    function collectConsentClickEvents() {
        cookiesBannerEvents();
    }
    function cookiesBannerEvents() {
        $('body').on('click', '[data-cc-cookies-confirm]', function(e) {
            CollectConsent.getCore().confirmConsent('cookieBanner');
            $(`#cookieBannerWrapper`).hide();
        });
        $('body').on('click', '[data-cc-cookies-more-options]', function(e) {
            $(`.cc-segment-key-cookieMoreOptions`).show();
            $(`[data-cc-cookies-decline]`).show();
            $(`[data-cc-cookies-more-options]`).hide();
        });
        $('body').on('click', '[data-cc-cookies-decline]', function(e) {
            CollectConsent.getCore().refuteConsent('cookieBanner');
            $(`#cookieBannerWrapper`).hide();
        });
    }
});

