	function doBanner(){
		bannerDivHeight=$(".classic-footer-ad").outerHeight();
		$("html").get(0).style.setProperty("--bannerHideValue", -bannerDivHeight+"px");

		 //$("html").get(0).style.setProperty("--bannerShowValue", "10px");
		//$("html").get(0).style.setProperty("--bannerHideValue", "-150px");
		var scroll=window.scrollY;


		var windowHeight=$(window).height();
		var bodyHeight=$("body").height();		
		var myHeight=parseInt(bodyHeight-windowHeight);		
		//console.log(-bannerDivHeight);
		//$("html").get(0).style.setProperty("--bannerShowValue", bannerDivHeight+"px");

		//console.log("s",scroll);
		//console.log("body",myHeight);
		//console.log("window",windowHeight);

		if(scroll>parseInt(myHeight*.4)){
			if(!$(".classic-footer-ad").hasClass("showBanner")){
				$(".classic-footer-ad").removeClass("hideBanner").removeClass("showBanner").addClass("showBanner");
			}
		}/*else{
			if($(".classic-footer-ad").hasClass("showBanner")){
				$(".classic-footer-ad").addClass("hideBanner");
			}
			$(".classic-footer-ad").removeClass("showBanner");
		}*/	


		if(scroll<lastScrollY){
			lastContinueScrollUp-=(lastScrollY-scroll);
		}else{
			lastContinueScrollUp=0;
		}
		//console.log("continue",lastContinueScrollUp);
		lastScrollY=scroll;

		if(lastContinueScrollUp<=-300 || scroll==0){
			if($(".classic-footer-ad").hasClass("showBanner")){
				$(".classic-footer-ad").addClass("hideBanner");
			}
			$(".classic-footer-ad").removeClass("showBanner");
		}



	}

